def ip_is_ipv4(ip):
    address = ''
    try:
        # Check if ip is valid
        address = IPAddress(ip) 
    except:
        return False
    # Check if ipv4 has dost because inet_aton accepts less ips with less than 3 dots
    if ip.count('.') != 3:
        return False
    if address.version == 4:
        return True
    else:
        return False

def ip_is_ipv6(ip):
    address = ''
    try:
        # Check if ip is valid
        address = IPAddress(ip)
    except:
        return False
    if address.version == 6:
        return True
    else:
        return False

def isip(ip):
    if (ip_is_ipv4(ip) or ip_is_ipv6(ip)):
        return True
    return False

def nofqdn(sysobj):
    hostname = sysobj.hostname.rstrip('\n')
    if (not isip(hostname)):
        rc, domain_cmd = sysobj.cmd('_cmd_which dnsdomainname')
        if rc == 0:
            rc, domain_name = sysobj.cmd('_cmd_' + domain_cmd)
            if domain_name:
                match = re.search('(.*).' + domain_name + '$', hostname)
                hostname = match.group(1) if match else hostname
        else:
            match = re.split('\.',hostname)
            sysobj.log.info('MATCH' + str(match))
            hostname = match[0] if match else hostname
    sysobj.log.info("nofqdn"+ hostname)
    return hostname

def transform_system_name(sysobj):
    sysname = sysobj.hostname
    if ip_is_ipv4(sysname):
        rc, sysname = sysobj.cmd("_cmd_hostname 2>/dev/null")
    if ip_is_ipv4(sysname):
        rc, sysname = sysobj.cmd("_cmd_hostname 2>/dev/null")
    sysname = nofqdn(sysobj)
    return sysname

# if without /prefix, the default is /32
def ip_prefix_2_netmask(ip_prefix):
    from netaddr import IPNetwork
    ip = IPNetwork(ip_prefix)
    ipaddr = str(ip.ip)
    netmask = str(ip.netmask)
    return (ipaddr, netmask)

def ip_netmask_2_prefix(ip, netmask):
    from netaddr import IPNetwork
    interface = IPNetwork(ip + '/' + netmask)
    return str(interface)


