def compvers(ver1,ver2,dim=0):
    '''
    compare versions, i.e. 6.2.1 vs 7.0.1.100
            0 if versions are the same
    returns 1 if version $ver1 is higher
           -1 if version $ver2 is higher
    only checks $dim dim if $dim is defined
    '''
    v1_list = list(map(int, ver1.split('.')))
    v2_list = list(map(int, ver2.split('.')))
    max_bits = max(len(v1_list), len(v2_list))
    dim = dim if dim and dim < max_bits else max_bits

    for i in range(dim-len(v1_list)):
        v1_list.append(0) 
    for i in range(dim-len(v2_list)):
        v2_list.append(0) 
    for i in range(dim):
        if (v1_list[i] > v2_list[i]):
            return 1
        elif (v1_list[i] < v2_list[i]):
            return -1
    return 0

def readfile(filename, type = None):
    if type == 'json':
        with open(filename, 'r') as fr:
            data = json.load(fr)
        return data
    else:
        with open(filename, 'r') as fr:
            data = fr.read()
            ### data with '\n' in each line
        return data


def writefile(filename, content, type = None):
    if type == 'json':
        with open(filename, 'w') as outfile:
            json.dump(content, outfile)
    else:
        with open(filename, 'w') as outfile:
            outfile.write(content)

def appendfile(filename,msg):
    with open(filename, 'a') as outfile:
        outfile.write(msg + '\n')

def parse_msg(msgdict):
    msg = ''
    colsize = 8
    charlength = 25
    for item in msgdict:
        msg1 = ''
        if re.search('list',str(type(item))):
            for i in item:
                if not re.search('str',str(type(i))):
                    msg1 = parse_msg(i)
                else:
                    msg1 += ' '*colsize + i
            msg += "\n" + ' '*colsize + msg1
        elif re.search('dict',str(type(item))):
            for x,y in item.items():
                msg += "\n" + ' '*colsize + x + ': ' + parse_msg(y)
        else:
            msg += item
    return msg

def normalize_version(version):
    # This function will convert 7.4.0.000 to 7.4
    if not re.search('.',version):
        return version
    list = version.split('.')
    newlist = list
    for digit in reversed(list):
        if int(digit) != 0:
            break
        else:
            newlist.pop()
    version = '.'.join(newlist)

    return version

