class Padv_linux(Padv_common, object):
    def __init__(self):
        super(Padv_linux, self).__init__()

    def check_ip_exists(self, sysobj, nic, ip, netmask):
        ip_prefix = ip_netmask_2_prefix(ip, netmask) 
        rc, out = sysobj.cmd('ip addr show ' + nic + " | _cmd_grep " + ip_prefix) 
        if out:
            return True
        else:
            return False

        return False

    def pkg_version(self,sysobj,pkg,minorvers_flag=None):
        if minorvers_flag:
            rc,iv =sysobj.cmd("_cmd_rpm -q --queryformat '%{VERSION}-%{RELEASE}' " + pkg + " 2> /dev/null")
        else:
            rc,iv =sysobj.cmd("_cmd_rpm -q --queryformat '%{VERSION}' " + pkg + " 2> /dev/null")
        if re.search('not installed',iv,re.M):
            iv = ''

        return iv

    def configure_ip(self, sysobj, nic, ip, netmask):
        rtn = {'changed': 1, 'rc': 0, 'msg': 'Config ip on nic ' + nic + ' successfully.'}
        if self.check_ip_exists(sysobj, nic, ip, netmask):
            return rtn['rc'], rtn

        config_file = '/etc/sysconfig/network-scripts/ifcfg-' + nic
        config_file_tmp = '/tmp/if_config_file_' + nic
        b_exist_ip = False
        b_exist_netmask = False
        used_ip_idx = []
        b_boot_on = False
        b_boot_static = False
        b_device = False
        if not os.path.exists(config_file):
            sysobj.cmd('_cmd_touch ' + config_file)
        with open(config_file_tmp, 'w') as write_file:
            with open(config_file, 'r') as read_file:
                line = read_file.readline()
                while line:
                    if re.search('^(IPADDR)(\d*)=(.*)\n*', line) or re.search('^(NETMASK)(\d*)=(.*)\n*', line):
                        ma = re.search('^(IPADDR)(\d*)=(.*)\n*', line) or re.search('^(NETMASK)(\d*)=(.*)\n*', line)
                        if ma.group(1) == 'IPADDR':
                            if ma.group(3) == ip:
                                b_exist_ip = True
                            if ma.group(2):
                                ip_idx = int(ma.group(2))
                            else:
                                ip_idx = 0
                            used_ip_idx.append(ip_idx)
                        elif ma.group(1) == 'NETMASK':
                            if ma.group(3) == netmask:
                                b_exist_netmask = True
                    elif re.search('^BOOTPROTO=\w+', line):
                        b_boot_static = True

                    elif re.search('^ONBOOT=\w+', line):
                        line = 'ONBOOT=yes\n'
                        b_boot_on = True
                    
                    elif re.search('^DEVICE=\w+', line):
                        line = 'DEVICE=' + nic  + '\n'
                        b_device = True
                       
                    write_file.writelines(line)
                    line = read_file.readline()
            if not b_boot_on:
                write_file.writelines('ONBOOT=yes\n')
            if not b_boot_static:
                write_file.writelines('BOOTPROTO=static\n')
            if not b_device:
                write_file.writelines('DEVICE=' + nic  + '\n')
            if b_exist_ip and b_exist_netmask:
                sysobj.log.info("the IP " + ip + " and netmask " + netmask + " exist on " + nic)
            else:
                ip_idx = 0
                while True:
                    if not (ip_idx in used_ip_idx):
                        break
                    else:
                        ip_idx += 1
                if 0 == ip_idx:
                    ip_idx_str = ""
                else:
                    ip_idx_str = str(ip_idx)
                write_file.writelines('IPADDR' + ip_idx_str + '=' + ip + '\n')
                write_file.writelines('NETMASK' + ip_idx_str + '=' + netmask + '\n')

        rc, out = sysobj.cmd('_cmd_diff ' + config_file_tmp + ' ' + config_file)
        if out:
            shutil.move(config_file_tmp, config_file)
            rc, out = sysobj.cmd('_cmd_ifdown ' + nic + ' && _cmd_ifup ' + nic)
            rtn['rc'] = rc
            rtn['msg'] = out
        return rtn['rc'], rtn['msg']



