# in 
#   dictp: a not NONE dict
#   *keys: variable amount keys for recursive dict
#   value: the value for the keys, eg:dictp[a][b] = value
def multi_dict_set_value (dictp, value, *keys):
        key_len = len(keys)
        key_len_cnt = 0
        for key in keys:
            if key_len - 1 <= key_len_cnt:
                break
            dictp = dictp.setdefault(key, {})
            key_len_cnt += 1
        dictp[keys[key_len_cnt]] = value
# to get value from recursive dict 
def multi_dict_get_value(dictp, *keys):
        key_len = len(keys)
        key_len_cnt = 0
        for key in keys:
            dictp = dictp.get(key)
            if (not dictp):
                break
        return dictp

