    def __init__(self, sys, os_version, release_matrix_path, to_check_pkgs):
        self.levels = ('ga', 'patch', 'hotfix')
        self.native_patch_padvs = ('hpux1123', 'hpux1131', 'sol8_sparc', 'sol9_sparc', 'sol10_sparc', 'sol10_x64')
        self.to_check = None
        self.padv = {}
        self.to_check_main = {}
        self.out = {}
        self.os_version = os_version
        self.sys = sys
        self.release_matrix_path = release_matrix_path
        self.to_check_pkgs = to_check_pkgs
        self.logger = Logger.logger(self.sys + '.check_installed_product' + '.log')
        
    
    # since the release matrix couldn't indicate obsolete relationship well
    # we assume the basic obsolete condition matches when:
    # Mxy obsoletes Nxy if M>N
    # xMy obsoletes xNy if M>N
    # xyM obsoletes xyN if M>N
    def obsolete_condition_match(self, num1, num2):
        num1_ints = [int(i) for i in list(num1)]
        num2_ints = [int(i) for i in list(num2)]
        if (3 != len(num1_ints) or 3 != len(num2_ints)):
            return 0
        if ((num1_ints[0] > num2_ints[0]) and (num1_ints[1] == num2_ints[1]) and (num1_ints[2] == num2_ints[2]) or
                (num1_ints[1] > num2_ints[1]) and (num1_ints[0] == num2_ints[0]) and (num1_ints[2] == num2_ints[2]) or
                (num1_ints[2] > num2_ints[2]) and (num1_ints[0] == num2_ints[0]) and (num1_ints[1] == num2_ints[1])):
            return 1
        return 0


    # checking the leading number of fields of the match version
    # using simple integer comparison in case there are leading zeros
    # or in case a #.#.0 is matched against a #.#
    def version_match(self, mvers, pvers, fields):
        mv = re.split('\.', mvers)
        pv = re.split('\.', pvers)

        fields = fields or len(mv)
        if (fields > len(pv)):
            for i in range(0, fields - len(pv)):
                pv.append('0')
        if (fields > len(mv)):
            for i in range(0, fields - len(mv)):
                mv.append('0')
        for n in range(0, fields):
            if ((2 == n) and (re.search('^[1-9]0+$', mv[n]))):
                # solves the #.#.100 matches #.#.133 for MR/SP
                mv[n] = re.sub('0+$', '', mv[n])
                pv[n] = pv[n][0:len(mv[n])]
            if ((3 == n) and (self.obsolete_condition_match(mv[n], pv[n]))):
                continue
            if (mv[n] != pv[n]):
                return ''
        return 1

    def all_pkgs_installed_mpvers(self, ghr, vers, mpvers, fields, mainpkgs):
        inst = 0

        for pkg in mainpkgs:
            mvm = self.version_match(mpvers, "" if not multi_dict_get_value(ghr, 'pkg', pkg, 'version') \
                                                else multi_dict_get_value(ghr, 'pkg', pkg, 'version'), fields)
            vvm = self.version_match(vers, "" if not multi_dict_get_value(ghr, 'pkg', pkg, 'version') \
                                              else multi_dict_get_value(ghr, 'pkg', pkg, 'version') , fields)
            if (not mvm and not vvm):
                return ''
            if vvm:
                inst = 1
        return inst

    def all_pkgs_unchecked(self, ghr, mainpkgs):
        for pkg in mainpkgs:
            if (multi_dict_get_value(ghr, 'mainpkg', pkg)):
                return ''
        return 1
    def base_version_match(self, mvers, pvers, fields):
        if (not pvers):
            return ''
        # Assume $mvers is always the base version
        mv = [int(i) for i in re.split('\.', mvers)]
        self.logger.debug('mvers' + mvers + str(mv))
        pv = [int(i) for i in re.split('\.', pvers)]
        self.logger.debug('pvers' + pvers + str(pv))
        # For 6.0.x we need to identify the base release.
        # 6.0.1/6.0.2/6.0.4: Base
        # 6.0.3/6.0.5/...    MR

        if (6 == mv[0] and 0 == mv[1] and 6 == pv[0] and 0 == pv[1]):
            # Base, $mv[2] can only be 100,200,400
            # When $pv[2]==10/100/200/400, means its already base version, no need for translation
            if (len(pv) >= 3 and 10 != pv[2] and 100 != pv[2] and 200 != pv[2] and 400 != pv[2]):
                if (pv[2] >= 100 and pv[2] > (mv[2] if len(mv) >= 3 else 0)):
                    # We intend to match the base version to be 6.0.1
                    # Because 6.0.2/6.0.4 doesn't always have full package list
                    pv[2] = 100

        fields = fields or len(mv)
        # For 5.x.y.z, when y in $pvers have higher version than $mvers
        # we can regard $pvers' base version is $mvers
        if (5 == mv[0] and 5 == pv[0]):
            if (len(mv) >= 3 and len(pv) >= 3):
                if (mv[2] < pv[2]):
                    pv[2] = mv[2]
            elif (len(mv) < 3 and len(pv) >= 3):
                pv[2] = 0

        for n in range(0, fields):
            if (mv[n] != (pv[n] if n < len(pv) else 0)):
                return False
        return True            

    def all_pkgs_installed_vers(self, ghr, vers, mpvers, fields, mainpkgs):
        if (mpvers):
            return self.all_pkgs_installed_mpvers(ghr, vers, mpvers, fields, mainpkgs)
        for pkg in mainpkgs: 
            if (not self.base_version_match(vers, multi_dict_get_value(ghr, 'pkg', pkg, 'version'), fields)):
                return ''
        return 1
    def find_obsolete_patches(self, phr, patchid):
        obsolete_patches = []
        level = multi_dict_get_value(phr, 'id', patchid, 'level')
        if (multi_dict_get_value(phr, level, patchid, 'obsoletes')):
            for id in re.split(',', multi_dict_get_value(phr, level, patchid, 'obsoletes')):
                obsolete_patches.insert(0, (id, self.find_obsolete_patches(phr, id)))
        return obsolete_patches

    def add_installed_patch(self, ghr, phr, level, id):
        if (multi_dict_get_value(ghr, 'ipatches', id)):
            return
        multi_dict_set_value(ghr, 1, 'ipatches', id)
        # When a new patch being added into the array, make sure
        # 1. If the previous patches in the array obsolete the new patch, do NOT add it into the array.
        # 2. If any of the previous patch in the array is obsoleted by the new patch, remove the previous patch. 
        obsolete_patch_ids = []
        patches = []
        for patch in multi_dict_get_value(self.out, 'installed_patches'):
            obsolete_patch_ids.insert(0, self.find_obsolete_patches(phr, patch['release_id']))
        obsolete_patch_ids = list_uniq(obsolete_patch_ids)
        if (id in obsolete_patch_ids):
            return

        obsolete_patch_ids = self.find_obsolete_patches(phr, id)
        obsolete_patch_ids = list_uniq(obsolete_patch_ids)

        for patch in (multi_dict_get_value(self.out, 'installed_patches')):
            if (not (patch['release_id'] in obsolete_patch_ids)):
                patches.insert(0, patch)
        multi_dict_set_value(self.out, patches, 'installed_patches')
        
        ipatch = {}
        for key in ('release_name', 'release_type', 'release_id'):
            ipatch[key] = multi_dict_get_value(phr, level, id, key)
        multi_dict_get_value(self.out, 'installed_patches').insert(0, ipatch) 
            
    def pkg_product_versions(self, ghr, phr):
        sort_key = lambda key: phr['idprod'][key]['vers_level']
        for id in sorted(phr['idprod'].keys(), key = sort_key, reverse = True):
            rel = phr['idprod'][id]['release_version']
            pvers = phr['idprod'][id]['pkg_version']
            sort_key = lambda key: int(phr['idprod'][id]['prod'][key]['level'])
            for prod in sorted(phr['idprod'][id]['prod'].keys(), key = sort_key, reverse = True):
                if (multi_dict_get_value(ghr, 'iprods', prod)):
                    continue
                mainpkgs = re.split(',', phr['idprod'][id]['prod'][prod]['mainpkgs'])
                self.logger.debug("id: " + id +", prod: " + prod + "\n")
                rtn = self.all_pkgs_unchecked(ghr, mainpkgs)
                self.logger.debug("vc->all_pkgs_unchecked: " + str(rtn));
                if (not rtn):
                    continue
                mpvers = multi_dict_get_value(phr, 'idprod', id, 'prod', prod, 'mpkg_version')
                f = re.split('\.', pvers)
                rtn = self.all_pkgs_installed_vers(ghr, pvers, mpvers, len(f), mainpkgs)
                self.logger.debug ("vc->all_pkgs_installed_vers: " + str(rtn));
                if (not rtn):
                    continue
                multi_dict_set_value(ghr, id, 'iprods', prod)
                self.logger.debug (prod + " in " + id + " is found\n")
                # set up for skipping point products whose mainpkgs have already been found in a higher product
                for pkg in mainpkgs:
                    multi_dict_set_value(ghr, rel + " " + prod, 'mainpkg', pkg)
                if (multi_dict_get_value(phr, 'patch', id)):
                    self.add_installed_patch(ghr, phr, 'patch', id)
    
    def define_packages_and_patches(self, ghr):
        multi_dict_set_value(self.out, [], 'installed_products')
        multi_dict_set_value(self.out, [], 'installed_patches')

        sysh = self.to_check
        multi_dict_set_value(ghr, [], 'patch_ids')
        for pkgh in (sysh['package_info']):
            pkg = pkgh['name']
            split_version = re.split('[,-]', pkgh['version'], 1)
            multi_dict_set_value(ghr, split_version[0], 'pkg', pkg, 'version')
            if (pkgh['patch_ids'] and ('-' != pkgh['patch_ids'])):
                multi_dict_set_value(ghr, pkgh['patch_ids'], 'pkg', pkg, 'patch_ids')
                for patch in (re.split(',', pkgh['patch_ids'])):
                    ghr['patch_ids'].insert(0, patch)
        
    def read_release_matrices(self):
        for level in self.levels:
            file = os.path.join(self.release_matrix_path, self.os_version + '-' + level + '.json')
            matrix = load_file(file)
            # record some needed data in dict for quick lookup 
            for padv in matrix.keys():
                multi_dict_set_value(self.padv, matrix[padv], level)
                phr = self.padv
                for id in (sorted(phr[level].keys())):
                    multi_dict_set_value(phr, id, level, id, 'release_id')
                    multi_dict_set_value(phr, level, 'id', id, 'level')
                    if (1 == len(multi_dict_get_value(phr, level, id, 'release_id'))):
                        phr[level][id]['release_id'] += '.0'
                    rel = multi_dict_get_value(phr, level, id, 'release_version')
                    multi_dict_set_value(phr, rel, 'id', id, 'rel')
                    rel = phr['id'][id]['rel'] = phr[level][id]['release_version']
                    if (multi_dict_get_value(phr, level, id, 'prod')):
                        multi_dict_set_value(phr, phr[level][id], 'idprod', id)
                    

                    for patch in (() if (not multi_dict_get_value(phr, level, id, 'patch')) \
                                     else multi_dict_get_value(phr, level, id, 'patch').keys()):
                        for pkg in (multi_dict_get_value(phr, level, id, 'patch', patch, 'pkgs')):
                            if (False):
                                pass
                            else:
                                for patch in (() if (not multi_dict_get_value(phr, 'patch', id, 'patch'))\
                                              else sorted(multi_dict_get_value(phr, 'patch', id, 'patch').keys())):
                                    pkg = re.split('-', patch, 1)
                                    multi_dict_set_value(phr, multi_dict_get_value(phr, 'patch', id, 'patch', patch, 'version'),\
                                            'id', id, 'pkg', pkg[0], 'version')

    def rel_prel_patch_match(self, rel, prel):
        if (not rel):
            return 0
        if (((re.search('PR', rel)) and (not re.search('PR', prel))) or ((not re.search('PR', rel)) and (re.search('PR', prel)))):
            return 0
        if (((prel == '6.0.1') or (prel == '6.0.2') or (prel == '6.0.4')) and (re.search('^6\.0\.[3-9]', rel))):
            prel = '6.0'
        return 0 if rel == prel or not re.search('^' + prel, rel) else 1

    def set_installed_products(self, ghr, phr):
        if (not ghr.get('iprods')):
            return
        for prod in ghr['iprods'].keys():
            iprod = {}
            iprod['product_abbr'] = prod
            id = ghr['iprods'][prod]
            level = phr['id'][id]['level']
            for key in ('release_version', 'release_name', 'release_type', 'release_id'):
                iprod[key] = phr[level][id][key]
            multi_dict_get_value(self.out, 'installed_products').insert(0, iprod)

    def pkg_patch_versions(self, ghr, phr):
        checked = {}
        for prod in (() if not ghr.get('iprods') else ghr.get('iprods').keys()):
            pid = multi_dict_get_value(ghr, 'iprods', prod)
            prel = multi_dict_get_value(phr, 'id', pid, 'rel')
            if (checked.get(prel)):
                continue
            checked[prel] = 1
            sort_key = lambda key: "" if not multi_dict_get_value(phr, 'patch', key, 'vers_level') \
                                      else multi_dict_get_value(phr, 'patch', key, 'vers_level')
            ids = sorted(phr['patch'].keys(), key = sort_key)
            for id in ids:
                rel = multi_dict_get_value(phr, 'patch', id, 'release_version')
                vers = multi_dict_get_value(phr, 'patch', id, 'pkg_version')
                if (not self.rel_prel_patch_match(rel, prel)):
                    continue
        
                mainpkgs = re.split(',', multi_dict_get_value(phr, 'idprod', pid, 'prod', prod, 'mainpkgs'))
                patches = 0
                ipatches = 0
                for pkg in mainpkgs:
                    if (not multi_dict_get_value(phr, 'id', id, 'pkg', pkg)):
                        continue
                    patches += 1
                    pvers = multi_dict_get_value(phr, 'id', id, 'pkg', pkg, 'version')
                    pvers = pvers or multi_dict_get_value(phr, 'patch', id, 'pkg_version')
                    v = re.split('\.', pvers)
                    # AIX sometimes has 4d revs to a patch level
                    fields = 4 if v[3] > '0' else 3
                    if (self.version_match(pvers, multi_dict_get_value(ghr, 'pkg', pkg, 'version'), fields)):
                        ipatches += 1
                    inst = '*' if (patches and patches == ipatches) else None
                    if (not inst):
                        continue
                    multi_dict_set_value(ghr, id, 'iprods', prod)
                    self.add_installed_patch(ghr, phr, 'patch', id)
        self.set_installed_products(ghr, phr)

    def find_obsolete_patches(self, phr, patchid):
        obsolete_patches = []
        level = multi_dict_get_value(phr, 'id', patchid, 'level')
        if (multi_dict_get_value(phr, level, patchid, 'obsoletes')):
            for id in re.split(',', multi_dict_get_value(phr, level, patchid, 'obsoletes')):
                obsolete_patches.insert(0, (id, self.find_obsolete_patches(phr, id)))
        return obsolete_patches

    # based on installed hotfix ids
    # remove all the obsoleted ids
    def remove_obsoleted_ids(self, ghr, phr, installed_hotfix_ids):
        obsoleted_hotfix_ids = []
        for id in installed_hotfix_ids:
            obsoleted_hotfix_ids.insert(0, self.find_obsolete_patches(phr, id))
        return [i for i in installed_hotfix_ids if i not in obsoleted_hotfix_ids]

    def pkg_hotfix_versions(self, ghr, phr):
        installed_hotfix_ids = []

        for prod in () if not ghr.get('iprods') else ghr['iprods']:
            pid = multi_dict_get_value(ghr, 'iprods', prod)
            prel = multi_dict_get_value(phr, 'id', pid, 'rel')
            sort_key = lambda key: "" if not multi_dict_get_value(phr, 'hotfix', key, 'vers_level') \
                                else phr['hotfix'][key]['vers_level']
            ids = () if not phr.get('hotfix') else sorted(phr['hotfix'], key = sort_key)
            for id in ids:
                rel = multi_dict_get_value(phr, 'hotfix', id, 'release_version')
                if (not rel or not re.search('^' + prel + '(PP|P\d|HF\d|\.\d\d\d|\.\d\.\d\d\d)', rel)):
                    continue
                if (not re.search(prod, "" if not multi_dict_get_value(phr, 'hotfix', id, 'products') \
                        else multi_dict_get_value(phr, 'hotfix', id, 'products'))):
                    continue
                patches = [] if not multi_dict_get_value(phr, 'hotfix', id, 'patch') \
                             else sorted(phr['hotfix'][id]['patch'].keys())
                if (not patches):
                    continue
                pkgs = []
                inst = ''
                # assumes $pvers is the same for all hotfix packages
                for patch in patches:
                    p = re.split('-', patch)
                    #writefile('/tmp/patchinfo',patch)
                    #pkg = str(int(p[0]) - int(p[1])) if 4 == len(p) else p[0]
                    pkg = p[0] + '-' + p[1] if 4 == len(p) else p[0]
                    pvers = p[2] if 4 == len(p) else p[1]
                    pkgs.insert(0, pkg)
                    if not multi_dict_get_value(ghr, 'pkg', pkg, 'version'):
                        continue
                    if (self.version_match(ghr['pkg'][pkg]['version'], pvers, 4)):
                        inst = '*'
                    else:
                        inst = ''
                        break
                if (not inst):
                    continue
                installed_hotfix_ids.insert(0, id)
            installed_hotfix_ids = self.remove_obsoleted_ids(ghr, phr, installed_hotfix_ids)
            for id in installed_hotfix_ids:
                self.add_installed_patch(ghr, phr, 'hotfix', id)

    def get_pkgs_info(self):

        package_infos = {}
        package_infos['package_info'] = []
        for pkg in re.split(' ', self.to_check_pkgs):
            package_info = {}
            #print(pkg)
            package_info['name'], package_info['version'], platform = re.split('-', pkg)
            package_info['patch_ids'] = '-'
            package_infos['package_info'].append(package_info)
        return package_infos

    def execute(self):    
        self.to_check = self.get_pkgs_info()

        self.read_release_matrices()

        ghr = self.to_check_main
        phr = self.padv
        self.define_packages_and_patches(ghr)
        if (not ghr.get('pkg')):
            pass
        elif (False):
            pass
        else:
            self.pkg_product_versions(ghr, phr)
            self.pkg_patch_versions(ghr, phr)
            self.pkg_hotfix_versions(ghr, phr)


