class ConfiguredComponent:

    def __init__(self):
        pass

    def get_configured_component(self,sysobj):
        # There is no need to check configured component for Uninstall
        if sysobj.args['parameter']['module'] == 'yum' and sysobj.args['parameter']['state'] == 'absent':
            sysobj.args['parameter']['previouscomponent'] = defaultcomponent
            sysobj.args['parameter']['activecomponent'] = defaultcomponent

        stack = self.get_component_stack(sysobj)
        task = 'check_config'
        no_double_check = 1
        comp_list = []
        for component in stack:
            component_obj = instance_factory(sysobj, component.lower(), None, 'prod')
            sysobj.log.debug('Performing ' + component + ' ' + task + ':')
            if getattr(component_obj, task)(sysobj):
                comp_list.append(component)
                sysobj.args['parameter']['host_facters']['configuredcomponents'] = comp_list
                sysobj.log.info('check_config passed for ' + component)
                return component
                # $prod->set_value('previouscomponent', 'push', $component) unless (defined($prod->{previouscomponent}     ));
        return 'None'


    def get_component_stack (self,sysobj):
        stack = ()
        installed_prod = sysobj.args['parameter']['host_facters']['product']
        if re.search('FOUNDATION',installed_prod,flags=re.IGNORECASE):
            stack = ('SF', 'DMP')
        elif re.search('AVAILABILITY',installed_prod,flags=re.IGNORECASE):
            stack = ('VCS')
        elif re.search('STORAGE',installed_prod,flags=re.IGNORECASE):
            stack = ('SFCFS' , 'SF' , 'DMP')
        else:
            #stack = ('SFRAC', 'SFSYBASECE' , 'SFCFSHA' , 'SFHA', 'VCS' , 'SF' , 'DMP')
            stack = ( 'SFCFSHA' , 'SFHA', 'VCS' , 'SF' , 'DMP' )
        #TODO check illegal component in stack on the basis of Padv
        return stack

