VER = '7.2'
class Precheck:
    def __init__(self,options):
        #options, args = self.parse_opt()
        self.options = options
        self.prod = (options['prod']).lower()
        self.component = options['component']
        self.task = options['task']
        self.method = ''
        if self.task.lower() == 'configure':
            self.method = options['method']
        # on which padv should be checked 
        self.padv = options['padv']
        self.host_facters = options['host_facters']
        self.matric_path = options['matric']
        #self.args = args
        self.facts = self.load_file(options['facts'])
        #self.facts = options['facts']
        self.matric = {}
        self.matric = self.read_matric(options['matric'])
        # save the specific release information to self.release to easily get the needed value
        version = str(options['release'] or VER)
        self.version = normalize_version(version)
        self.release = self.get_release(self.version)
        #with open('/tmp/mcpi/self_release','w') as w_file:
        #     w_file.write(str(self.release) + ' : ' + self.version)
        self.output = '/opt/VRTS/install/ansible/logs/ansible_play_' + options['timestr'] + '/precheck.json'

    def parse_opt(self):
        # parse the options, -v, -p, -c, -t, -m -i are required options
        msg_usage = '\n%prog -t <task> -v <padv> -p <product> -c <component> -m <location of release matric> -f <location of facter file>'
        optparser = OptionParser(msg_usage)
        optparser.add_option('-t', '--task',
            dest='task', type='choice', choices=['install', 'configure'],
            help='Check conditions for defined task: install, configure')
        optparser.add_option('-v', '--padv',
            dest='padv', type='choice', choices=['rhel6_x86_64', 'rhel7_x86_64'],
            help='Check conditions for defined platform: rhel6_x86_64, rhel7_x86_64')
        optparser.add_option('-p', '--product',
            dest='product', type='choice', choices=['availability', 'foundation', 'storage', 'enterprise'],
            help='Check conditions for defined product: availability, foundation, storage, enterprise')
        optparser.add_option('-c', '--component',
            dest='component',
            help='Check conditions for defined component: vcs, sf, sfha, sfcfs, sfcfsha')
        optparser.add_option('-m', '--release_matric',
            dest='matric',
            help='The location of release matric')
        optparser.add_option('-f', '--facter',
            dest='facter', 
            help='The location of the facter json file')
        optparser.add_option('-o', '--output',
            dest='output', default='/var/tmp/precheck.json',
            help=SUPPRESS_HELP)
        optparser.add_option('-r', '--release', type='float',
            dest='release', help=SUPPRESS_HELP)
        (options, args) = optparser.parse_args()
        msg = self.verify_args(options, args)
        if msg:
            print(msg)
            #optparser.print_help()
            exit(1)

        return options, args

    def verify_args(self, options, args):
        msg = ''
        invalid = []
        arguments = ['padv', 'matric', 'facter']
        task_opt = {'install': ['product', 'component'], 'configure': ['component']}
        # option -t must be defined
        if not options.task:
            msg = 'error: task is not defined. The expected task: '+', '.join(task_opt.keys())+'\n'
            return msg
        # the required options depend on the task
        arguments[1:1] = task_opt[options.task]
        for item in arguments:
            this_opt = getattr(options, item)
            if not this_opt: 
                invalid.append(item)
                msg = '\nerror: Required option(s): ' + ', '.join(invalid) + '\n'
        if msg: return msg
        # check if component valid to the product
        prod_component = {'availability': ['vcs'], 
                          'foundation': ['sf'], 
                          'storage': ['sf', 'sfcfs'],
                          'enterprise': ['vcs', 'sf', 'sfha', 'sfcfs', 'sfcfsha']}
        if options.component not in prod_component[options.product]:
            msg += 'error: invalid choice: \''+options.component+'\' (choose from '+', '.join(prod_component[options.product])+')'
        if not options.facter or not os.path.isfile(options.facter):
            msg += 'error: Invalid facter file.\n' 
        if not options.matric or not os.path.exists(options.matric):
            msg += 'error: The matric directory not exists.\n'
        return msg

    def load_file(self, js_file):
        if not os.path.exists(js_file):
            print('error: '+js_file+' not exists.\n')
            exit(1)
        with open(js_file, 'r') as fh: 
            data = json.load(fh)
        return data

    def read_matric(self, matric_path):
        # merge ga/patch/hotfix and local release file into one dict
        levels = ('ga', 'patch', 'hotfix')
        for level in levels:
            file = matric_path+'/'+self.padv+'-'+level+'.json'
            matric = self.load_file(file)
            self.matric = self.merge_matric(self.matric, matric)
        release_file_path = os.path.dirname(matric_path)+'/release_data.json'
        release_file = self.load_file(release_file_path)
        self.matric = self.append_matric(self.matric, release_file)

        return self.matric

    def read_kernel_matric(self,matric_path):
        file = matric_path+'/kernel_padv.json'
        return readfile(file,'json')

    def merge_matric(self, dict1, dict2):
        # merge the json matric
        for padv, releases in dict2.items():
            if padv == self.padv and padv in dict1:
                for key, value in releases.items():
                    if key in dict1[padv].keys():
                        dict1[padv][key].update(value)
                    else:
                        dict1[padv].update(releases)
            elif padv == self.padv:
                dict1[padv] = releases

        return dict1

    def append_matric(self,dict1,dict2):
        # merge the local file release_file.json to online matric
        for padv, releases in dict2.items():
            if padv == self.padv and padv in dict1:
                for key, value in releases.items():
                    if key in dict1[padv].keys():
                        for k, v in value.items():
                            dict1[padv][key][k] = v
            elif padv == self.padv:
                dict1[padv] = releases

        return dict1
    def get_release(self, version):
        # get the information of the specific release version from release matric
        for padv, releases in self.matric.items():
            if padv == self.padv:
                for value in releases.values():
                    if value['release_version'] == version:
                        return value

    def precheck_compare(self,sysobj):
        json_path = sysobj.tmpdir + '/precheck.json'
        json_data = load_file(json_path)

        # Read precheck json and put all errors with rc!=0 in sysobj.error list
        for i in json_data:
            for key in i.keys():
                sysobj.log.info(str(i[key]))
                if i[key]['sys'][sysobj.hostname]['rc'] == 1 or i[key]['sys'][sysobj.hostname]['rc'] == 4:
                    if key == 'check_must_have_patch' and 'ignore_required_patch' in sysobj.args['parameter'].keys() and sysobj.args['parameter']['ignore_required_patch'] == 1:
                        sysobj.log.info('USER MENTIONED TO IGNORE REQUIRED PATCH CHECK')
                        continue
                    if key == 'check_prod_prerequisite':
                        (m,oslibs) = re.split(':', i[key]['sys'][sysobj.hostname]['msg'])
                        rc,rtn_oslibs = self.try_installing_os_libs(sysobj,oslibs)
                        if rc != 0:
                            sysobj.error.append({key:'The following required OS lib(s) were not found on the system:'+ ','.join(rtn_oslibs)})
                        else:
                            sysobj.message.append({key: 'The following required OS lib(s) installed found on the system:'+ ','.join(rtn_oslibs)})
                        continue
 
                    sysobj.error.append({key:i[key]['sys'][sysobj.hostname]['msg']})
                elif i[key]['sys'][sysobj.hostname]['rc'] == 2 or i[key]['sys'][sysobj.hostname]['rc'] == 3:
                    sysobj.warning.append({key:i[key]['sys'][sysobj.hostname]['msg']})
                elif i[key]['sys'][sysobj.hostname]['rc'] == 0:
                    sysobj.message.append({key:i[key]['sys'][sysobj.hostname]['msg']})
        sysobj.log.info(str(sysobj.error))

    def try_installing_os_libs(self,sysobj,oslibs):
        oslibs = re.sub(',',' ',oslibs)
        sysobj.cmd('_cmd_yum install -y ' + oslibs)
        rc = 0
        left_oslibs = []
        for lib in re.split(' ',oslibs):
            if lib:
                rcode,output = sysobj.cmd('_cmd_rpm -q ' + lib)
                if rcode != 0:
                    rc += 1
                    left_oslibs.append(lib)

        return rc,left_oslibs

    def precheck(self):
        # put all check tasks into funcs, and run those check functions
        # dump hash with each function returns to output file(default: /var/tmp/precheck.json)
        # also print to screen for now
        # for return value of each check function:
        # error message: rc = 1
        # warning message: rc = 2
        # no facter info, skip check: rc = 3
        # fatal check, if failed, no checks would be continue: rc = 4
        self.summary = {}
        funcs = ['check_release_compatibility', 'check_installed_product', 'check_must_have_patch']
        task = {'install': ['check_prod_prerequisite', 'check_disk_space', 'check_prod_basic'], 
                'upgrade': ['check_prod_prerequisite', 'check_disk_space', 'check_network'],
                'uninstall': ['check_prod_prerequisite', 'check_prod_basic'],
                'configure': ['check_prod_basic', 'check_network']}
        funcs.extend(task[self.task])
        # for each system, run each precheck functions
        for system in self.facts.keys():
            for func in funcs:
                result = {}
                result = getattr(self, func)(system)
                self.summary.setdefault(system, {}).setdefault(func, {}).update(result)
                #if self.summary[system][func]['rc'] == 4:
                #    break

        # go through variable summary, put check function as key, each system information as value, and put the function info as list element
        result = []
        for func in funcs:
            sys_func = {}
            for system, function in self.summary.items():
                if self.summary[system].get(func):
                    sys_func.setdefault(func, {}).setdefault('sys', {})
                    sys_func[func]['sys'][system] = self.summary[system][func]
            if sys_func: result.append(sys_func)

            # add key 'summary' to catalog the msgs for each check

        with open(self.output, 'w') as fh:
            json.dump(result, fh, sort_keys=True, indent=4)
        #print(json.dumps(result, sort_keys=True, indent=4))
        #return {'msg':'Done', 'rc':0 ,'changed':0}
        return result

    def get_value(self, dict_name, key):
        # Recursively find the value according to the key
        if key in dict_name: return dict_name[key]
        for v in dict_name.values():
            if isinstance(v, dict):
                item = self.get_value(v, key)
                if item is not None:
                    return item

    def check_release_compatibility(self, sys):
        # if the needed facter is not get, then skip this check
        result = {'rc': 0, 'msg': 'Passed'}
        operatingsystem = self.get_value(self.facts[sys], 'operatingsystem')
        if not operatingsystem:
            result['rc'] = 3
            result['msg'] = 'Cannot detect the operating system information, stop the checkings.'
            return result

        if operatingsystem['sort_kernel'] == '':
            result['rc'] = 1
            result['msg'] = 'Kernel ' + operatingsystem['kernel_version'] + ' is not supported for Infoscale.'
            return result

        ver_min = int(self.release['os_update_min'])
        ver_max = int(self.release['os_update_max'])
        # check if padv is expected, like rhel6_x86_64, if yes, check if OS update is expected
        sys_padv = operatingsystem['padv']
        if sys_padv != self.padv:
            result['rc'] = 4
            msg = 'The detected platform on the system is not expected as ' + self.padv
            result['msg'] = msg
        else:
            # check the update
            update = operatingsystem['distribution_minor_version']
            update = int(update)
            if not (ver_min <= update <= ver_max):
                result['rc'] = 4
                minor = operatingsystem['minor']
                distro = operatingsystem['distribution']
                major = operatingsystem['distribution_major_version']
                support_ver = major + ' ' + minor + ' '+','.join([str(x) for x in range(ver_min, ver_max + 1)])
                msg = 'This release is intended to operate on ' + distro + ' ' + support_ver 
                result['msg'] = msg

        return result

    def check_prod_prerequisite(self, sys):
        result = {'rc': 0, 'msg': 'Passed'}
        software = self.get_value(self.facts[sys], 'software')
        if not software:
            # if cannot get the required facter, skip this check
            msg = 'Cannot detect the package information, skip to check the required os libs.'
            result = {'rc': 3, 'msg': msg}
            return result
        pkgs = self.release['prod'][self.prod.lower()]['rpkgs']
        required_os_libs = []
        #with open('/tmp/mcpi/self_release','w') as w_file:
        #     w_file.write(str(pkgs))
        miss_pkg = []
        for pkg in pkgs:
            if self.release['package'][pkg].get('os_lib'):
                required_os_libs.extend(self.release['package'][pkg]['os_lib'])
        required_os_libs = set(required_os_libs)
        sys_pkg = software['os_packages'].split(' ')
        for pkg in required_os_libs:
            if pkg not in sys_pkg:
                miss_pkg.append(pkg)
        if miss_pkg:
            msg = 'The following required OS lib(s) were not found on the system: ' + ','.join(miss_pkg)
            result = {'rc': 1, 'msg': msg}

        return result

    def check_must_have_patch(self, sys):
        result = {'rc': 0, 'msg': 'Passed'}
        operatingsystem = self.get_value(self.facts[sys], 'operatingsystem')
        if not operatingsystem:
            result['rc'] = 3
            result['msg'] = 'Cannot detect the operating system information, stop to check the required InfoScale patch for the specific OS.\n'
            return result
        prod_str = self.summary[sys]['check_installed_product'].get('msg')
        if not prod_str and self.task != 'install':
            result['rc'] = 3
            result['msg'] = 'Cannot detect installed product, skip to check the required InfoScale patch.\n'
            return result
        if self.release.get('required_patch') and (operatingsystem.get('distribution_minor_version') in self.release['required_patch'].keys()):
            release_id = self.release['required_patch'][operatingsystem.get('distribution_minor_version')]
            release_name = self.get_value(self.matric, release_id)['release_name']
            if not re.search(release_name, prod_str):
                result['rc'] = 1
                result['msg'] = 'Required InfoScale patch: '+release_name+' (Patch ID: '+release_id+')'+'\n'
        return result

    def check_prod_basic(self, sys):
        # get system hardware information from sys facter
        hardware = self.get_value(self.facts[sys], 'hardware')
        # if cannot get the required facter, skip this check
        if not hardware:
            msg = 'Cannot detect the system information on, skip to check the hardware requirement.'
            result = {'rc': 3, 'msg': msg}
            return result

        # get required hardware info from matric
        required_hardware = self.release['component'].get(self.component)
        msg = ''
        if required_hardware:
            # check cpu number
            if required_hardware.get('cpu_num') and (hardware['processor_count'] < required_hardware['cpu_num']):
                msg = 'You have '+str(hardware['processor_count'])+' CPU(s) on the system. It is required that you run '+self.component+' on servers with minimal '+str(required_hardware['cpu_num'])+' CPU(s).\n'           
            # check cpu speed
            if required_hardware.get('cpu_speed') and (hardware['cpu_MHz'] < required_hardware['cpu_speed']):
                msg += 'The CPU speed on the system is '+str(hardware['cpu_MHz'])+' MHz, the minimal CPU speed required by '+self.component+' is '+str(required_hardware['cpu_speed'])+' MHz.\n'
            # check memory size
            if required_hardware.get('memory') and (hardware['memfree_mb'] < required_hardware['memory']):
                msg += 'The availible RAM size on the system is '+str(hardware['memfree_mb'])+' megabytes, the recommended free RAM size required by '+self.component+' is '+str(required_hardware['memory'])+' megabytes.\n'
            # check swap size
            if required_hardware.get('swap') and (hardware['swapfree_mb'] < required_hardware['swap']):
                msg += 'The size of free swap space on the system is '+str(hardware['swapfree_mb'])+' megabytes, the minimal free swap size required by '+self.component+' is '+str(required_hardware['swap'])+' megabytes.'
        if msg:
            result = {'rc':2, 'msg':msg}
        else:
            result = {'rc':0, 'msg':'Passed'}

        return result 

    def check_disk_space(self, sysname):
        result = {'rc':0, 'msg':'Passed'}
        # check fs space must need facter module environment.
        if not self.get_value(self.facts[sysname], 'environment'):
            result['rc'] = 3
            result['msg'] = 'Cannot detect the environment information, stop the checkings.'
            return result
        # support for sol LU just retain at here
        rootpath = ''
        vols_hash = {rootpath + 'opt': rootpath + '/opt', rootpath + 'usr': rootpath + '/usr',
                     rootpath + 'var': rootpath + '/var', rootpath + 'root': rootpath + '/'}
        vols = ('root', 'opt', 'var', 'usr')
        space = self.all_volumespace(vols, sysname)

        req_vols = {'req_opt': 0, 'req_usr': 0, 'req_var': 0, 'req_root': 0}
        for pkg in self.release['prod'][self.prod.lower()]['rpkgs']:
            for vol in vols:
                req_vols['req_' + vol] = req_vols['req_' + vol] + self.release['package'][pkg]['size'][vol]
        err_vols = []
        space_index = 2 if re.search('sol',self.padv) else 1.3
        #with open('/tmp/mcpi/space_release','w') as w_file:
        #     w_file.write(str(space))
        #     w_file.write(str(req_vols))
        for filesys in space:
            req_space = 0
            for vol in space[filesys]['vols']:
                req_space = req_space + req_vols['req_' + vol]
            r_space = req_space//1024*space_index
            if space[filesys]['space'] < r_space:
                for vol in space[filesys]['vols']:
                    err_vols.append('"' + vols_hash[vol]+ '" => ' + str(space[filesys]['space']) + 'MB:' + str(r_space)+ 'MB')
        if err_vols:
            result['msg'] = 'Space is not enough: ' + ' '.join(err_vols)
            result['rc'] = 1
        else:
            result['msg'] = 'Passed'
            result['rc'] = 0
        return result

    # calculate vols partition space save as hash
    # space = {'/dev/sda2': {'space': 10000, 'vols': ['root', 'opt', 'usr', 'var']}}
    def all_volumespace(self, vols, sysname):
        space = {}
        filesystem = 'root'
        vol_space = 0
        for vol in vols:
            mounts_sys = self.facts[sysname]['environment']
            if 'mounts_space' in mounts_sys and vol in mounts_sys['mounts_space']:
                if 'available' in mounts_sys['mounts_space'][vol]:
                    vol_space = mounts_sys['mounts_space'][vol]['available']
                if 'filesystem' in mounts_sys['mounts_space'][vol]:
                    filesystem = mounts_sys['mounts_space'][vol]['filesystem']
            space.setdefault(filesystem, {'space': vol_space})
            if 'vols' not in space[filesystem]:
                space[filesystem]['vols'] = [vol]
            else:
                space[filesystem]['vols'].append(vol)
        return space

    def check_network(self, sys):
        result = {'rc': 0, 'msg':'Passed'}
        if 'enable_lgf' in self.options.keys() and self.options['enable_lgf'] == 0:
            result['msg'] = 'One node cluster config. Skipping check_network.'
            return result
        network = self.get_value(self.facts[sys], 'network')
        if not network:
            msg = 'Cannot detect the network information, skip to check the network.'
            result = {'rc': 3, 'msg': msg}
            return result
        if network.get('network_interfaces'):
            nics = network['network_interfaces'].keys()
            if len(nics) == 1 and self.method.lower() != 'udp':
                result['msg'] = 'Only 1 NIC detected on the system, it is recommended to configure InfoScale with 2 NICs.'
                result['rc'] = 2
        else: 
            result['msg'] = 'No NICs discovered on the system.'
            result['rc'] = 1
        return result

    def check_installed_product(self, sys):
        rtn = {'rc': 0, 'msg' : 'Passed'}
        vrts_pkgs = self.get_value(self.facts[sys], 'vrts_packages')
        if (not vrts_pkgs):
            rtn['msg'] = 'No product installed.'  
            return rtn
        vc = Version_check(sys, self.padv, self.matric_path, vrts_pkgs) 
        vc.execute()
        if (len(vc.out['installed_products'])):
            rtn['msg'] += 'Installed_products:\n' 
            prod = ''
            for installed_product in vc.out['installed_products']:
                prod = installed_product['release_name'] + ' ' + installed_product['product_abbr']
                rtn['msg'] += ' ' * 4 + prod + '\n'
            if self.task == 'install':
                rtn['msg'] = 'Infoscale is already installed.'
                if installed_product['release_version'] != self.version or installed_product['product_abbr'].lower() != self.prod.lower():
                    rtn['rc'] = 1
                    rtn['msg'] += 'Please use upgrade options.'
                else:
                    rtn['rc'] = 0
                rtn['msg'] += ' Installed product: ' +  prod
        if (len(vc.out['installed_patches'])):
            rtn['msg'] += 'Installed_patches:\n'
            for installed_patch in vc.out['installed_patches']:
                rtn['msg'] += ' ' * 4 + installed_patch['release_name'] + '\n'
        return rtn
        

