class LinuxEnvironment:
    def execute(self, sysobj):
        facts = {}
        #get date time
        rc, output = sysobj.cmd('_cmd_date +%m%d%y_%H%M%S 2>/dev/null')
        if not rc and output:
            facts['date_time'] = output

        #get user environments
        rc, output = sysobj.cmd('_cmd_env 2>/dev/null')
        facts['user_env'] = {}
        if not rc and output:
            for line in output.split('\n'):
                tmp_match = re.search('(\w+)=(.*)$', line)
                if tmp_match:
                    key = tmp_match.group(1)
                    value = tmp_match.group(2)
                if key == '_' or key == 'LS_COLORS' or key == 'module':
                    continue
                else:
                    facts['user_env'][key] = value
            facts['user_id'] = facts['user_env']['USER'] if facts['user_env'].get('USER') else 'root'
            facts['user_homedir'] = facts['user_env']['HOME'] if facts['user_env'].get('HOME') else '/root'
            facts['user_shell'] = facts['user_env']['SHELL'] if facts['user_env'].get('SHELL') else '/bin/bash'

        #get uid/gid
        rc,output = sysobj.cmd('_cmd_id 2>/dev/null')
        if not rc and output:
            tmp_match = re.search('uid=(\d+)\(\w+\)\s+gid=(\d+)\(\w+\)', output)
            if tmp_match:
                facts['user_uid'] = tmp_match.group(1)
                facts['user_gid'] = tmp_match.group(2)

        #get mount
        rc,output = sysobj.cmd('_cmd_dfkT 2>/dev/null')
        facts['mounts_space'] = {}
        if not rc and output:
            for line in output.split('\n'):
                tmp_match = re.search('^(\S+)\s+(\S+)\s+(\S+)\s+(\S+)\s+(\S+)\s+\S+\s+(\S+)', line)
                if tmp_match:
                    filesystem = tmp_match.group(1)
                    fstype = tmp_match.group(2)
                    total = tmp_match.group(3)
                    used = tmp_match.group(4)
                    available = tmp_match.group(5)
                    mountpoint = tmp_match.group(6)
                    if not re.search('^\/', filesystem):
                        continue
                    else:
                        total = int(total)//1024
                        available = int(available)//1024
                        if mountpoint == '/':
                            mountpoint = 'root'
                        facts['mounts_space'][mountpoint] = {}
                        facts['mounts_space'][mountpoint]['filesystem'] = filesystem
                        facts['mounts_space'][mountpoint]['fstype'] = fstype
                        facts['mounts_space'][mountpoint]['total_mb'] = total
                        facts['mounts_space'][mountpoint]['available'] = available
        return facts


