class LinuxNetwork:
    def execute(self, sysobj):
        facts = {}
    
        ip_bin_path='_cmd_ip'
        if_class_path='/sys/class/net'
        
        # get default ipv4 interface
        # Use the commands:
        #     ip -4 route get 8.8.8.8                     -> Google public DNS
        #     ip -6 route get 2404:6800:400a:800::1012    -> ipv6.google.com
        # to find out the default outgoing interface, address, and gateway
        rc, output = sysobj.cmd(ip_bin_path+' -4 route get 8.8.8.8 2>/dev/null')
        if not rc and output and not re.match('unreachable', output):
            words = re.split('\s+', output) 
            i = 0
            length = len(words)
            length -= 1
            while i < length:
                word = words[i]
                if word == 'via':
                    i += 1
                    facts['default_gateway'] = words[i]
                elif word == 'dev':
                    i += 1
                    facts['network_default_ipv4_interface'] = words[i]
                elif word == 'src':
                    i += 1
                    facts['network_default_ipv4_address'] = words[i]    
                i += 1
        if facts['network_default_ipv4_interface']:
            rc,output =  sysobj.cmd("ifconfig " + facts['network_default_ipv4_interface'])
            res_list = output.split('\n')
            for item in res_list:
                if item and re.search('Mask:',item):
                    facts['netmask'] = re.sub('.*Mask:','',item)
                    break

        # get default ipv6 interface
        rc, output = sysobj.cmd(ip_bin_path+' -6 route get 2404:6800:400a:800::1012 2>/dev/null')
        if not rc and output and not re.match('unreachable', output):
            words = re.split('\s+', output) 
            i = 0 
            length = len(words)
            length -= 1
            while i < length:
                word = words[i]
                if word == 'via':
                    i += 1
                    facts['network_default_ipv6_gateway'] = words[i]
                elif word == 'dev':
                    i += 1
                    facts['network_default_ipv6_interface'] = words[i]
                elif word == 'src':
                    i += 1
                    facts['network_default_ipv6_address'] = words[i]      
                i += 1

        ## get domain name, dns
        rc, output = sysobj.cmd("cat /etc/resolv.conf")
        res_list = output.split('\n')
        facts['nameserver']=[]
        facts['domainname']=[]
        for item in res_list:
            if item and not re.search('^#', item):
                if re.search('nameserver',item):
                    sysobj.log.info("nameserver is  " + item + "   " + re.split('\s+',item)[1])
                    facts['nameserver'].append(re.split('\s+',item)[1])
                elif re.search('domain|search',item):
                    temp = re.sub('^domain\s+|^search\s+','',item)
                    if re.search(r',',temp):
                        sysobj.log.info("temp is  " + temp)
                        facts['domainname'].extend(temp.split(','))
                    else:
                        sysobj.log.info("temp is  " + temp)
                        facts['domainname'].append(temp)

        # get all network interfaces
        rc, output = sysobj.cmd('_cmd_ls '+if_class_path+'/ |egrep -v \'lo|virbr\' 2>/dev/null')
        if not rc and output:
            for interface in re.split('\s+', output):
                if interface == '.' or interface == '..': continue
                if_path = if_class_path+'/'+interface
                # get all IPv4 and IPv6 address
                all_ipv4_addresses = []
                all_ipv6_addresses = []
                rc, output = sysobj.cmd(ip_bin_path+' -o addr show dev '+interface+' 2>/dev/null')
                if not rc and output:
                    for line in re.split('\n+', output):
                        match = re.search('inet\s+(\S+)', line)
                        if match: 
                            all_ipv4_addresses.append(match.group(1))
                        else:
                             match = re.search('inet6\s+(\S+)', line)
                             if match: all_ipv6_addresses.append(match.group(1)) 
                    if all_ipv4_addresses:
                        facts.setdefault('network_interfaces',{}).setdefault(interface,{})['ipv4_addresses'] = all_ipv4_addresses
                    if all_ipv6_addresses:
                        facts.setdefault('network_interfaces',{}).setdefault(interface,{})['ipv6_addresses'] = all_ipv6_addresses
                else:
                    continue
     
                # get interface macaddress
                file = if_path + '/address'
                try:
                    with open(file, 'r') as fh:
                        content = fh.read()
                        content = content.rstrip('\n')
                        if content and content != '00:00:00:00:00:00': facts.setdefault('network_interfaces',{}).setdefault(interface,{})['macaddress'] = content
                except OSError: pass

                # get interface state
                file = if_path + '/operstate'
                try:
                    with open(file, 'r') as fh:
                        content = fh.read()
                        content = content.rstrip('\n')
                        if content and content == 'up': facts.setdefault('network_interfaces',{}).setdefault(interface,{})['active'] = 'true'
                except OSError: pass                    

                # get interface mtu
                file = if_path + '/mtu'
                try:
                    with open(file, 'r') as fh:
                        content = fh.read()
                        content = content.rstrip('\n')
                        if content: facts.setdefault('network_interfaces',{}).setdefault(interface,{})['mtu'] = content
                except OSError: pass

                # get interface speed
                file = if_path + '/speed'
                try:
                    with open(file, 'r') as fh:
                        #content = fh.read()
                        #content = content.rstrip('\n')
                        if content: facts.setdefault('network_interfaces',{}).setdefault(interface,{})['speed'] = content
                except OSError: pass
    
                # get interface type
                file = if_path + '/type'
                try:
                    with open(file, 'r') as fh:
                        content = fh.read()
                        content = content.rstrip('\n')
                        type = ''
                        if content == '1': type = 'ether'
                        elif content == '512': type = 'ppp'
                        elif content == '772': type = 'loopback'
                        if type: facts.setdefault('network_interfaces',{}).setdefault(interface,{})['type'] = type 
                except OSError: pass

#                # get if the interface is bridged
#                file = if_path + '/bridge'
#                if os.path.exists(file):
#                    facts.setdefault('network_interfaces',{}).setdefault(interface,{})['type'] = 'bridge'               
#
#                    rc, output = sysobj.cmd('_cmd_ls '+if_path+'/brif/ 2>/dev/null')
#                    if not rc and output: facts.setdefault('network_interfaces',{}).setdefault(interface,{})['interfaces'] = re.split('\s+',output)
#                    
#                    file = if_path + '/bridge/bridge_id'
#                    try:
#                        with open(file, 'r') as fh:
#                            content = fh.read()
#                            content = content.rstrip('\n')
#                            if content: facts.setdefault('network_interfaces',{}).setdefault(interface,{})['id']  = content                   
#                    except OSError: pass
                    
                # get if the interface is bonded
                file = if_path + '/bonding'
                if os.path.exists(file):
                    facts.setdefault('network_interfaces',{}).setdefault(interface,{})['type'] = 'bonding'
                    
                    rc, output = sysobj.cmd('_cmd_ls '+if_path+'/bonding/slaves 2>/dev/null')
                    if not rc and output: facts.setdefault('network_interfaces',{}).setdefault(interface,{})['interfaces'] = re.split('\s+',output)

                    file = if_path + '/bonding/slaves'
                    try:
                        with open(file, 'r') as fh:
                            content = fh.read()
                            content = content.rstrip('\n')
                            if content: facts.setdefault('network_interfaces',{}).setdefault(interface,{})['slaves']  = content
                    except OSError: pass

        return facts


