class LinuxHardware:
    def execute(self, sysobj):
        facts = {}
        with open('/proc/meminfo', 'r') as meminfo:
            content = meminfo.read()
            memory_facts = ('MemTotal', 'SwapTotal', 'MemFree', 'SwapFree')
            for key in memory_facts:
                tmp_match = re.search(key+':\D*(\d+)', content).group(1)
                if tmp_match:
                    facts[key.lower()+'_mb'] = int(tmp_match)//1024                  

        with open('/proc/cpuinfo', 'r') as cpuinfo:
            content = cpuinfo.read()
            facts['processor_count'] = int(content.count('processor'))
            #cpu_facts = ('cpu_cores', 'cpu_MHz', 'vendor_id', 'model_name')
            cpu_facts = {'cpu_cores':'cpu cores','cpu_MHz':'cpu MHz', 'vendor_id':'vendor_id','model_name':'model name'}
            for key,value in cpu_facts.items():
                if key == 'model_name' or key == 'vendor_id':
                    facts[key] = re.search(value+'\s+: (.+)',content).group(1)
                else:
                    facts[key] = re.search(value+'\D*:\D*(\d+)', content).group(1)
                if key == 'cpu_MHz':
                    facts[key] = int(facts[key])
                if key == 'cpu_cores':
                    facts[key] = int(facts[key])
        return facts

class SunOSHardware:
    def execute(self, sysobj):
        facts = {}
        rc, mem = sysobj.cmd("_cmd_prtconf | grep Mem | awk '{print $3}'")
        if mem: 
            facts['memtotal_mb'] = int(mem)//1024
        rc, swap = sysobj.cmd('_cmd_swap -s')
        if rc == 0 and swap:
            tmp_match = re.search('.*= (\d+)k used, (\d+)k available', swap)
            facts['swaptotal_mb'] = ( int(tmp_match.group(1))+int(tmp_match.group(2)) )//1024
            facts['swapfree_mb'] = int(tmp_match.group(1))//1024

        rc, cpu = sysobj.cmd('_cmd_kstat cpu_info')
        if rc == 0 and cpu:
            facts['processor_count'] = int(cpu.count('instance:'))
            cpu_facts = ('clock_MHz', 'brand', 'cpu_type')
            facts['clock_MHz'] = int( re.search('clock_MHz\D* (\d+)', cpu).group(1) )
            facts['brand'] = re.search('brand\s+ (.+)', cpu).group(1)
            facts['cpu_type'] = re.search('cpu_type\s+ (.+)', cpu).group(1)
        return facts

