class LinuxSoftware:
    def execute(self, sysobj):
        facts = {}
        # get basic OS packages
        rc, output = sysobj.cmd('_cmd_rpm -qa --qf "%{NAME}\.%{ARCH}\n" 2>/dev/null')
        if not rc and output:
            os_packages = ''
            m = re.compile(r'^(gtk|php|Py|py|libX|xorg|gnome|kde|qt|m17n|xml|mesa|VRTS|SYMC)| \
                           devel|lang|l10n|gnome|gtk|desktop|python|emacs|eclipse|firmware|fonts|docs')
            for line in re.split('\n+', output):
                # kick off those extra packages, like docs, l10n, fonts, desktop packages
                if m.search(line):
                    continue
                os_packages = os_packages+line+' '
            os_packages = os_packages.rstrip()
            facts['os_packages'] = os_packages
        rc, output = sysobj.cmd('_cmd_rpm -qa|grep VRTS|grep -v vita|grep -v python')
        vrts_packages = ''
        for line in re.split('\n+', output):
            vrts_packages = vrts_packages + line + ' '
        vrts_packages = vrts_packages.rstrip()
        facts['vrts_packages'] = vrts_packages

        return facts

