    def check_onenode_cluster(self,sysobj):
        onenode = 0
        vcsobj = instance_factory(sysobj,'vcs'.capitalize(),'None', 'Prod')
        vcs_initfile = vcsobj.initfile['vcs']
        if os.path.exists(vcs_initfile):
            rc,output = sysobj.cmd('unset ONENODE; . ' + vcs_initfile + ' >/dev/null 2>/dev/null ; echo $ONENODE')
            if output == 'yes':
                onenode = 1
        sysobj.args['parameter']['onenode_cluster'] = onenode
