    def get_cluster_nodes(self, sysobj):
        nodes = []
        if not os.path.exists('/etc/llthosts'):
            # check if onenode cluster
            host_facts = sysobj.args['parameter']
            if 'onenode_cluster' in host_facts.keys() and host_facts['onenode_cluster'] == 1:
                nodes.append(sysobj.hostname)
            return nodes
        rc, content = sysobj.cmd("_cmd_cat /etc/llthosts")
        lines = re.split('\n', content)
        for line in lines:
            ma = re.search('\d+\s+(\S+)', line)
            if ma:
                nodes.append(ma.group(1))

        return nodes
    
    def get_id_and_node(self, sysobj):
        nodes = {}
        if not os.path.exists('/etc/llthosts'):
            return nodes
        rc, content = sysobj.cmd("_cmd_cat /etc/llthosts")
        lines = re.split('\n', content)
        for line in lines:
            ma = re.search('(\d+)\s+(\S+)', line)
            if ma:
                nodes[ma.group(2)] = ma.group(1)
                # for rever check
                nodes[ma.group(1)] = ma.group(2)
        return nodes
   
    # return form: "/24"
    def get_prefix_from_ip(self, sysobj, nic, ip):
        rc, output = sysobj.cmd("ip addr show " + nic + " | _cmd_grep " + ip)
        ma = re.search(ip + '(/\d+)', output)
        if ma:
            return ma.group(1)
        else:
            sysobj.log.error("Can not match prefix for IP:" + ip + " on " + nic)
            return ""

    def get_llt_config(self, sysobj, facts):

        udp_link = []
        content = ''
        rc, localnode_name = sysobj.cmd('_cmd_cat /etc/VRTSvcs/conf/sysname')
        if os.path.exists('/etc/llttab'):
            rc, content = sysobj.cmd("_cmd_cat /etc/llttab")
        else:
            return
        node_dict = self.get_id_and_node(sysobj)
        sysobj.log.info(str(node_dict))

        # three helper inner dict
        nic_port = {}
        nic_pri = {}
        host_peer_udp = {}
        nic_2_prefix = {}

        local_udp_link = {}
        local_udp_link['system'] = localnode_name

        if re.search('udp\s+-\s+(\w+)', content):
            ma = re.search('udp - (\w+)', content)
            facts['method'] = ma.group(1)
            udp_link.append(local_udp_link)
        elif re.search ('ether', content):
            facts['method'] = 'ethernet'
        if 'method' not in facts.keys():
            return

        else:
            sysobj.log.debug('Method:' + facts['method'])
        lines = re.split('\n', content)
        for line in lines:
            # local link
            ma = re.search('(link)\s+(\S+)\s+eth', line) or re.search('(link-lowpri)\s+(\S+)\s+eth', line)
            if ma:
                if 'link' == ma.group(1):
                    link_pri_type = 'private_link'
                elif 'link-lowpri' == ma.group(1):
                    link_pri_type = 'low_priority_link'
                facts.setdefault(link_pri_type, [])
                facts[link_pri_type].append(ma.group(2))
                continue

            ma = re.search('(link)\s+(\S+)\s+udp\s+-\s+' + facts['method'] + '\s+(\S+)\s+-\s+(\S+)\s+-', line) or \
                    re.search('(link-lowpri)\s+(\S+)\s+udp\s+-\s+' + facts['method'] + '\s+(\S+)\s+-\s+(\S+)\s+-', line)
            if ma:
                one_nic_link = {}
                one_nic_link['nic'] = ma.group(2)
                one_nic_link['port'] = ma.group(3)
                nic_2_prefix[one_nic_link['nic']] = self.get_prefix_from_ip(sysobj, one_nic_link['nic'], ma.group(4))
                one_nic_link['ip'] = ma.group(4) + nic_2_prefix[one_nic_link['nic']]
                nic_port[one_nic_link['nic']] = one_nic_link['port']
                if 'link' == ma.group(1):
                    link_pri_type = 'heartbeat_link'
                elif 'link-lowpri' == ma.group(1):
                    link_pri_type = 'low_priority_link'

                nic_pri[one_nic_link['nic']] = link_pri_type
                local_udp_link.setdefault(link_pri_type, [])
                local_udp_link[link_pri_type].append(one_nic_link)
                continue

            # udp or tcp peer's
            ma = re.search('set-addr\s+(\d+)\s+(\S+)\s+(\S+)', line)
            if ma:
                hostname = node_dict[ma.group(1)]
                if host_peer_udp.get(hostname):
                    one_peer_udp_link = host_peer_udp[hostname]
                else:
                    one_peer_udp_link = {}
                    udp_link.append(one_peer_udp_link)
                    host_peer_udp[hostname] = one_peer_udp_link
                    one_peer_udp_link['system'] = hostname
                    one_peer_udp_link.setdefault('heartbeat_link', [])
 
                one_nic_link = {}
                one_nic_link['nic'] = ma.group(2)
                # make sure that the local link config before peers'
                one_nic_link['port'] = nic_port[one_nic_link['nic']]
                # usually, we think that the peers use the same prefix for same nic
                one_nic_link['ip'] = ma.group(3) + nic_2_prefix[one_nic_link['nic']]
                one_peer_udp_link.setdefault(nic_pri[one_nic_link['nic']], [])
                one_peer_udp_link[nic_pri[one_nic_link['nic']]].append(one_nic_link)
                continue
        for node in facts['all_nodes']:
            facts.setdefault('node_ids', {})
            facts['node_ids'][node] = node_dict[node]
        facts[facts['method'] + '_link'] = udp_link
        return

    def get_fencing_config(self, sysobj, facts):
        rc, vxfenmode = sysobj.cmd("_cmd_vxfenadm -d 2>/dev/null | _cmd_grep 'Fencing Mode' | _cmd_awk '{print $3}'")
        if not vxfenmode:
            facts['vxfenmode'] = 'N/A'
            return
        facts['vxfenmode'] = vxfenmode
        if 'SCSI3' == facts['vxfenmode']:
            facts['vxfenmode'] = 'disks'
        elif 'Customized' == facts['vxfenmode']:
            facts['vxfenmode'] = 'cps'
        if not facts['vxfenmode']:
            facts['vxfenmode'] = 'disabled'
        facts['vxfenmode'] = facts['vxfenmode'].lower()
        facts.setdefault('cps', [])
        facts.setdefault('disks', [])
        rc, content = sysobj.cmd("_cmd_vxfenconfig -l")   
        for line in re.split('\n', content):
            ma = re.search('\[(.*)\]:(\d+)', line)
            if ma:
                facts['cps'].append(ma.group(1) + ':' + ma.group(2))
                continue
            ma = re.search('/dev/vx/rdmp/(.*)\s+', line)
            if ma:
                facts['disks'].append(ma.group(1))
                continue
        rc, content = sysobj.cmd("_cmd_cat /etc/vxfenmode | _cmd_grep vxfendg")
        ma = re.search('vxfendg=(.*)', content)
        if ma:
            facts['vxfendg'] = ma.group(1)
        rc, check_agent_disk = sysobj.cmd("_cmd_cat /etc/VRTSvcs/conf/config/main.cf | _cmd_grep CoordPoint")       
        if not check_agent_disk:
            facts['cpagent'] = '0'

        return
               
    def get_vxtune_value(self, sysobj, facts):
        asymmetry_value = ''
        asymmetry_key = 'storage_connectivity'
        rc, out = sysobj.cmd('_cmd_vxtune ' + asymmetry_key)            
        if not rc:
            ma = re.search(asymmetry_key + '\s+(\w+)', out)
            if ma:
                asymmetry_value = ma.group(1)
        facts['asymmetry_value'] = asymmetry_value
        return

    def get_disks(self, sysobj, facts):

        free_list = []
        rc, all_list = sysobj.cmd('_cmd_vxdisk -o fullshared -o alldgs list 2>/dev/null | _cmd_grep online |_cmd_grep cdsdisk')
        if rc :
            rc, all_list = sysobj.cmd('_cmd_vxdisk -o alldgs list 2>/dev/null | _cmd_grep online |_cmd_grep cdsdisk')
        if not all_list:
            facts['free_disks'] = []
            return
        for line in re.split('\n', all_list):
            if re.search('DEVICE\s+TYPE', line): continue
            (disk, undef, undef, group, status) = re.split('\s+', line, 4)
            if '-' == group:
                if not re.search('exported|remote', status):
                    free_list.append(disk)
        facts['free_disks'] = free_list
        return

    def get_system_status(self, sysobj, facts):
        #if 'SF' == facts['component']:
        if re.search('^SF\d+', facts['component']):
            rc, out = sysobj.cmd("_cmd_lsmod | _cmd_awk '{print $1}' | _cmd_grep 'vxportal\|fdd\|vxfs'")
            if 3 == len(re.split('\n', out)):
                facts['system_state'] = 'healthy'
            elif 0 == len(re.split('\n', out)):
                facts['system_state'] = 'stopped'
            else:
                facts['system_state'] = 'unhealthy'
        else:
            rc, hastatus = sysobj.cmd('_cmd_hastatus -sum | _cmd_grep ' + sysobj.hostname)
            if rc:
                facts['system_state'] = 'stopped'
                return

            facts['system_state'] = 'healthy'
            for line in re.split('\n', hastatus):
                ma = re.search('A\s+(\w+)\s+(\w+)', line)
                if re.search('fail|fault|^D\s+|^J\s+|^R\s+', line, re.I):
                    facts['system_state'] = 'unhealthy'
                    return
            return

    def get_cluster_info(self, sysobj, facts):
        facts['onenode_cluster'] = self.check_onenode_cluster(sysobj)
        facts['all_nodes'] = self.get_cluster_nodes(sysobj)
        if facts['onenode_cluster'] == 1:
            facts['cluster_id'] = facts['cluster_uuid'] = facts['cluster_name'] = ''
            facts['cluster_name'] = self.get_cluster_name(sysobj)
            facts['cluster_uuid'] = self.get_cluster_uuid(sysobj)
        else:
            facts['cluster_id'] = self.get_cluster_id(sysobj)
            facts['cluster_uuid'] = self.get_cluster_uuid(sysobj)
            facts['cluster_name'] = self.get_cluster_name(sysobj)
            self.get_llt_config(sysobj, facts)
            self.get_fencing_config(sysobj, facts)
            self.get_gab_info(sysobj,facts)
            self.get_llt_info(sysobj,facts)

        facts['CFSSG_SVC_GRP'] = self.get_CFSSG_SVC_GRP(sysobj)
        self.get_vxtune_value(sysobj, facts)
        self.get_disks(sysobj, facts)
        self.get_system_status(sysobj, facts)
        #TODO
        #self.get_gco_nic(sysobj, facts)
        return

    def get_CFSSG_SVC_GRP(self,sysobj):
        rtn = ''
        rc, out = sysobj.cmd("_cmd_grep '^CFSSG_SVC_GRP' /opt/VRTSvxfs/cfs/lib/cfsadm.env 2> /dev/null")
        ma = re.match('CFSSG_SVC_GRP\s*=\s*(.*)', out)
        if ma:
            cfsnfssg = ma.group(1)
            cfsnfssg = cfsnfssg.strip()
            rc, out = sysobj.cmd("_cmd_hagrp -display " + cfsnfssg)
            if rc != 0:
                #possibly VCS not running:
                maincf_file_path = '/etc/VRTSvcs/conf/config/main.cf'
                with open(maincf_file_path, 'r') as fh:
                    maincf_content= fh.read()
                    if not re.search ("^\s*group\s+" + cfsnfssg,maincf_content,re.M):
                        return rtn
                    else:
                        return cfsnfssg
        else:
            return rtn

    def get_license(self, sysobj, facts):
        facts['license'] = []
        if 'infoscale' == facts['veritas']:
            rc, output = sysobj.cmd("_cmd_vxlicrep")
            expect_product = ('Veritas InfoScale Foundation', 'Veritas InfoScale Storage', \
                    'Veritas InfoScale Availability', 'Veritas InfoScale Enterprise')
            parts = re.split('\s+\-+\*+', output)
            for part in parts:
                license_info = {}
                for product in expect_product:
                    if re.search(product, part):
                        license_info['Product Name'] = product
                        if re.search('VXKEYLESS\s+=\s+Enabled', part):
                            license_info['License Type'] = 'KEYLESS'
                        else:
                            license_info['License Type'] = 'PERMANENT'
                        ma = re.search('Version\s+=\s+(.*)', part,re.I)
                        if ma:
                            license_info['Version'] = ma.group(1).rstrip()
                        facts['license'].append(license_info)
                        break
        if 'Access' == facts['veritas']:
            license_info = {}
            rc, output = sysobj.cmd("/opt/VRTSslic/bin/vxlicrep")
            result_list = output.split('\n')
            for item in result_list:
                if re.search('Version\s+=\s+(.*)', item):
                    license_info['Version'] = item.split('=')[1].strip()
                elif re.search('Product Name\s+=\s+(.*)', item):
                    license_info['Product Name'] = item.split('=')[1].strip()
                elif re.search('License_type\s+=\s+(.*)', item):
                    license_info['License Type'] = item.split('=')[1].strip()
                elif re.search('Start_date\s+=\s+(.*)', item):
                    license_info['Start Date'] = item.split('=')[1].strip()
                elif re.search('End_date\s+=\s+(.*)', item):
                    license_info['End Date'] = item.split('=')[1].strip()
            facts['license'].append(license_info)

        return

    def get_infoscale_info(self,sysobj,facts):
        LS = LinuxSoftware()
        vrts_pkgs = LS.execute(sysobj)['vrts_packages']
        if (not vrts_pkgs):
            return facts
        sys = sysobj.hostname
        padv = sysobj.padv
        matrix_path = sysobj.matrix
        vc = Version_check(sys, padv, matrix_path, vrts_pkgs)
        vc.execute()
        if (len(vc.out['installed_products'])):
            for installed_product in vc.out['installed_products']:
                facts['product'] = installed_product['product_abbr']
                veritas = installed_product['release_name'].split('-')[0]
                facts['veritas'] = veritas
                version = ''
                if re.search('MR',installed_product['release_name']):
                    version = installed_product['release_name'].split('-')[3]
                else: 
                    version = installed_product['release_name'].split('-')[2]
                facts['version'] = version
                self.get_license(sysobj, facts)
                facts['onenode_cluster'] = 0
                if os.path.exists('/etc/VRTSvcs/conf/config/main.cf'):
                    self.get_cluster_info(sysobj, facts)
                    facts['haconf_writable'] = self.check_haconf_permission(sysobj)
                if 'product' in facts.keys() and facts['product'].lower() == 'storage':
                    self.adjust_prodname_vers_RU(sysobj,facts)
                sysobj.args['parameter']['host_facters'] = facts
                configcomp_obj = ConfiguredComponent()
                facts['component'] = configcomp_obj.get_configured_component(sysobj)
                self.get_disks_info(sysobj, facts)
                self.get_dg_info(sysobj,facts)
                self.get_sharedg_mount(sysobj,facts)
                self.get_scsi3pr_setting(sysobj,facts)
                #component_path = get_component_path()
                #rc, output = sysobj.cmd('_cmd_cat ' + component_path + ' 2>/dev/null')
                #if not rc and output:
                #    component = output.split('=')[1].strip()
                #    facts['component'] = component
        return

    def get_access_info(self,sysobj,facts):
        facts['veritas'] = 'Access'
        facts['product'] = 'Access'
        facts['component'] = ''
        if os.path.isfile("/opt/VRTSnas/conf/version.conf"):
            rc, output = sysobj.cmd("cat /opt/VRTSnas/conf/version.conf")
            res_list = output.split('\n')
            for line in res_list:
                if line and re.search('|', line):
                    facts['version'] = line.split('|')[0]
        if os.path.isfile("/opt/VRTSnas/conf/cluster_type"):
            facts['component'] = 'SFCFSHA'
            self.get_cluster_info(sysobj, facts)
            self.get_license(sysobj, facts)
        return

    def execute(self, sysobj):
        facts = {}
        facts['veritas'] = ''
        facts['product'] = ''
        facts['version'] = ''
        facts['component'] = ''

        rc, nas_pkg = sysobj.cmd("_cmd_rpm -qa |grep -w VRTSnas")
        if re.search("VRTSnas", nas_pkg):
            self.get_access_info(sysobj,facts)
        else:
            self.get_infoscale_info(sysobj,facts)
        return facts

    def adjust_prodname_vers_RU(self,sysobj,facts):
        nonkernelpkgs = ['VRTSvcs']
        check=0
        for pkg in nonkernelpkgs:
            rc,iv =sysobj.cmd("_cmd_rpm -q --queryformat '%{VERSION}' " + pkg + " 2> /dev/null")
            if re.search('not installed',iv,re.M):
                check += 0
            else:
                check += 1
        if check != 0 and check == len(nonkernelpkgs):
            facts['product'] = 'enterprise'
            rc,facts['version'] = sysobj.cmd("_cmd_rpm -q --queryformat '%{VERSION}' VRTSvcs 2> /dev/null")


