class Facter:
    b_store_msg = True 
    def __init__(self, args):
        self.args = args
        #self.plat = os.uname().sysname

    def execute(self, sysobj):
        rtn = {'rc':0, 'changed':0, 'msg':''}
        facter = {}
        items = []

        if DONOTCHECKSORT == 1:
            sysobj.matrix = USER_DEFINED_MATRIX_PATH
            sysobj.matrix = self.dump_matrix(sysobj)
        else:
            #sysobj.matrix = self.dump_matrix(sysobj)
            sysobj.matrix = self.dump_sort_matrix(sysobj)

        if sysobj.matrix == '':
            rtn['msg'] = sysobj.error
            rtn['rc'] = 1
            return rtn

        sysobj.release_data = self.dump_release_data(sysobj)
        self.plat=sysobj.plat

        if self.args and self.args.get('items'):
            items = self.args.get('items')
        else:
            items = ['environment', 'hardware', 'network', 'operatingsystem', 'software', 'product']
        for item in items:
            item_obj = eval(self.plat+item.capitalize()+'()')
            facter[item] = item_obj.execute(sysobj) 
                
        #output = sysobj.logdir_base + '/' + sysobj.hostname + '_facts.json'
        output = sysobj.args['parameter']['facts']
        if (os.path.exists(output)):
            os.remove(output)
        sysobj.cmd("_cmd_rm -f " + output)
        with open(output, 'w') as f:
            json.dump({sysobj.hostname:facter}, f, sort_keys=True, indent=4)
        sysobj.log.info('The facter information is saved to ' + output)
        rtn['msg'] = "facter created"
        # Add some more keys in dict of infoscale facts
        info_facts = facter['product']
        info_facts.update({"padv":sysobj.padv})
        rtn['infoscale_facts'] = {sysobj.hostname:json.dumps(info_facts)}

        return rtn

    def dump_matrix(self,sysobj):
        m_path = sysobj.logdir_base + '/' + 'matrix'
        if (os.path.exists(m_path)):
            shutil.rmtree(m_path)
        os.mkdir(m_path)
        m_data = sysobj.matrix_data.replace('\"\"\"','').replace('\n','').replace('\r','')
        levels = ['ga','hotfix','patch','kernel']

        #ma_data = json.dumps(m_data)
        mat_data = json.loads(m_data)
        for level in levels:
            if level == 'kernel':
                with open(m_path + '/' + 'kernel_padv.json', "w") as w_file:
                    #w_file.write(mat_data['kernel'])
                    json.dump(mat_data['kernel'], w_file)
            else:
                with open(m_path + '/' + sysobj.padv + '-' + level + '.json' , "w") as w_file:
                    try:
                        json.dump({sysobj.padv:mat_data[level][sysobj.padv]}, w_file,indent=4)
                    except:
                        sysobj.log.info('no padv matrix data')
        #sysobj.log.info (m_data)
        return m_path

    def dump_sort_matrix(self,sysobj):
        m_path = sysobj.args['parameter']['matric']
        if (os.path.exists(m_path)):
            shutil.rmtree(m_path)
        os.mkdir(m_path)
        sort_obj = Sort()
        if sort_obj.download_matrix_json(sysobj.padv,m_path) and sort_obj.download_kernel_json(m_path):
            return m_path
        else:
            sysobj.error.append({"Matrix download from sort": "failed"})
            return ''

