        f_path = sysobj.logdir_base + '/' + 'release_data.json'
        if (os.path.exists(f_path)):
            sysobj.cmd("_cmd_rm -fr " + f_path)
        file_data = sysobj.release_data.replace('\"\"\"','').replace('\n','').replace('\r','')
        f_data = json.loads(file_data)
        with open(f_path,"w") as w_file:
            json.dump(f_data,w_file, indent=4)

