class LinuxOperatingsystem:
    def match_pattern(self, pattern, file_name):
        with open(file_name, 'r') as fh:
            content = fh.read().rstrip('\r\n')
            match = re.search(pattern, content)
            return match

    def execute(self, sysobj):
        facter = {}
        undef, facter['platform'] = sysobj.cmd('_cmd_uname -s 2>/dev/null')
        undef, facter['architecture'] = sysobj.cmd('_cmd_uname -m 2>/dev/null')
        undef, facter['nodename'] = sysobj.cmd('_cmd_uname -n 2>/dev/null')
        undef, facter['hostname'] = sysobj.cmd('_cmd_hostname 2>/dev/null')
        undef, facter['kernel_version'] = sysobj.cmd('_cmd_uname -r 2>/dev/null')
        undef, facter['umask'] = sysobj.cmd('_cmd_umask 2>/dev/null')

        # read kernel_padv and decide the OS level
        m_path = sysobj.logdir_base + '/' + 'matrix/kernel_padv.json'
        kernel_data = readfile(m_path,'json')

        facter['sort_kernel'] = self.get_sort_kernel(sysobj,facter['kernel_version'])

        if facter['sort_kernel'] == '':
            sysobj.log.debug( facter['kernel_version'] + ' not in kernel padv.')
            facter['padv'] = facter['distribution'] = facter['minor'] = facter['distribution_major_version'] = facter['distribution_minor_version'] = facter['distribution_version'] = '' 
        else:
            kernel_info = kernel_data[facter['sort_kernel']]
            facter['padv'] = kernel_info['padv']

            if re.search('^oel',kernel_info['padv'],re.I):
                facter['distribution'] = 'OracleLinux'
                facter['minor'] = ''
                if kernel_info['level']:
                    facter['minor'] = 'update'
                    m = re.search('oel(\d+)(\S+)',kernel_info['padv'])
                    if m:
                        facter['distribution_major_version'] = m.group(1)

                    m = re.search('UEK\s+\S+\s+Update\s(\d+)',kernel_info['level'])
                    if m:
                        facter['distribution_minor_version'] = m.group(1)

                    facter['distribution_version'] = facter['distribution_major_version'] + '.' + facter['distribution_minor_version']
            elif re.search('^rhel',kernel_info['padv'],re.I):
                facter['distribution'] = 'RedHat'
                facter['minor'] = ''
                if kernel_info['level']:
                    facter['minor'] = 'update'
                    m = re.search('rhel(\d+)(\S+)',kernel_info['padv'])
                    if m:
                        facter['distribution_major_version'] = m.group(1)

                    m = re.search('Update\s(\d+)',kernel_info['level'])
                    if m:
                        facter['distribution_minor_version'] = m.group(1)

                    facter['distribution_version'] = facter['distribution_major_version'] + '.' + facter['distribution_minor_version']

            elif re.search('^sles',kernel_info['padv'],re.I):
                facter['distribution'] = 'SuSE'
                facter['minor'] = ''
                if kernel_info['level']:
                    facter['minor'] = 'SP'
                    m = re.search('sles(\d+)(\S+)',kernel_info['padv'])
                    if m:
                        facter['distribution_major_version'] = m.group(1)

                    m = re.search('SP(\d+)',kernel_info['level'])
                    if m:
                        facter['distribution_minor_version'] = m.group(1)

                    facter['distribution_version'] = facter['distribution_major_version'] + '.' + facter['distribution_minor_version']

        # get kernel_bits
        rc, output = sysobj.cmd('_cmd_getconf LONG_BIT 2>/dev/null')
        if not rc and output:
            facter['kernel_bits'] = output

        # get BIOS and DMI information
        dmi_files = {}
        if os.path.exists('/sys/devices/virtual/dmi/id/product_name'):
            # DMI SPEC -- http://www.dmtf.org/sites/default/files/standards/documents/DSP0134_2.7.0.pdf
            dmi_files.update({
                'bios_release_date': '/sys/devices/virtual/dmi/id/bios_date',
                'bios_version': '/sys/devices/virtual/dmi/id/bios_version',
                'bios_vendor': '/sys/devices/virtual/dmi/id/bios_vendor',
                'system_product_name': '/sys/devices/virtual/dmi/id/product_name',
                'system_serial_number': '/sys/devices/virtual/dmi/id/product_serial',
                'system_uuid': '/sys/devices/virtual/dmi/id/product_uuid',
                'system_version': '/sys/devices/virtual/dmi/id/product_version',
                'system_manufacturer': '/sys/devices/virtual/dmi/id/sys_vendor',                           
            })
            
            for key, value in dmi_files.items():
                with open(value, 'r') as fh:
                    content = fh.read().rstrip('\r\n')
                    facter[key] = content if content else 'NA'
        else:
            dmi_files.update({
                'bios_release_date': 'bios-release-date',
                'bios_version': 'bios-version',
                'bios_vendor': 'bios-vendor',
                'system_product_name': 'system-product-name',
                'system_serial_number': 'system-serial-number',
                'system_uuid': 'system-uuid',
                'system_version': 'system-version',
                'system_manufacturer': 'system-manufacturer'
            })
            for key, value in dmi_files.items():
                rc, output = sysobj.cmd('_cmd_dmidecode -s ' + value)
                output = output.rstrip('\r\n')
                facter[key] = output if not rc and output else 'NA'
                    
        # get virtualization information
        # For more information, check: http://people.redhat.com/~rjones/virt-what/
        system_product_name = facter['system_product_name']
        if re.search('VMware', system_product_name, re.I):
            facter['virtualization_type'] = 'VMware'
            facter['virtualization_role'] = 'guest'
        elif re.search('VirtualBox', system_product_name, re.I):
            facter['virtualization_type'] = 'VirtualBox'
            facter['virtualization_role'] = 'guest'
        elif re.search('RHEV', system_product_name, re.I):
            facter['virtualization_type'] = 'RHEV'
            facter['virtualization_role'] = 'guest'
        elif re.search('RHEV', system_product_name, re.I):
            facter['virtualization_type'] = 'KVM'
            facter['virtualization_role'] = 'guest'
            
        # get selinux
        if os.path.exists('/etc/selinux/config'):
            m = self.match_pattern('^SELINUX=\s*(\S+)', '/etc/selinux/config')
            if m:
                facter['selinux_mode'] = m.group(1)
            else:
                facter['selinux_mode'] = 'not_enabled'
        else:
            facter['selinux_mode'] = 'na'

        # get fips
        with open('/proc/sys/crypto/fips_enabled', 'r') as fh:
            content = fh.read().rstrip('\r\n')
            if content and content == '1':
                facter['fips'] = 1

        return facter

    def get_sort_kernel(self,sysobj,kstring):
        # read kernel_padv and decide the OS level
        m_path = sysobj.logdir_base + '/' + 'matrix/kernel_padv.json'
        kernel_data = readfile(m_path,'json')

        for kernel in kernel_data.keys():
            if re.search('^'+kernel, kstring, re.IGNORECASE) or re.search('^'+kstring, kernel, re.IGNORECASE):
                return kernel

        return ''
         

