class Yum:
    def __init__(self, arguments):
        self.args = arguments
        #v = re.match(r'^([A-Z]+)(\d+)$',self.args.get('product'))
        #prodv = '.'.join(list(v.group(2)))
        #prodname = product = v.group(1)
        self.keys_to_compare = ["product","version","padv"]

    def check_args(self,sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg':''}
        if self.args['state'] == 'absent':
            if sysobj.args['parameter']['host_facters']['product'] == '':
                rtn = {'changed': 0, 'rc': 0, 'msg':'No product installed on the system.'}
                return rtn
            if 'seednode' not in self.args.keys() or self.args['seednode'] == None:
                rtn = {'changed': 0, 'rc': 1, 'msg':'Argument seednode is required for uninstallation to stop the services if needed.'}

        if self.args['state'] == 'present':
            if not self.args.get('repository_name'):
                rtn['msg'] = "missing required arguments: repository_name"
                rtn['rc'] = 1

        return rtn

    def execute(self, sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg':''}
        rtn = self.check_args(sysobj)
        if rtn['rc'] == 1 or rtn['msg'] != '':
            return rtn
        prodname = self.args.get('product')
        prodvers = self.args.get('product_version')
        prodlist = get_prod_list(prodvers)
        
        if prodname.lower() not in prodlist:
            rtn['msg'] = 'Product name ' + prodname + ' not supported for Infoscale version ' + prodvers
            rtn['rc'] = 1
            return rtn

        if self.args.get('state') == 'absent':
            if prodname.lower() != sysobj.args['parameter']['host_facters']['product']:
                sysobj.error.append({'Prodname compare failed':'Product name in playbook ' + prodname.upper() + ' is different from installed product ' + sysobj.args['parameter']['host_facters']['product'].upper()})

            if normalize_version(prodvers) != sysobj.args['parameter']['host_facters']['version']:
                sysobj.error.append({'Product version compare failed':'Product version in playbook ' + prodvers + ' is different from installed version ' + sysobj.args['parameter']['host_facters']['version']})

            if sysobj.error:
                rtn['rc'] = 1
                rtn['msg'] = parse_msg(sysobj.error)
                return rtn

            self.args['task'] = 'uninstall'
            self.args['component'] = sysobj.args['parameter']['host_facters']['component']
            self.args['product'] = sysobj.args['parameter']['host_facters']['product']
            prodvers = self.args['product_version'] = sysobj.args['parameter']['host_facters']['version']
        else:
            prod = sysobj.args['parameter']['host_facters']['product']
            if prod and prod.upper() == self.args.get('product').upper():
                rtn['msg'] = 'Already ' + prod.upper() + ' ' + sysobj.args['parameter']['host_facters']['version'] + ' installed.'
                if compvers(sysobj.args['parameter']['host_facters']['version'],self.args['product_version']) != 0:
                    rtn['rc'] = 1
                return rtn
            self.args['task'] = 'install'
            self.args['component'] = ''
            self.args['prod'] = self.args.get('product')

        self.args['padv'] = sysobj.padv
        self.args['prod'] = self.args['product']
        self.args['release'] = prodvers

        sysobj.args['parameter'].update(self.args)

        rpm_name = self.args.get('product').upper() + (self.args.get('product_version')).replace('.','')
        if hasattr(sysobj,'upgrade_phase'):
            rpm_name = self.args['rpm_name']
        rpm_exists = 0

        if self.args['task'] == 'install':
            self.precheck_install(sysobj) 
            if sysobj.error:
                rtn['msg'] = parse_msg(sysobj.error)
                if re.search ('Infoscale is already installed. Installed product:',rtn['msg']):
                    rtn['rc'] = 0
                else:
                    rtn['rc'] = 1
                sysobj.log.debug(rtn['msg'])
                return rtn
            if sysobj.message:
                rtn['msg'] = parse_msg(sysobj.message)
                if re.search ('Infoscale is already installed. Installed product:',rtn['msg']):
                    rtn['rc'] = 0
                    return rtn

            if self.args.get('repository_baseurl'):
                repository_name = self.args.get('repository_name')
                repository_baseurl = self.args.get('repository_baseurl')
                gpgcheck = self.args.get('gpgcheck') or 0
                gpgkey = self.args.get('gpgkey') or ''

                mrtn = self.setup_yumrepo(sysobj,repository_name,repository_baseurl,gpgcheck,gpgkey)
                if mrtn['changed'] == 1:
                    rtn['msg'] = rtn['msg'] + "\n" + mrtn['msg']
                if mrtn['rc'] != 0:
                    rtn['msg'] = rtn['msg'] + "\n" + mrtn['msg']
                    rtn['rc'] = mrtn['rc']
                    return rtn
            mrtn = self.execute_install(sysobj,check_only=True)
            if mrtn['rc'] == 0 and not re.search('No new rpms to install',mrtn['msg']):
                mrtn = self.execute_install(sysobj)
            
        else:
            self.precheck_uninstall(sysobj)
            if sysobj.error:
                rtn['msg'] = parse_msg(sysobj.error)
                rtn['rc'] = 1
                sysobj.log.debug(rtn['msg'])
                return rtn

            self.args['seednode'] = sysobj.hostname

            # If no component is configured, no need to stop the services
            if self.args.get('component') != 'None':
                prod = self.args['component'].lower()
                prodobj = instance_factory(sysobj,prod,'None', 'Prod')

                prodobj.stop_precheck(sysobj)
                if sysobj.error:
                    rtn['msg'] = parse_msg(sysobj.error)
                    rtn['rc'] = 1
                    sysobj.log.debug(rtn['msg'])
                    return rtn
                prodobj.pre_stop(sysobj)
                proc = Process(self.args)
                srtn = proc.execute(sysobj);

                if srtn['msg'] != "Stop " + self.args['component'].upper() + " successfully":
                    rtn['msg'] = "Could not stop the processes: " + srtn['msg']
                    rtn['changed'] = 1
                    rtn['rc'] = 1
                    return rtn

            if not self.args.get('rpm_name'):
                prod_obj = instance_factory(sysobj,prodname.lower(),'None', 'Prod')
                pkgs = prod_obj.set_all_pkgs(sysobj,'uninstall')
            else:
                pkgs = self.args.get('rpm_name')

            mrtn = self.execute_uninstall(sysobj,pkgs)
            if mrtn['rc'] == 0:
                component_path = get_component_path()
                sysobj.cmd("_cmd_rmr " + component_path)
                rtn['changed'] = 1
                prod_obj = instance_factory(sysobj,prodname.lower(),'None', 'Prod')
                prod_obj.post_uninstall(sysobj)


        rtn['msg'] = rtn['msg'] + "\n" + mrtn['msg']
        rtn['changed'] = rtn['changed'] + mrtn['changed']
        rtn['rc'] = rtn['rc'] + mrtn['rc']

        return rtn

    def execute_install(self,sysobj,check_only=False):
        rtn = {'changed': 0, 'rc': 0, 'msg':''}
        repository_name = self.args.get('repository_name')
        if sysobj.args['parameter']['task'] == 'install':
            rpm_name = self.args.get('product').upper() + (self.args.get('product_version')).replace('.','')
        else:
            rpm_name = sysobj.args['parameter']['rpm_name']
        ## disable all InfoScale repositories and only enable to be installed repo
        disable_repo = '--disablerepo="*InfoScale*" --enablerepo="' + repository_name + '" '
        installcmd = "groupinstall --setopt=group_command=compat -y"

        if hasattr(sysobj,'upgrade_phase'):
            installcmd = "install -y"

        if check_only:
            installcmd += " --setopt=tsflags=test --setopt=protected_multilib=false"
            cmd = "_cmd_yum " + disable_repo + installcmd + " " + rpm_name
            rtn['rc'], output = sysobj.cmd(cmd)

        elif hasattr(sysobj,'upgrade_phase'):
            for rpm in re.split('\s+', rpm_name):
                if re.search('^\s+$', rpm):
                    continue
                cmd = "_cmd_yum " + disable_repo + installcmd + " " + rpm
                rtn['rc'], output = sysobj.cmd(cmd)
                if rtn['rc'] != 0:
                    rtn['msg'] = 'Failed to install:' + rpm
        else:
            cmd = "_cmd_yum " + disable_repo + installcmd + " " + rpm_name
            rtn['rc'], output = sysobj.cmd(cmd)

        if rtn['rc'] != 0:
            rtn['msg'] = 'Error occurred while installing: ' + rpm_name + "\nError:" + output 
            rtn['changed'] = 0
            rtn['rc'] = 1
        else:
            if re.search('Nothing to do|No packages in any requested group available to install or update', output):
                rtn['msg'] = 'No new rpms to install.'
                rtn['rc'] = 0
                rtn['changed'] = 0
            else:
                rtn['msg'] = 'New rpms have been installed for: ' + rpm_name
                rtn['changed'] = 1
                rtn['rc'] = 0
        if rtn['rc'] == 0 and not check_only:
            # If this is rolling upgrade and create install scripts in phase 2. If full upgrade/install, create install scripts
            if (hasattr(sysobj,'upgrade_phase') and sysobj.args['parameter']['upgrade_phase'] == 'upgrade_nonkernelpkgs') or (not hasattr(sysobj,'upgrade_phase')):
                mrtn = create_install_scripts(sysobj,self.args.get('product_version'))
                # Need to handle exit code here. TODO
        return rtn
    
    def execute_uninstall(self,sysobj,pkgs):

        rtn = {'changed': 0, 'rc': 0, 'msg':''}

        if pkgs:
            #cmd = "_cmd_yum remove -y --noautoremove " + ' '.join(pkgs)
            failed_pkgs = []
            for pkg in pkgs:
                if sysobj.padv_obj.pkg_version(sysobj,pkg):
                    cmd = "_cmd_rpm -e --nodeps " + pkg
                    rc, out = sysobj.cmd(cmd)
                    if rc != 0:
                        rtn['rc'] = 1
                        failed_pkgs.append(pkg)
            if rtn['rc'] == 1:
                rtn['msg'] = 'Failed to uninstall packages: ' + ','.join(failed_pkgs)
            else:
                rtn['msg'] = 'Uninstall has completed.'
        else:
            rtn['rc'] = 2
            rtn['changed'] = 0
            rtn['msg'] = 'Could not fetch the product packages'

        return rtn

    def setup_yumrepo(self,sysobj,repository_name,repository_baseurl,gpgcheck,gpgkey):
        rtn = {'rc':0, 'changed':0, 'msg':''}
        repository_name = self.args.get('repository_name')
        repository_baseurl = self.args.get('repository_baseurl')

        repository_file = "/etc/yum.repos.d/" + repository_name + ".repo"
        gpgcheck = self.args.get('gpgcheck') or 0
        gpgkey = self.args.get('gpgkey') or ''
        cmd = "_cmd_echo '[" + repository_name + "]\nname=" + repository_name + "\nbaseurl=" + \
              repository_baseurl + "\nenabled=1\ngpgcheck=" + str(gpgcheck) + "\ngpgkey=" + \
              gpgkey + "\n' > " + repository_file
        rtn['rc'],output = sysobj.cmd(cmd)
        cmd = "_cmd_yum clean all; _cmd_yum updateinfo"
        sysobj.cmd(cmd)
        rtn['changed'] = 1
        rtn['msg'] = 'Yum Repo created'

        return rtn

    def precheck_install(self,sysobj):
        precheck_obj = Precheck(sysobj.args['parameter'])
        mrtn = precheck_obj.precheck()
        precheck_obj.precheck_compare(sysobj)
        facts = Facter(self.args)
        #facts.compare_facters(sysobj,['padv'])
        sysobj.log.info(str(sysobj.error))
        pass

    def precheck_uninstall(self,sysobj):
        sysobj.log.debug(str(sysobj.args['parameter']))
        precheck_obj = Precheck(sysobj.args['parameter'])
        mrtn = precheck_obj.precheck()
        facts = Facter(self.args)
        configured_comp = sysobj.args['parameter']['host_facters']['component']
        
        if configured_comp != 'None' and configured_comp.lower() != 'sf' and sysobj.args['parameter']['host_facters']['onenode_cluster'] != 1:
            self.keys_to_compare.append('all_nodes')
        facts.compare_facters(sysobj,self.keys_to_compare)
        sysobj.log.info(str(sysobj.error))
        pass


