        # Check onenode cluster or one node in a cluster
        if re.search('ru_phase',sysobj.args['parameter']['module'].lower()):
            # Check if configured component is supported for RU
            if sysobj.args['parameter']['host_facters']['component'].lower() not in self.ru_supported_comps:
                sysobj.error.append({'upgrade_precheck configured component':'Configured component(' + sysobj.args['parameter']['host_facters']['component'].upper() + ') is not supported for Rolling upgrade.'})
                return
            if len(sysobj.args['parameter']['host_facters']['all_nodes']) <= 1 or sysobj.args['parameter']['host_facters']['onenode_cluster'] == 1:
                sysobj.error.append({'upgrade_precheck one node cluster':'One node cluster is not supported for Rolling upgrade.'})
                return 
        product = self.check_upgrade_matrix(sysobj)
        # Check upgrade matrix
        if product == '':
            sysobj.error.append({'upgrade_precheck upgrade path':'Upgrade path from'+ installed_prod + ' cannot be determined. Please check the release guide for more information'})
        else:
            sysobj.args['parameter']['product'] = product

        return self.perform_task(sysobj, 'upgrade_precheck')

