    def detect_ru_phase(self,sysobj):
        sysobj.log.debug('detect_ru_phase of base_prod called:')
        params = sysobj.args['parameter']
        ru_pkgs = []
        padv_obj = Padv_linux()
        llt_vers = padv_obj.pkg_version(sysobj,'VRTSllt')
        vcs_vers = padv_obj.pkg_version(sysobj,'VRTSvcs')
        amf_vers = padv_obj.pkg_version(sysobj,'VRTSamf')

        sysobj.log.debug("Determing RU_phase:" + 'LLT_VERS: ' + llt_vers + ' VCS_VERS:' + vcs_vers + ' AMF_VERS' +amf_vers + ' REL_VERS' + params['product_version'])

        v_l = compvers(vcs_vers,llt_vers,3)
        v_a = compvers(vcs_vers,amf_vers,3)

        sysobj.log.debug('v_l=' + str(v_l) + ' v_a=' + str(v_a))
        if v_l == 0:
            v_l = v_a
            if compvers(llt_vers,params['product_version']) == -1:
                sysobj.log.debug("No ru P1 done yet")
                return 0

        #Check with llt/amf version
        if v_l == -1:
            return 1

        return 2

