    def post_uninstall(self,sysobj):
        configured_component = sysobj.args['parameter']['host_facters']['component']
        if configured_component and configured_component != 'None' and configured_component.lower() != 'sf':
            for file in ['/etc/llttab', '/etc/llthosts', '/etc/gabtab', '/etc/VRTSvcs/conf/config/main.cf']:
                if os.path.exists(file):
                    new_file = file + '.old'
                    if os.path.exists(new_file):
                        sysobj.cmd("_cmd_rmr " + new_file)
                    sysobj.cmd("_cmd_mv " + file + ' ' + file + '.old')

