def get_class(class_name, sysobj):
    my_class = None
    try:
        my_class = eval(class_name)
    except AttributeError:
        sysobj.log.debug(class_name + " doesn't exist")
    finally:
        if(None != my_class):
            sysobj.log.debug(class_name + " found")
        return my_class

#sysobj: to get padv
#class_name:llt
#class_type:Proc
#version: product version, 72/73
def instance_factory(sysobj, class_name, version=None, class_type="proc"):
    #module_str = "infoscale."+class_type+"."+class_name
    #my_module = importlib.import_module(module_str)
    class_name = class_name[0].upper() + class_name[1:]

    plat = sysobj.plat.lower()
    padv = sysobj.padv.lower()
    if(None == version):
        #TODO:set for sysobj
        version = str(sysobj.version)
    #find proc_padv_version
    class_str = class_name + "_" + padv +  "_v" + version.replace('.', '')
    my_class = get_class(class_str, sysobj)
    if(None != my_class):
        return my_class()

    #Special case for release 70/71/72 + Linux
    if 'proc' == class_type:
        if((plat != "linux") or compvers(version, '7.3') != -1):
            #find proc_padv
            class_str = class_name + "_" + padv 
            my_class = get_class(class_str, sysobj)
            if(None != my_class):
                return my_class()
    #find proc_plat
    else:
        class_str = class_name + "_" + plat
        my_class = get_class(class_str, sysobj)
        if(None != my_class):
            return my_class()

    #find proc_plat
    class_str = class_name + "_" + plat
    my_class = get_class(class_str, sysobj)
    if(None != my_class):
        return my_class()

    #find proc_common
    class_str = class_name + "_" + "common"
    my_class = get_class(class_str, sysobj)
    if(None != my_class):
        return my_class()

    sysobj.log.info("Error:Class not found!!" + class_name + "-"+ class_str+' PLAT=PADV:' + plat + '=' + padv)
    exit(1)


