    def execute(self,sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        mrtn = self.check_args(sysobj)
        if mrtn['rc'] != 0:
            return mrtn

        sysobj.upgrade_phase = self.args['upgrade_phase']

