class Upgrade(Base_prod,object):
    def __init__(self, arguments):
        self.args = arguments
        self.args['operation'] = 'upgrade'
        self.args['task'] = 'upgrade'
        self.keys_to_compare = ["product","version","padv","component"]
        self.args['product'] = ''
        self.args['component'] = ''

    def check_args(self,sysobj):
        #check if version 7.4.1 or higher, telemetry server/port must be given in argument
        rtn = {'rc':0,'msg':'','changed':0}
        msg = []
        rc = 0
        if compvers('7.4.1',self.args.get('product_version')) != 1:
            if not self.args.get('edgeserver_hostname'): 
                rc += 1
                msg.append('edgeserver_hostname')
            if not self.args.get('edgeserver_port'):
                msg.append('edgeserver_port')
                rc += 1

        if rc != 0:
            rtn['msg'] = 'Missing arguments for the module ' + self.args.get('module') + ' for product version 7.4.1:' + ','.join(msg)
            rtn['rc'] = rc
            rtn['changed'] = 0

        return rtn

    def execute(self,sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        mrtn = self.check_args(sysobj)
        if mrtn['rc'] != 0:
            return mrtn

        state = self.args.get('state')
        installed_prod = sysobj.args['parameter']['host_facters']['product']
        installed_prod_vers = sysobj.args['parameter']['host_facters']['version']
        version = self.args['product_version'] = normalize_version(self.args['product_version'])
        if compvers(installed_prod_vers, self.args['product_version'],3) != -1:
            msg = sysobj.args['parameter']['host_facters']['veritas'].capitalize() + ' ' + installed_prod.capitalize() + ' ' + installed_prod_vers + ' already installed. No action needed'
            rtn['msg'] = msg
            return rtn

        # do not support upgrade from below 7.0 versions(i.e. 6.2/6.2.1)
        if compvers('7.0',installed_prod_vers) == 1:
            rtn['rc'] = 1
            rtn['msg'] = 'Upgrade from ' + installed_prod_vers + ' is not supported currently.'
            return rtn

        repository_name = self.args.get('repository_name')
        product = self.check_upgrade_matrix(sysobj)
        if product == '':
            msg = 'Upgrade path from'+ installed_prod + ' cannot be determined. Please check the release guide for more information'
            rtn['msg'] = msg
            rtn['rc'] = 1
            return rtn
        self.args['product'] = product
        configured_component = self.args['component'] = sysobj.args['parameter']['host_facters']['component']
        sysobj.operation = self.args['operation']

        sysobj.args['parameter']['task'] = self.args['operation']
        self.args['padv'] = sysobj.padv
        self.args['prod'] = self.args['product']
        self.args['release'] = self.args['product_version']

        sysobj.args['parameter'].update(self.args)
        if configured_component and (configured_component.lower() != 'sf' and configured_component.lower() != 'dmp'): 
            clust_list = self.check_system_list(sysobj)
            if clust_list != 0:
                sysobj.error.append({'system check' : "All the systems must be provided to do full upgrade\nFollowing systems are detected:" + str(clust_syslist)})

            if sysobj.error:
                rtn['msg'] = parse_msg(sysobj.error)
                rtn['rc'] = 1
                sysobj.log.debug(rtn['msg'])
                return rtn

        yum = Yum(self.args)
        if self.args.get('repository_baseurl'):
            repository_name = self.args.get('repository_name')
            repository_baseurl = self.args.get('repository_baseurl')
            gpgcheck = self.args.get('gpgcheck') or 0
            gpgkey = self.args.get('gpgkey') or ''
            mrtn = yum.setup_yumrepo(sysobj,repository_name,repository_baseurl,gpgcheck,gpgkey)
            if mrtn['changed'] == 1:
                rtn['msg'] = rtn['msg'] + "\n" + mrtn['msg'] + "\n"
            if mrtn['rc'] != 0:
                rtn['msg'] = rtn['msg'] + "\n" + mrtn['msg']  + "\n"
                rtn['rc'] = mrtn['rc']
                return rtn

        self.precheck_upgrade(sysobj)
        if sysobj.error:
            rtn['msg'] = parse_msg(sysobj.error)
            rtn['rc'] = 1
            sysobj.log.debug(rtn['msg'])
            return rtn

        product = sysobj.args['parameter']['prod'].lower()
        base_comp = instance_factory(sysobj,configured_component.lower(),'None', 'Prod')
        base_prod = instance_factory(sysobj,product,'None', 'Prod')
        self.args['product_version'] = normalize_version(self.args.get('product_version'))

        self.args['rpm_name'] = rpm_name = product.upper() + self.args['product_version'].replace('.','')
        rpm_exists = 0

        if state == 'present':

            self.args['state'] = "absent"
            proc = Process(self.args)

            proc.stop_precheck(sysobj,configured_component)
            if sysobj.error:
                rtn['msg'] = parse_msg(sysobj.error)
                rtn['rc'] = 1
                sysobj.log.debug(rtn['msg'])
                return rtn

            proc.prestop(sysobj,configured_component)
            
            # Check if any error before stopping the services
            if sysobj.error:
                rtn['msg'] = parse_msg(sysobj.error)
                rtn['rc'] = 1
                sysobj.log.debug(rtn['msg'])
                return rtn

            if base_comp.upgrade_pre_stop(sysobj) == 1:
                rtn['changed'] = 0
                rtn['rc'] = 1
                rtn['msg'] = "upgrade_pre_stop step failed."
                return rtn

            srtn = proc.execute(sysobj);
            if srtn['msg'] != "Stop " + self.args.get('component') + " successfully":
                rtn['msg'] = "Could not stop the processes: " + srtn['msg']
                rtn['changed'] = 1
                rtn['rc'] = 1

                return rtn

            else:
                rtn['msg'] = "Stopped the processes of :" + self.args.get('component') + "\n"


            base_prod.upgrade_pre_uninstall(sysobj)
            if sysobj.error:
                rtn['msg'] = "upgrade_pre_uninstall step failed."
                rtn['rc'] = 1
                rtn['changed'] = 1
                return rtn

            self.args['state'] = "present"
            self.args['product'] = rpm_name

            sysobj.args['parameter'].update(self.args)

            yum = Yum(self.args)

            #Check if any rpm needs to uninstall before upgrade
            if (installed_prod_vers in base_prod.uninstall_before_upgrade.keys()):
                #Uninstall needed rpms before running yum upgrade
                rpms = base_prod.uninstall_before_upgrade[installed_prod_vers]
                for pkg in base_prod.obsoleted_pkgs.keys():
                    if self.args.get('product_version') in base_prod.obsoleted_pkgs[pkg]:
                        rpms.append(pkg)
                rpms2 = []
                for pkg in rpms:
                    if sysobj.padv_obj.pkg_version(sysobj,pkg):
                        rpms2.append(pkg)

                mrtn = yum.execute_uninstall(sysobj,rpms2)
                

            srtn = yum.execute_install(sysobj)

            if srtn['rc'] == 0:
                if srtn['changed'] == 1: 
                    rtn['msg'] = rtn['msg'] + 'New rpms installed for: ' + rpm_name
                elif srtn['changed'] == 0:
                    rtn['msg'] = rtn['msg'] + 'NO new rpms to be installed. It seems product already on desired version' 
                    #return rtn
            else:
                rtn['msg'] = rtn['msg'] + 'Error occurred while installing rpms: ' + rpm_name
                rtn['changed'] = 1
                rtn['rc'] = srtn['rc'] + 1
                return rtn
            component_path = get_component_path()
            sysobj.cmd("_cmd_echo 'configured_component = " + self.args['component'].upper() + (self.args.get('product_version')).replace('.','') + "' > " + component_path)

            base_prod.upgrade_post_install(sysobj)
            if sysobj.error:
                rtn['msg'] = "upgrade_post_install step failed."
                rtn['rc'] = 1
                rtn['changed'] = 1
                return rtn

            licdict = {'product_version':self.args.get('product_version'),'license':self.args.get('license')}
            lic = Licensing(licdict)
            lrtn = lic.execute(sysobj)
            if lrtn['rc'] == 0:
                rtn['msg'] = rtn['msg'] + "\nLicense applied successfully"
                rtn['changed'] = 2
            else:
                rtn['msg'] = rtn['msg'] + "\nLicensing failed with error:\n" + lrtn['msg']
                rtn['changed'] = 2
                rtn['rc'] =2

            self.args['product'] = product

            #procdict = {'state':'present','product':self.args.get('product'),'product_version':self.args.get('product_version'),'component':self.args.get('component'),'seednode':self.args.get('seednode'), 'state':'present'}
            #proc = Process(procdict)
            sysobj.log.info('COMPONENT=' + self.args.get('component'))
            proc = Process(self.args) 
            sysobj.args['parameter']['host_facters']['version'] = self.args['product_version']
            srtn = proc.execute(sysobj);
            if srtn['msg'] != "Start " + self.args.get('component') + " successfully":
                rtn['msg'] = rtn['msg'] + "\nCould not start the processes of :\n" + srtn['msg']
                rtn['changed'] = 3
                rtn['rc'] = 3
                return rtn
            else:
                rtn['msg'] = rtn['msg'] + "\n" + srtn['msg']
                rtn['changed'] = 3

        elif state == 'absent':
            rtn['msg'] = "Only state present is allowed in case of Upgrade. Nothing to do"
            rtn['rc'] = 0
            rtn['changed'] = 0

        sysobj.log.debug('upgrade_post_start called' + self.args.get('seednode'))
        base_comp.upgrade_post_start(sysobj,self.args.get('seednode'))
        return rtn

    def precheck_upgrade(self,sysobj):
        precheck_obj = Precheck(sysobj.args['parameter'])
        mrtn = precheck_obj.precheck()
        precheck_obj.precheck_compare(sysobj)
        facts = Facter(self.args)

        if sysobj.args['parameter']['host_facters']['product'] != sysobj.args['parameter']['product']:
            if compvers(sysobj.args['parameter']['host_facters']['version'],sysobj.args['parameter']['product_version']) == 1:
                sysobj.error.append({'Upgrade Matrix check failed': sysobj.args['parameter']['host_facters']['product'].upper() +sysobj.args['parameter']['host_facters']['version'] +' is already installed. A direct upgrade from ' + sysobj.args['parameter']['host_facters']['product'].upper() + sysobj.args['parameter']['product_version'] + ' to ' + sysobj.args['parameter']['product'].upper() + ' is not supported'})

        facts.compare_facters(sysobj,self.keys_to_compare)
        sysobj.args['parameter']['task'] = 'install'
        yum = Yum(self.args)
        rtn = yum.execute_install(sysobj, check_only=True)
        if rtn['rc'] != 0:
            sysobj.error.append({'Yum upgrade simulation': "Dry run return code:  {0}".format(rtn['rc'])})

        sysobj.log.info(str(sysobj.error))


