        rtn = {'changed': 0, 'rc': 0, 'msg' : ''}
        sysobj.log.info('Performing LLT config')
        attr = self.args
        attr['llt_hosts'] = attr['systems']
        if (not attr):
            return rtn
        self.check_args(sysobj)
        for key in attr.keys():
            if (key in ["private_link", "llt_hosts", "low_priority_link"]):
                if type(attr[key]) == str:
                    value = re.split(r',', attr[key])
                    attr[key] = value
            #sysobj.log.error("ATTR=" + str(attr["private_link"]))
        llt_proc = instance_factory(sysobj, 'llt')
        state = self.args.get('state') or "present"
        if (state == "present"):
            if ((not attr['vcs_clusterid']) or (not attr['llt_hosts'])):
                return rtn
            hostname = sysobj.hostname

            if not attr.get('node_ids'):
                attr.setdefault('node_ids', {})
                n = 0
                for host in attr['llt_hosts']:
                    attr['node_ids'][host] = str(n)
                    n += 1
            llthosts_list = [] 
            for host in attr['llt_hosts'] :
                one_llthost = {}
                one_llthost['node_id'] = str(attr['node_ids'][host])
                one_llthost['hostname'] = host
                llthosts_list.append(one_llthost)
            sort_key = lambda key: int(key['node_id']) 
            llthosts_list = sorted(llthosts_list, key = sort_key)
            llthosts = ""
            for one_llthost in llthosts_list:
                llthosts += one_llthost['node_id'] + " " + one_llthost['hostname'] +"\n"
            llthosts = llthosts.rstrip('\n')
            (rc, result) = sysobj.cmd("_cmd_echo '" + llthosts + "' > /etc/llthosts 2>/dev/null")
            if rc:
                rtn['rc'] = Llt.VERITAS_LLT_WRITE_CONF_FAIL
                rtn['msg'] = Llt.rtnMsgCode[Llt.VERITAS_LLT_WRITE_CONF_FAIL]
                sysobj.log.error(rtn['msg'] + "rtn: " + str(rc))
                return rtn
            else:
                rtn['changed'] = 1


            llttab = "set-node " + hostname + "\n" + "set-cluster " + str(attr['vcs_clusterid']) + "\n"
            if (attr['method'] == 'ethernet'):
                if (not attr.get('private_link') or (1 == len(attr.get('private_link')) and not attr.get('low_priority_link'))):
                    return rtn
                for nic in attr['private_link']:
                    (rc, output) = self.check_nic_is_availaible(sysobj, nic)
                    if rc :
                        rtn['rc'] = Llt.VERITAS_LLT_NIC_NOT_EXIST
                        rtn['msg'] = Llt.rtnMsgCode[Llt.VERITAS_LLT_NIC_NOT_EXIST]
                        sysobj.log.error("Nic " + nic + " not exists on " + hostname)
                        return rtn
                    llttab += self.convert_nic2lltlink_sys(sysobj, nic, False)
                if attr.get('low_priority_link'):
                    for nic in attr['low_priority_link']:
                        (rc, output) = self.check_nic_is_availaible(sysobj, nic)
                        if rc:
                            rtn['rc'] = Llt.VERITAS_LLT_NIC_NOT_EXIST
                            rtn['msg'] = Llt.rtnMsgCode[Llt.VERITAS_LLT_NIC_NOT_EXIST]
                            sysobj.log.error("Nic " + nic + " not exists on " + hostname)
                            return rtn
                        llttab += self.convert_nic2lltlink_sys(sysobj, nic, True)
                llttab = llttab.rstrip('\n')
            elif attr['method'] in ('udp', 'tcp'):
                self.split_ip_netmask()
                string = self.get_llttab(sysobj, attr)
                if string :
                    llttab += string
                else:
                    rtn['rc'] = Llt.VERITAS_LLT_UDP_IP_NOT_EXIST
                    rtn['msg'] = Llt.rtnMsgCode[Llt.VERITAS_LLT_UDP_IP_NOT_EXIST]
                    sysobj.log.error(rtn['msg'] + " on " + hostname)
                    return rtn
            #elif (attr['method'] == 'rdma') :
                #pass


            (rc, result) = sysobj.cmd("_cmd_echo '" + llttab + "' > /etc/llttab 2>/dev/null;")
            if rc:
                rtn['rc'] = Llt.VERITAS_LLT_WRITE_CONF_FAIL
                rtn['msg'] = Llt.rtnMsgCode[Llt.VERITAS_LLT_WRITE_CONF_FAIL]
                sysobj.log.error(rtn['msg'] + "rtn: " + str(rc))
            else:
                rtn['changed'] = 1
            llt_proc.enable_proc(sysobj)

        elif (attr['state'] == 'absent'):
            (rc, result) = sysobj.cmd("_cmd_rmr /etc/llt* 2>/dev/null")
            if (0 == rc):
                rtn['changed'] = 1
            else:
                rtn['rc'] = Llt.VERITAS_LLT_DELET_FILE_FAIL
                sysobj.log.error(Llt.rtnMsgCode[Llt.VERITAS_LLT_DELET_FILE_FAIL] + " rc:" + str(rc))
            llt_proc.disable_proc(sysobj)
            
        return rtn


