    def __is_vlan_nic_sys__(self, sysobj, nic):
        #TMB#return Path(r"/proc/net/vlan/" + nic).exists()
        return os.path.exists(r"/proc/net/vlan/" + nic)
    
    def __is_bonded_nic_sys__(self, sysobj, nic):
       (bnics,undef) = self.bondednics_sys(sysobj)
       return nic in bnics

    def bondednics_sys(self, sysobj):
        slavenics = []
        bondednics = []
        if (sysobj.plat == "Linux"):
            file_pattern = r"/proc/net/bonding/"
            (rt, files) = sysobj.cmd("_cmd_ls " + file_pattern + r" 2>/dev/null")
            if (files != ""):
                for file1 in re.split('\n', files):
                    (rt, slave) = sysobj.cmd("_cmd_grep 'Slave Interface'" + file_pattern + file1 + " 2>/dev/null | _cmd_awk '{print $3}'" )
                    snics = re.split('\n', slave)
                    slavenics.extend(snics)
                    bondednics.extend(file1)
        return (bondednics, slavenics)

    #rc 0 ok
    def convert_nic2lltlink_sys(self, sysobj, nic, b_lowpri):
        if (b_lowpri):
            lowpri = "-lowpri"
        else:
            lowpri = ''
        if ((self.__is_vlan_nic_sys__(sysobj, nic)) or self.__is_bonded_nic_sys__(sysobj, nic)):
            return "link" + lowpri + " " + nic + " " + nic + " - ether - -\n"
        (rc, output) = self.check_nic_is_availaible(sysobj, nic)
        if rc :
            return ""
        ifc = re.split('\s+', output)
        if ("link/ether" in ifc):
            idx = ifc.index("link/ether")
        else:
            return ""
        return "link" + lowpri + " " + nic + " eth-" + ifc[idx + 1] + " - ether - -\n"

