class Odm:

    ODM_CONF_SUCCESS = 600
    ODM_CONF_FAIL = 601
    ODM_ERROR_ARGS = 603
    rtnMsgCode = {
        ODM_CONF_SUCCESS : 'Veritas odm configure successfully',
        ODM_CONF_FAIL : 'Veritas odm configure fail',
        ODM_ERROR_ARGS : 'Veritas odm arguments is not right: '
    }

    def __init__(self, arguments):
        self.args = arguments
        #default value for arguments state
        if 'state' not in self.args:
            self.args['state'] = 'present'

    def check_args(self):
        statelist = ('present', 'absent')
        componentlist = ('dmp', 'vcs', 'sf', 'sfha', 'sfcfs', 'sfcfsha', 'sfrac')
        invalid = []
        if not ('state' in self.args and self.args['state'] in statelist):
            invalid.append('state')
        if not ('component' in self.args and self.args['component'].lower() in componentlist):
            invalid.append('component')

        return sorted(invalid)

    def execute(self, sysobj):
        rtn = {'changed': 1, 'rc': 0, 'msg': ''}
        sysobj.log.info('Performing ODM config')
        odm_proc = instance_factory(sysobj, 'vxodm')

        if not self.args.get('state'):
            self.args['state'] = 'present'
        component = sysobj.args['parameter'].get('component')
        if self.args['state'] == 'present':
            #SFSYBASECE and VCS won't start ODM
            #If reconfigure to VCS, but with SF started, odm should be enabled.
            if component and (component == 'vcs' and os.path.exists('/etc/vx/reconfig.d/state.d/install-db') or component == 'sfsybasece'):
                odm_proc.disable_proc(sysobj) if odm_proc.is_enabled(sysobj) else ''
            else:
                odm_proc.enable_proc(sysobj) if not odm_proc.is_enabled(sysobj) else ''

        elif self.args['state'] == 'absent':
            if component and re.search('vcs|sfsybasece', component) and not odm_proc.is_enabled(sysobj):
                return rtn
            odm_proc.disable_proc(sysobj)
        return rtn


