class Sfha_config:
    SFHA_SUCCESS = 0
    SFHA_FAIL = 1
    rtnMsgCode = { SFHA_SUCCESS: 'SFHA configuration completed successfully',
                   SFHA_FAIL: 'Failed to configure SFHA'}

    def __init__(self, arguments):
        self.args = arguments

    def check_args(self):
        vcs_config = Vcs_config(self.args)
        return vcs_config.check_args()

    def execute(self,sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}

        # check if correct prod is installed and whether component can be configured
        prod = sysobj.args['parameter']['host_facters']['product']
        prod_vers = sysobj.args['parameter']['host_facters']['version']
        comp = self.args['component']
        if prod != "" and comp.lower() in get_configurable_components(prod.lower()):
            sysobj.log.info('Correct product found for configuration')
        else:
            prod_msg = ''
            if prod == "":
                prod_msg = 'no product installed.'
            else:
                prod_msg = 'product ' + prod + ' installed.'
            rtn['msg'] = comp + ' cannot be configured with ' + prod_msg
            rtn['rc'] = 1
            return rtn

        if compvers(prod_vers,normalize_version(self.args['product_version'])) != 0:
            rtn['msg'] = 'Provided product version(' + self.args['product_version'] + ') is not same as installed version(' + prod_vers + ').'
            rtn['rc'] = 1
            return rtn

        invalid_args = self.check_args()
        if invalid_args:
            rtn['msg'] = 'Invalid/Missing arguments for config:' + str(invalid_args)
            rtn['rc'] = 1
            return rtn

        sysobj.log.info('Performing SFHA config')
        if not self.args.get('state'):
            return rtn
        vcs_config = Vcs_config(self.args)
        component_path = get_component_path()
        configured_comp = sysobj.args['parameter']['host_facters']['component']
        if configured_comp and configured_comp.lower() != 'none':
            rtn['msg'] = 'Already ' + configured_comp + ' is configured. '
            if configured_comp.lower() == sysobj.args['parameter']['component'].lower():
                rtn['rc'] = 0
            else:
                rtn['rc'] = 1
                rtn['msg'] += 'Reconfiguration is not supported.'
            return rtn
        if self.args['state'] == 'present':
            rtn = vcs_config.execute(sysobj)
        elif self.args['state'] == 'absent':
            rtn = vcs_config.execute(sysobj)
            sysobj.cmd("_cmd_rmr " + component_path)

        sf_config = Sf_config(self.args)
        rtn1 = sf_config.execute(sysobj)

        if rtn['rc'] or rtn1['rc']:
            sysobj.log.error("Failed to configure SFHA")
        else:
            if 'license' in self.args.keys() and self.args['license'] != None:
                # Call licensing with provided license
                lic_obj = Licensing(self.args)
                lic_obj.execute(sysobj)
            else:
                # Call licensing with keyless
                self.args['license'] = self.args['product'].lower()
                lic_obj = Licensing(self.args)
                lic_obj.execute(sysobj)

            # Start services
            proc_obj = Process(self.args)
            proc_rtn = proc_obj.execute(sysobj)
            if proc_rtn['rc'] != 0:
                rtn['rc'] = Sfha_config.SFHA_FAIL
                rtn['msg'] = Sfha_config.rtnMsgCode[rtn['rc']]
                sysobj.log.info(rtn['msg'])
            else:
                rtn['rc'] = Sfha_config.SFHA_SUCCESS
                rtn['msg'] = Sfha_config.rtnMsgCode[rtn['rc']]
                sysobj.log.info(rtn['msg'])
                rtn['changed'] = 1
            sysobj.cmd("_cmd_echo 'configured_component = " + self.args['component'].upper() + (self.args.get('product_version')).replace('.','') + "' > " + component_path)
        return rtn

