class Licensing:

    def __init__(self, arguments):
        self.args = arguments
        self.lic_dat = '/etc/vx/licenses/dat/licenses.dat'
    
    def execute(self, sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        if 'access' == self.args.get('product', ''):
            return self.register_veritas_access_license(sysobj)
        #if not give the 'license' in paramter use default license
        #default license is enterprice license
        license = ''
        if self.args.get('license'):
            license = self.args.get('license')
        else:
            license = sysobj.args['parameter']['host_facters']['product'].upper()

        prod_list = get_prod_list(self.args.get("product_version"))
        
        b_keykess = False
        if not os.path.isfile(license):
            for prod in prod_list:
                if re.search(prod, license.lower()):
                    b_keykess = True
                    break
        if b_keykess:
            cmd = '_cmd_vxkeyless display'
            rc, output = sysobj.cmd(cmd)
            if re.search( license.lower(), output.lower()) and not re.search('^7.4',self.args.get("product_version")):
                rtn.update({'changed': 0, 'rc': rc, 'msg': 'License already present.'})
            else:
                if compvers('7.4',self.args.get("product_version")) != 1:
                    keylesshash = {}
                    filecontent = readfile(self.lic_dat)
                    lines = filecontent.split('\n')
                    for line in lines:
                        m = re.search('^(\w+).*\s+(\S+)$', line)
                        if m:
                            keylesshash.update({m.group(1):'/etc/vx/licenses/dat/' + m.group(2)})
                    license = keylesshash[license.upper()]
                    rc,output = sysobj.cmd('_cmd_vxlicinstupgrade -k ' + license)
                    if rc == 0 or rc == 9:
                        rc = 0

                else:
                    rc, output = sysobj.cmd('_cmd_vxkeyless set -q ' + license.upper())

                if rc ==0:
                    rtn.update({'changed': 1, 'rc': rc})
                else:
                    rtn.update({'changed':0 , 'rc': rc, 'msg': 'Register keyless key failed.'})
        else:
            permanent_key_cmd = 'vxlicinstupgrade'
            if re.search('^6', self.args.get("product_version")):
                permanent_key_cmd = 'vxlicinst'

            rc, output = sysobj.cmd('_cmd_' + permanent_key_cmd  + ' -k ' + license)

            #license register successfully
            if rc == 0 or rc == 9:
            #and re.search('successfully', output):
                for line in output.split('\n'):
                    tmp_match = re.search('License key successfully (.+)', line)
                    if tmp_match:
                        rtn['msg'] = rtn['msg'] + tmp_match.group(0)
                rtn.update({'changed': 1, 'rc': rc})
                sysobj.log.info("in License Successfully")
            #license register Warning like duplicate key
            elif re.search('WARNING', output):
                for line in output.split('\n'):
                    tmp_match = re.search(permanent_key_cmd + ' WARNING(.+)', line)
                    if tmp_match:
                        rtn['msg'] = rtn['msg'] + tmp_match.group(0)
                rtn.update({'changed': 0, 'rc': rc})
                sysobj.log.info("in License WARNING")
            #license register Error like Error key
            elif re.search('ERROR', output):
                for line in output.split('\n'):
                    tmp_match = re.search(permanent_key_cmd + ' ERROR (.+)', line)
                    if tmp_match:
                        rtn['msg'] = rtn['msg'] + tmp_match.group(0)
                rtn.update({'rc': rc, 'changed': 0})
            #unknow error like do not install register license pkg
            else :
                rtn.update({'changed': 0, 'rc': rc, 'msg': output})

        return rtn


    def register_veritas_access_license(self,sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        license = self.args.get('license')
        if 'enterprise' == license.lower():
            default_license = 'ENTERPRISE'
            license = self.args['license'] if 'license' in self.args else default_license
            sysobj.log.info("Register Veritas Access license : "  + license.upper())
            rc, output = sysobj.cmd("_cmd_vxkeyless set -q " + license.upper())
            if rc == 0:
                sysobj.cmd("/opt/VRTSslic/bin/vxlicinstupgrade -k /opt/VRTSslic/key/TRAILWARE.slf")
                sysobj.cmd("/opt/VRTSnas/pysnas/system/PyLicenseChecks.py enable_disable_services 2>/dev/null")
                rtn.update({'changed': 1, 'rc': 0, 'msg': 'Register keyless key successfully.'})
            else:
                rtn.update({'changed': 0, 'rc': 1, 'msg': 'Register keyless key failed.'})
        else:
            if os.path.isfile(license):
                sysobj.cmd("/opt/VRTSvlic/bin/vxlicinstupgrade -k AJZE-2AT6-RG3Y-DG4H-998F-GM77-F7OZ-NWP3-6 2>/dev/null")
                rc,output = sysobj.cmd("/opt/VRTSslic/bin/vxlicinstupgrade -k " + license + " 2>/dev/null")
                if rc == 0:
                    sysobj.log.info("successfully set perpetual or subscription key")
                    sysobj.cmd("/opt/VRTSnas/pysnas/system/PyLicenseChecks.py enable_disable_services 2>/dev/null")
                    rtn.update({'changed': 1, 'rc': 0,'msg': 'Register non-keyless key successfully.'})
                else:
                    rtn.update({'changed': 0, 'rc': 1, 'msg': 'Register non-keyless key failed.'})

        return rtn

