class Process:
    STOP_SUCCESS = 2
    STOP_FAIL = 1
    START_SUCCESS = 2
    START_FAIL = 1

    def __init__(self, arguments):
        self.args = arguments
        if not self.args.get("procs"):
            self.args['product'] = self.args['product'].lower()
            self.prod_list = get_prod_list(self.args.get("product_version"))
            if self.args['product'] in self.prod_list:
                self.args['prod-key'] = 'infoscale'
            else:
                self.args['prod-key'] = 'access'

    def execute(self,sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        if self.args.get("procs"):
            if self.args['state'] == 'present':
                operation = 'Start'
                self.args['switch'] = 'start'
            else:
                operation = 'Stop'
                self.args['switch'] = 'stop'
            procs = self.args.get("procs")
            rtn = self.operate_proc(sysobj, self.args['switch'], procs)
            return rtn

        configured_component = ''
        if re.search('_config',self.args.get('module')) :
            missing = check_edgserver_entry(self)
            if missing:
                rtn['msg'] = 'Missing arguments for the module ' + self.args.get('module') + ' for product version 7.4.1 or higher:' + ','.join(missing)
                rtn['rc'] = 1
                return rtn

        operation = ''
        changed = 0
        if not self.args.get("procs"):
            if self.args['product'].lower() == 'access':
                self.args['component'] = 'sfcfsha'
            component = self.args['component']

        seednode = self.args.get('seednode') or self.args['systems'][0]
        if re.search('config',self.args['module'].lower()):
            configured_component = self.args['component']
        else:
            configured_component = sysobj.args['parameter']['host_facters']['component']
        if 'task' in self.args.keys() and self.args['task'] == 'upgrade':
            self.args['component'] = configured_component

        # This check is for general start process. configured component should be same as give in playbook
        if configured_component and self.args['component'].lower() != configured_component.lower():
            rtn['msg'] = 'Provided component in playbook(' + self.args.get('component') + ') is different from configured component(' + configured_component + ').Start/Stop task will not proceed'
            rtn['rc'] = 1
            rtn['changed'] = 0
            return rtn

        version = sysobj.args['parameter']['host_facters']['version']
        if compvers(normalize_version(self.args['product_version']), version) != 0 and self.args['module'].lower() == 'process':
            rtn['msg'] = 'Provided version in playbook(' + self.args.get('product_version') + ') is different from installed version(' + version  + ').Start/Stop task will not proceed'
            rtn['rc'] = 1
            rtn['changed'] = 0
            return rtn

        if self.args['state'] == 'present':
            operation = 'Start'
            self.args['switch'] = 'start'
            if self.args.get("procs"):
                procs = self.args.get("procs")
                rtn = self.operate_proc(sysobj, self.args['switch'], procs)
                return rtn

            self.pre_start(sysobj,configured_component,seednode)

            # Check if any error before proceeding 
            if sysobj.error:
                rtn['msg'] = parse_msg(sysobj.error)
                rtn['rc'] = 1
                sysobj.log.debug(rtn['msg'])
                return rtn

            procs = get_procs(sysobj, self.args['prod-key'], configured_component, self.args['switch'])
            if self.args['product'].lower() == 'foundation' or configured_component.lower() == 'sf':
                procs = self.adjust_foundation_procs(procs)
            rtn = self.operate_proc(sysobj, self.args['switch'], procs)

            rc = self.poststart(sysobj,configured_component,seednode)
            rtn['rc'] = rtn['rc'] or rc
            component = configured_component.upper()
            if rtn['rc']:
                sysobj.log.error(component + ' startup failed')
                rtn['rc'] = Process.START_FAIL
            else:
                sysobj.log.info(component + ' startup completed successfully')
                rtn['rc'] =  Process.START_SUCCESS
            changed = 1
            time.sleep(5)

        elif self.args['state'] == 'absent':
            operation = 'Stop'
            self.args['switch'] = 'stop'
            if self.args.get("procs"):
                procs = self.args.get("procs")
                rtn = self.operate_proc(sysobj, self.args['switch'], procs)
                return rtn
            params = sysobj.args['parameter']
            if params['module'].lower() in ('process'):
                # Run stop_precheck and prestop whole product
                self.stop_precheck(sysobj,configured_component)
                self.prestop(sysobj, configured_component)

                # Check if any error before stopping the services
                if sysobj.error:
                    rtn['msg'] = parse_msg(sysobj.error)
                    rtn['rc'] = 1
                    sysobj.log.debug(rtn['msg'])
                    return rtn

            if self.args['module'].lower() == 'yum':
                b_prod = sysobj.args['parameter']['host_facters']['product']
                obj = instance_factory(sysobj,b_prod.capitalize(),'None', 'Prod') 
                configured_component = obj.max_component
            procs = get_procs(sysobj, self.args['prod-key'], configured_component, self.args['switch'])
            rtn = self.operate_proc(sysobj, self.args['switch'], procs)

            component = configured_component.upper()
            if rtn['rc']:
                sysobj.log.error('Stopping ' + component + ' failed')
                rtn['rc'] = Process.STOP_FAIL
            else:
                sysobj.log.info('Stopping ' + component + ' completed successfully')
                rtn['rc'] =  Process.STOP_SUCCESS
            changed = 1

        if changed == 1:
            rtn['changed'] = 1
        if rtn['rc'] == 1:
            rtn['msg'] = operation + ' ' + self.args['component'] + ' not successfully\n' + rtn['msg']
        else:
            rtn['msg'] = operation + ' ' + self.args['component'] + ' successfully'
            rtn['rc'] = 0

        return rtn

    def operate_proc(self, sysobj, action, procs):
        # action must be start or stop
        rtn = {'rc': 0, 'changed': 1, 'msg': ''}
        summary = []
        failed_procs = []
        for proc in procs:
            # call function proc_start_sys or proc_stop_sys
            result = getattr(self, 'proc_' + action +'_sys')(sysobj, proc)
            summary.append(result)
            if result == 0:
                failed_procs.append(proc)
        if 0 in summary:
            rtn['rc'] = 1
            rtn['msg'] = 'Process ' + action + ' failed: ' + ' '.join(failed_procs)

        return rtn

    def proc_start_sys(self, sysobj, proc, version=None):
        # get proc instance
        proc = instance_factory(sysobj, proc, version)
        # if the proc is in abort list, skipt to start this proc
        sysobj.abort_procs = sysobj.abort_procs if hasattr(sysobj, 'abort_procs') else []
        if proc.proc in sysobj.abort_procs:
            sysobj.log.info('## Aborted to start ' + proc.proc)
            return 0
        sysobj.log.info('## Starting ' + proc.proc + ' on ' + sysobj.hostname)
        # initial check
        sysobj.log.info('## Begin pre_start checking for ' + proc.proc)
        running = proc.check_proc(sysobj, 'start')
        sysobj.log.info("## The pre_start checking return '" + str(running) + "'")
        if running:
            sysobj.log.info('## ' + proc.proc + ' is already running')
            rtn = 1
        else:
            # start the process
            sysobj.log.info('## Begin starting ' + proc.proc)
            rtn = proc.start_proc(sysobj)
            sysobj.log.info("## The start_proc() return '" + str(rtn) + "'")
            if rtn:
                # confirm startup, may retry check for a defined period
                sysobj.log.info('## Begin poststart checking for ' + proc.proc)
                running = proc.check_proc(sysobj, 'start')
                sysobj.log.info("## The poststart checking return '" + str(running) + "'")
                if not running:
                    sleep = 0
                    while sleep < proc.start_period:
                        sleep += 10
                        time.sleep(10)
                        sysobj.log.info('## Sleep 10, and do poststart checking again')
                        running = proc.check_proc(sysobj, 'start')
                        sysobj.log.info("## The poststart checking return '" + str(running) + "'")
                        if running: break
                if running:
                    sysobj.log.info('## ' + proc.proc + '  is successfully started')
                    rtn = 1
                    if hasattr(proc,'post_start'):
                        proc.post_start(sysobj)
                else:
                    sysobj.log.info('## ' + proc.proc + '  is not successfully started')
                    rtn = 0
            else:
                sysobj.log.info('## The start_proc() directly failed')
                rtn = 0
            if rtn == 0:
                # Start failed.
                # To show debug informations in start_failed()
                proc.start_failed(sysobj)
                # set processes which depend on this proc
                if hasattr(proc, 'abort_procs'):
                    sysobj.abort_procs.extend(proc.abort_procs)

        sysobj.log.info("## The proc_start_sys() return '" + str(rtn) + "'")
        return rtn

    def proc_stop_sys(self, sysobj, proc, version=None):
        # get proc instance
        proc = instance_factory(sysobj, proc, version)
        sysobj.log.info('## Stopping ' + proc.proc + ' on ' + sysobj.hostname)
        # initial check
        sysobj.log.info('## Begin prestop checking for ' + proc.proc)
        running = proc.check_proc(sysobj, 'stop')
        sysobj.log.info("## The prestop checking return '" + str(running) + "'")
        if running:
            sysobj.log.info('## ' + proc.proc + ' is running')
            sysobj.log.info('## Begin stopping ' + proc.proc)
            rtn = proc.stop_proc(sysobj)
            sysobj.log.info("## The stop_proc() return '" + str(rtn) + "'")
            if rtn:
                # confirm shutdown, may retry check for a defined period
                sysobj.log.info('## Begin poststop checking for ' + proc.proc)
                running = proc.check_proc(sysobj, 'stop')
                sysobj.log.info("## The poststop checking return '" + str(running) + "'")
                if running:
                    sleep = 0
                    while sleep < int(proc.stop_period):
                        sleep += 10
                        time.sleep(10)
                        sysobj.log.info('## Sleep 10, and do poststop checking again')
                        if hasattr(proc, 'retry_stop'):
                            sysobj.log.info('## The previous stop failed, retry stopping ' + proc.proc)
                            proc.stop_proc(sysobj)
                        running = proc.check_proc(sysobj, 'stop')
                        sysobj.log.info("## The poststop checking return '" + str(running) + "'")
                        if not running: break
                    if running and callable(getattr(proc, 'force_stop_proc', None)):
                        sysobj.log.info('## Begin force stop ' + proc.proc)
                        proc.force_stop_proc(sysobj)
                        running = proc.check_proc(sysobj, 'stop')
                        sysobj.log.info("## The poststop checking after force stop return '" + str(running) + "'")
                if running:
                    sysobj.log.info('## ' + proc.proc + '  is not successfully stopped')
                    # To show debug informations in stop_failed()
                    proc.stop_failed(sysobj)
                    rtn = 0
                else:
                    sysobj.log.info('## ' + proc.proc + '  is successfully stopped')
                    rtn = 1
            else:
                sysobj.log.info('## The stop_proc() directly failed')
                rtn = 0
        else:
            sysobj.log.info('## ' + proc.proc + ' is not running')
            rtn = 1

        sysobj.log.info(proc.proc + ": The proc_stop_sys() return '" + str(rtn) + "'")
        return rtn

    def pre_start(self,sysobj,component,seednode):
        #prod = self.args.get('product')
        #prodobj = instance_factory(sysobj,component.capitalize(),'None', 'Prod')
        if component != None:
            prodobj = instance_factory(sysobj,component.capitalize(),'None', 'Prod')
            prodobj.pre_start(sysobj)
        else:
            sysobj.error.append({'pre_start':"Component is not defined for start"})
        return

    def poststart(self,sysobj,component,seednode):
        hostname = sysobj.hostname

        # start sfcfs agents on seednode
        if not re.search('sfcfs', component, re.IGNORECASE):
            return

        count = 0
        while count < 6 and hostname != seednode:
            rc,output = sysobj.cmd("_cmd_hares -state cvm_clus -sys " + hostname)
            if output == 'ONLINE':
                break
            time.sleep(10)
            count += 1

        rc = 0
        if hostname == seednode:
            rc,output = sysobj.cmd("_cmd_cfscluster config -t 200 -s")
            if rc != 0:
                sysobj.error.append({'SFCFSHA poststart failed': output + '\n You may need to run cfscluster config again.' })
        elif count >= 6:
            sysobj.error.append({'SFCFSHA poststart failed': 'cvm_clus not online yet'})
            rc = 1

        # Copy CVMTypes.cf and CFSTypes.cf to /etc/VRTSvcs/conf for Dynamic Upgrade.
        if os.path.isfile("/etc/VRTSvcs/conf/config/CVMTypes.cf") and not os.path.isfile("/etc/VRTSvcs/conf/CVMTypes.cf"):
            sysobj.cmd("_cmd_cp -p /etc/VRTSvcs/conf/config/CVMTypes.cf /etc/VRTSvcs/conf/CVMTypes.cf")
        if os.path.isfile("/etc/VRTSvcs/conf/config/CFSTypes.cf") and not os.path.isfile("/etc/VRTSvcs/conf/CFSTypes.cf"):
            sysobj.cmd("_cmd_cp -p /etc/VRTSvcs/conf/config/CFSTypes.cf /etc/VRTSvcs/conf/CFSTypes.cf")

        return rc

    def stop_precheck(self,sysobj,component):

        prodobj = None
        # If no component is configured, no need to stop the services
        if component != None:
            # Check if previous configured component, then stop that first
            pre_component = sysobj.args['parameter']['host_facters']['component']
            if pre_component != None:
                prodobj = instance_factory(sysobj,pre_component.capitalize(),'None', 'Prod')
            else:
                prodobj = instance_factory(sysobj,component.capitalize(),'None', 'Prod')

            prodobj.stop_precheck(sysobj)
        #    prodobj.pre_stop(sysobj)
        return

    def prestop(self,sysobj,component):
        prod = self.args.get('product')
        prodobj = instance_factory(sysobj,prod.capitalize(),'None', 'Prod')
        # If no component is configured, no need to stop the services
        if component != "None":
            prodobj.pre_stop(sysobj)
        return

    def prestop_old(self,sysobj,component):
        rc = 0
        # stop sfcfs agents
        if re.search('sfcfs', component, re.IGNORECASE):
            rc, port_v = sysobj.cmd("_cmd_gabconfig -a 2>/dev/null | _cmd_grep -c 'Port v gen' ")    #confirm port_v is output
            rc, port_h = sysobj.cmd("_cmd_gabconfig -a 2>/dev/null | _cmd_grep -c 'Port h gen' ")
            if int(port_v) >= 1:
                if int(port_h) == 0:
                    sysobj.cmd("_cmd_cfscluster start")
                    rc = 1
            sysobj.cmd("_cmd_cfscluster stop -f")
            sysobj.cmd("_cmd_fsclustadm cfsdeinit")
        if re.search('vcs|sfha|sfcfs|sfrac',component,re.IGNORECASE):
            sysobj.cmd("_cmd_hastop -local")
        return rc

