VER = 72

class Vxfen:
    VXFEN_CONF_SUCCESS = 300
    VXFEN_UNCONF_FAIL = 301
    VXFEN_DISABLE_FAIL = 303
    VXFEN_DISK_MODE_FAIL = 305
    VXFEN_NO_VXFEND = 307
    VXFEN_NO_ENABLE_FILE = 309
    VXFEN_VXVM_NOT_RUN = 311
    VXFEN_INIT_DISK_FAIL = 313
    VXFEN_CREATE_DG_FAIL = 315
    VXFEN_STOP_VCS_FAIL = 317
    VXFEN_STOP_VXFEN_FAIL = 319
    VXFEN_START_VXFEN_FAIL = 321
    VXFEN_START_VCS_FAIL = 323
    VXFEN_ARGS_ERROR = 335
    VXFEN_PRIVATE_KEY_ERROR = 337 
    VXFEN_CRS_FILE_ERROR = 339 
    VXFEN_CERT_FILE_ERROR = 341 
    VXFEN_REGISTER_NODE_FAIL = 343
    VXFEN_ADD_CPS_CLUSTER_FAIL = 347
    VXFEN_MAJORITY_MODE_FAIL = 351
    VXFEN_SYCUP_FAIL = 353
    VXFEN_OPENSSL_ERROR = 355

    rtnMsgCode = {VXFEN_CONF_SUCCESS : 'Veritas vxfen configure successfully',\
            VXFEN_UNCONF_FAIL : 'Veritas vxfen unconfigure vxfen fail',\
            VXFEN_DISABLE_FAIL : 'Veritas vxfen_disable_fail',\
            VXFEN_DISK_MODE_FAIL : 'Veritas vxfen disk mode fail',\
            VXFEN_NO_VXFEND : 'Veritas vxfen no vxfend',\
            VXFEN_NO_ENABLE_FILE : 'Veritas vxfen no enable file',\
            VXFEN_VXVM_NOT_RUN : 'Veritas vxvm not run',\
            VXFEN_INIT_DISK_FAIL : 'Veritas vxfen init disk fail',\
            VXFEN_CREATE_DG_FAIL : 'Veritas vxfen create dg fail',\
            VXFEN_STOP_VCS_FAIL : 'Veritas vxfen stop vcs fail',\
            VXFEN_STOP_VXFEN_FAIL : 'Veritas vxfen stop vxfen fail',\
            VXFEN_START_VXFEN_FAIL : 'Veritas vxfen start vxfen fail',\
            VXFEN_START_VCS_FAIL : 'Veritas vxfen start vcs fail',\
            VXFEN_ARGS_ERROR : 'Veritas vxfen arguments is not right:',
            VXFEN_PRIVATE_KEY_ERROR: 'Unable to create private key on CP Server',
            VXFEN_CRS_FILE_ERROR: 'Unable to create csr file on CP Server',
            VXFEN_CERT_FILE_ERROR: 'Unable to create certificate on CP Server',
            VXFEN_REGISTER_NODE_FAIL: 'Register client node failed',
            VXFEN_ADD_CPS_CLUSTER_FAIL: 'Add cluster on CPS failed',
            VXFEN_MAJORITY_MODE_FAIL: 'Veritas configure majority mode fail',
            VXFEN_SYCUP_FAIL: 'Time sync up failed',
            VXFEN_OPENSSL_ERROR: 'openssl is not installed'}

    def __init__(self, arguments):
        self.args = arguments
        #default value for arguments state/vxfenmode
        if 'state' not in self.args:
            self.args['state'] = 'present'
        if 'vxfenmode' not in self.args:
            self.args['vxfenmode'] = 'disabled'
        # set default value for cps mode fencing
        self.args['default_https_port'] = '443'
        if self.args.get('cps'):
            self.args['https_server_keys_dir'] = '/var/VRTScps/security/keys/'
            self.args['https_server_certs_dir'] = '/var/VRTScps/security/certs/'
            self.args['https_client_security_dir'] = '/var/VRTSvxfen/security'
            self.args['https_client_keys_dir'] = '/var/VRTSvxfen/security/keys/'
            self.args['https_client_certs_dir'] = '/var/VRTSvxfen/security/certs/'
            # set for cps based fencing, the time difference between CPS and client
            self.args['max_async_secs'] = 120
            # set cps dict
            self.args['cps'] = self.set_cps()
        if self.args.get('non_scsi3'):
            self.args['loser_exit_delay'] = 55
        if self.args.get('disks') and not self.args.get('disk_policy'):
            self.args['disk_policy'] = 'dmp'
        self.args['no_coord_disks'] = 1 if not self.args.get('disks') else 0
        vxfenmode = self.args.get('vxfenmode') 
        if (vxfenmode == 'disks' or vxfenmode == 'cps' or vxfenmode == 'sybase') and self.args.get('cpagent') == None:
            self.args['cpagent'] = '1'

    def check_args(self):
        statelist = ('present', 'absent')
        fenmodelist = ('disabled', 'disks', 'cps', 'majority', 'sybase')
        invalid = []
        if 'state' in self.args and self.args['state'] in statelist:
            state = self.args.get('state')
        else:
            invalid.append('state')
            return invalid
        if state == 'present':
            vxfenmode = self.args.get('vxfenmode')
            if vxfenmode not in fenmodelist:
                invalid.append('vxfenmode')
                return invalid
            if (vxfenmode == 'disks'):
                #disks vxfen mode must have disks&vxfendg arguments
                [invalid.append(arg) for arg in ['disks', 'vxfendg'] if not self.args.get(arg)]
            elif (vxfenmode == 'cps'):
                cps = self.args.get('cps')
                if cps and cps.get('cps') and (len(cps['cps'])%2 == 0 or any([ip_is_ipv4(x) is False for x in cps['cps']])):
                    invalid.append('cps')
            if self.args.get('cpagent') == 1 and not self.args.get('systems'):
                    invalid.append('systems')
            if (self.args.get('disks')):
                if not self.args.get('vxfendg'):
                    invalid.append('vxfendg')
                if not self.args.get('seednode') and not self.args.get('systems'):
                    invalid.append('seednode')

        return sorted(invalid)

    def prerequisite(self, sysobj):
        rtn = {'rc': 0, 'changed': 0, 'msg': ''}
        vxfenmode = self.args['vxfenmode']
        # check whether openssl is installed except configure fencing in disabled mode majority based fencing
        if vxfenmode != 'disabled' and vxfenmode != 'majority' and not self.check_openssl(sysobj):
            rtn['rc'] = Vxfen.VXFEN_OPENSSL_ERROR
            rtn['msg'] = self.rtnMsgCode[rtn['rc']]
            return rtn
        
        # Check if VM is available only if at least one disk is being used
        if not self.args.get('is_addnode') or False == self.args.get('is_addnode'):
            rc,output = sysobj.cmd('_cmd_vxdctl mode')
            if self.args.get('disks') and (rc or not re.search('enabled', output)):
                rtn['rc'] = Vxfen.VXFEN_VXVM_NOT_RUN
                rtn['msg'] = self.rtnMsgCode[rtn['rc']]
                sysobj.log.error('Make sure Volume Manager is running.')
        return rtn

    def check_openssl(self, sysobj):
        rc = self.redirect_openssl_command(sysobj)
        if not rc: return 0
        rc, output = sysobj.cmd('_cmd_openssl version 2>/dev/null')
        if rc: return 0
        return 1

    def execute(self, sysobj):
        rtn  = {'changed': 0, 'rc': 0, 'msg': ''}
            
        state = self.args.get('state')
        sysobj.log.info('Performing VXFEN config')
        vxfenmode = self.args.get('vxfenmode')
        # get process object 
        self.had = instance_factory(sysobj, 'had')
        self.vxfen_proc = instance_factory(sysobj, 'vxfen')
        if state == 'present':
            # precheck for fencing configuration
            rtn = self.prerequisite(sysobj)
            if rtn['rc'] % 2: return rtn

            rc = self.vxfen_proc.enable_proc(sysobj)
            if not rc:
                return rtn
            if vxfenmode == 'disabled':
                rtn = self.cmp_rtn(rtn, self.config_disabled_fencing(sysobj))
            elif vxfenmode == 'disks':
                rtn = self.cmp_rtn(rtn, self.config_disk_fencing(sysobj))
            elif vxfenmode == 'majority':
                rtn = self.cmp_rtn(rtn, self.config_majority_fencing(sysobj))
            elif vxfenmode == 'cps':
                rtn = self.cmp_rtn(rtn, self.config_cps_fencing(sysobj))
            if rtn['rc'] % 2: return rtn

        elif state == 'absent':
            modefile = '/etc/vxfenmode'
            vxenviron = '/etc/vxenviron'
            vxfendg = '/etc/vxfendg'
            maincf = '/etc/VRTSvcs/conf/config/main.cf'
            vxfen_conf = ''
            if os.path.isfile(modefile):
                vxfen_conf = self.get_vxfen_config(sysobj)
                tmpcmd = '_cmd_rmr ' +  modefile
                sysobj.cmd(tmpcmd)
                
            rc, output = sysobj.cmd('_cmd_grep UseFence ' + maincf + ' 2>/dev/null')
            if output:
                #if absent state should comment UseFence in main.cf
                rc, output = sysobj.cmd("_cmd_sed -i 's#UseFence#//UseFence#g' " + maincf)
                if rc ==0: rtn['changed'] = 1 
            rc = self.had.check_proc(sysobj, 'start')
            if rtn['changed'] and rc:
                rtn = self.cmp_rtn(rtn, self.stop_vcsfen(sysobj))

            # if 'cps' defined in playbook,
            # or if there's cps info in vxfenmode file
            # clean up client info on the cps
            cps_conf = self.args.get('cps') or vxfen_conf
            if cps_conf and cps_conf.get('cps'):
                for server in cps_conf['cps']:
                    self.cleanup_from_cps(sysobj, server, cps_conf)
            # remove /etc/vxfendg file
            if os.path.isfile(vxfendg):
                sysobj.cmd('_cmd_rmr ' + vxfendg)
            # remove file for non-scsi3 
            if os.path.isfile(vxenviron):
                sysobj.cmd('_cmd_rmr ' + vxenviron)
            # change VXFEN_START/STOP to 0
            self.vxfen_proc.disable_proc(sysobj)

        else:
            rtn['rc'] = Vxfen.VXFEN_ARGS_ERROR
            rtn['msg'] = self.rtnMsgCode[Vxfen.VXFEN_ARGS_ERROR] + ' state: ' + state
            sysobj.log.error(rtn['msg'])
            return rtn

        if rtn['rc']%2 == 0:
            rtn['rc'] = Vxfen.VXFEN_CONF_SUCCESS
        else: rtn['rc'] = Vxfen.VXFEN_UNCONF_FAIL
        rtn['msg'] = self.rtnMsgCode[rtn['rc']]

        return rtn
    #TODO:common code needed?
    def cmp_rtn (self, rtn1, rtn2):
        rtn = rtn1
        rtn['changed'] = rtn['changed'] or rtn2['changed']
        if rtn['rc'] == 0 and rtn2['rc']:
            rtn['rc'] = rtn2['rc']
            rtn['msg'] = rtn2['msg']
        return rtn

    def config_disabled_fencing(self, sysobj):
        rtn  = {'changed': 0, 'rc': 0, 'msg': ''}
        vxfen = self.parse_vxfen_state(sysobj)
        vxfen_conf = self.get_vxfen_config(sysobj)
        rtn = self.cmp_rtn(rtn, self.update_files(sysobj))
        rc, output = sysobj.cmd('_cmd_grep UseFence /etc/VRTSvcs/conf/config/main.cf 2> /dev/null')
        if output:
            (rc, output) = sysobj.cmd("_cmd_sed -i 's#UseFence#//UseFence#g' /etc/VRTSvcs/conf/config/main.cf")
            if rc == 0:
                rtn['changed'] = 1
        rtn = self.cmp_rtn(rtn, self.stop_vcsfen(sysobj))
        if rtn['rc']%2:
            sysobj.log.error('Failed to configure fencing in disabled mode"') 
            rtn['rc'] = Vxfen.VXFEN_DISABLE_FAIL
            rtn['msg'] = self.rtnMsgCode[rtn['rc']]

        # if 'cps' defined in playbook,
        # or if there's cps info in vxfenmode file
        # clean up client info on the cps
        cps_conf = vxfen_conf or self.args.get('cps') 
        if cps_conf and cps_conf.get('cps'):
            for server in cps_conf['cps']:
                self.cleanup_from_cps(sysobj, server, cps_conf)

        return rtn

    def config_disk_fencing(self, sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        vxfen = self.parse_vxfen_state(sysobj)
        vxfen_conf = self.get_vxfen_config(sysobj)
        disk_policy = ''
        if 'disk_policy' in vxfen:
            disk_policy = vxfen['disk_policy']
        rtn = self.cmp_rtn(rtn, self.config_fence_dg(sysobj))
        rtn = self.cmp_rtn(rtn, self.update_files(sysobj))
        if self.usefence(sysobj):
            rtn['changed'] = 1
        if rtn['changed'] or not disk_policy:
            rtn = self.cmp_rtn(rtn, self.stop_vcsfen(sysobj))
        if rtn['rc']%2:
            sysobj.log.error('Failed to configure disk based fencing.')
            rtn['rc'] = Vxfen.VXFEN_DISK_MODE_FAIL

        # if 'cps' defined in playbook,
        # or if there's cps info in vxfenmode file
        # clean up client info on the cps
        cps_conf = vxfen_conf or self.args.get('cps') 
        if cps_conf and cps_conf.get('cps'):
            for server in cps_conf['cps']:
                self.cleanup_from_cps(sysobj, server, cps_conf)

        return rtn

    def config_majority_fencing(self, sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        rtn = self.cmp_rtn(rtn, self.update_files(sysobj))
        if self.usefence(sysobj):
            rtn['changed'] = 1
        if rtn['changed']:
            rtn = self.cmp_rtn(rtn, self.stop_vcsfen(sysobj))
        if rtn['rc'] % 2:
            sysobj.log.error('Failed to configure disk majority fencing.')
            rtn['rc'] = Vxfen.VXFEN_MAJORITY_MODE_FAIL
            rtn['msg'] = Vxfen.rtnMsgCode[rtn['rc']]
        return rtn

    def parse_vxfen_state(self, sysobj):
        vxfen={}
        (rc, output) = sysobj.cmd('_cmd_vxfenadm -d')
        if rc:
            vxfen['status'] = 'vxfen is not started'
            return vxfen
        vxfen['protocol_version'] = re.search('(?<=Fencing Protocol Version: )\w+', output).group(0)
        vxfen['fencing_mode'] = re.search('(?<=Fencing Mode: )\w+', output).group(0)
        tmp_match = re.search('(?<=Disk Policy: )\w+', output)
        if tmp_match:
            vxfen['disk_policy'] = tmp_match.group(0)
        tmp_match = re.search('(?<=Fencing Mechanism: )\w+', output)
        if tmp_match:
            vxfen['mechanism'] = tmp_match.group(0)
        return vxfen

    def update_files(self, sysobj):
        rtn = {'changed' : 0, 'rc': 0, 'msg' : ''}
        # copy /etc/vxfen.d/vxfenmode to /etc/vxfenmode
        if not os.path.exists('/etc/vxfen.d'):
            sysobj.log.error('Cannot find derectory /etc/vxfen.d/.')
            rtn['rc'] = Vxfen.VXFEN_NO_VXFEND
            return rtn
        vxfenmode = self.args.get('vxfenmode')
        vxfenmode_file = '/etc/vxfenmode'
        suffix = 'scsi3_dmp' if vxfenmode == 'disks' else vxfenmode
        if os.path.exists(vxfenmode_file):
            sysobj.cmd('_cmd_rm '+vxfenmode_file+'.prev 2>/dev/null')
            sysobj.cmd('_cmd_cp '+vxfenmode_file+' '+vxfenmode_file+'.prev 2>/dev/null')
            sysobj.cmd('_cmd_cp /etc/vxfen.d/vxfenmode_'+suffix+' '+vxfenmode_file+' 2>/dev/null')
            if not filecmp.cmp(vxfenmode_file, vxfenmode_file+'.prev'):
                rtn['changed'] = 1
        else :
            rc, output = sysobj.cmd('_cmd_cp /etc/vxfen.d/vxfenmode_'+suffix+' '+vxfenmode_file)
            if rc == 0: rtn['changed'] = 1

        fendg_file = '/etc/vxfendg'
        vxfendg = self.args.get('vxfendg')
        if vxfenmode == 'disabled':
            if os.path.exists(fendg_file):
                rc, output = sysobj.cmd('_cmd_mv ' + fendg_file + ' ' + fendg_file + '.prev')
        elif vxfenmode == 'disks':
            if os.path.exists(fendg_file):
                rc,output = sysobj.cmd('_cmd_grep -w ' + vxfendg + ' ' + fendg_file)
                if not output:
                    sysobj.cmd('_cmd_rmr ' + fendg_file+'.prev; _cmd_cp ' + fendg_file + ' ' + fendg_file+'.prev')
                    rc, output  = sysobj.cmd('_cmd_echo ' + vxfendg + ' > ' + fendg_file)
                    if not rc:
                        rtn['changed'] = 1
            else:
                rc,output = sysobj.cmd('_cmd_echo ' + vxfendg + ' > ' + fendg_file)
                if not rc:
                    rtn['changed'] = 1
        elif vxfenmode == 'cps':
            string = self.get_cps_vxfenmode_str()      
            with open(vxfenmode_file, 'w') as fh:
                fh.write(string)
            #if self.args.get('non_scsi3'):
#                self.non_scsi3_updatefiles()

        return rtn

    def get_cps_vxfenmode_str(self):
        cps = self.args.get('cps')
        string = ''
        string = 'vxfen_mode=customized\nvxfen_mechanism=cps\n'
        if len(cps['cps']) == 1: string += 'single_cp=1\n'
        index = 1
        for server in cps['cps']:
            tmpstr = ''
            for vip in cps['vips'][server]:
                port = cps['cpport'][vip]
                tmpstr += '['+vip+']:'+port+',' if port != self.args['default_https_port'] else '['+vip+'],'
            string += 'cps'+str(index)+'='+tmpstr+'\n'
            index += 1
        if self.args.get('disks'):
            string += 'vxfendg='+self.args['vxfendg']+'\nscsi3_disk_policy='+self.args['disk_policy']+'\n'
        # add parameters in vxfenmode for non-scsi3 fencing
        elif self.args.get('non_scsi3'):
            string += 'loser_exit_delay='+str(self.args['loser_exit_delay'])+'\n'
            
        return string

    def config_fence_dg(self, sysobj):
        rtn = {'changed' :0, 'rc' : 0, 'msg' : ''}
        if self.args.get('is_addnode') and True == self.args.get('is_addnode'):
            return rtn
        rc,output = sysobj.cmd('_cmd_vxdctl mode')
        if re.search(r'enabled', output) == None:
            sysobj.log.error('Make sure Volume Manager is running.')
            rtn['rc'] = Vxfen.VXFEN_VXVM_NOT_RUN
            return rtn
        if 'vxfendg' not in self.args:
            sysobj.log.error('Not define vxfendg')
            return rtn
        dgname = self.args.get('vxfendg')
        disks = self.args.get('disks')
        if not disks:
            return rtn
        systems = self.args.get('systems')
        seednode = self.args.get('seednode')
        if not seednode:
            if not systems:
                return rtn
            seednode = systems[0]
        rc, sysname = sysobj.cmd('_cmd_cat /etc/VRTSvcs/conf/sysname')
        if sysname == seednode:
            rc, output = sysobj.cmd('_cmd_vxdisk -o alldgs list | _cmd_grep -w ' + dgname)
            if output:
                sysobj.log.info('Disk group already exists')
                return rtn
            else:
                for disk in disks:
                    rc, output=sysobj.cmd('_cmd_vxdisk -o alldgs list | _cmd_grep '+disk +' 2>/dev/null | _cmd_grep -v LVM | grep -v cdsdisk')
                    if output:
                        rc, output = sysobj.cmd('_cmd_vxdisksetup -i ' + disk)
                        if rc:
                            sysobj.log.error('Initializing VxVM disk failed.')
                            rtn['rc'] = Vxfen.VXFEN_INIT_DISK_FAIL
                            return rtn
                        else:
                            rtn['changed'] = 1
                tmpcmd = '_cmd_vxdg init ' + dgname + ' ' + ' '.join(disks)
                rc, output = sysobj.cmd(tmpcmd)
                if rc:
                    sysobj.log.error('Disk group '+dgname+ ' failed to be created')
                    rtn['rc'] = Vxfen.VXFEN_CREATE_DG_FAIL
                    return rtn
                tmpcmd = '_cmd_vxdg deport ' + dgname + '; '
                tmpcmd = tmpcmd + '_cmd_vxdg -t import ' + dgname + '; '
                tmpcmd = tmpcmd + '_cmd_vxdg -g ' + dgname + ' set coordinator=on; '
                tmpcmd = tmpcmd + '_cmd_vxdg deport ' + dgname
                sysobj.cmd(tmpcmd + ' 2>/dev/null')
                rtn['changed'] = 1
        return rtn

    def usefence(self, sysobj):
        change = 0
        maincf_dir = '/etc/VRTSvcs/conf/config'
        rc,output = sysobj.cmd('_cmd_grep UseFence '+ maincf_dir + '/main.cf 2> /dev/null')
        if not output:
            sysobj.cmd('_cmd_cp '+maincf_dir+'/main.cf '+maincf_dir+'/main.cf.bak')
            lines = []
            with open(maincf_dir+'/main.cf', 'r+') as myfile:
                lines = myfile.readlines()
                n = 0
                for line in lines:
                    if re.search(r'^cluster', line):
                        lines[n] = line + '\tUseFence=SCSI3\n'
                        break
                    n += 1
            if len(lines) > 0:
                with open(maincf_dir + '/main.cf', 'w+') as myfile:
                    myfile.writelines(lines)
            rc,output = sysobj.cmd('_cmd_grep UseFence '+maincf_dir+'/main.cf 2> /dev/null')
            if output:
                change = 1
        return change
    
    # TODO: need to call proc to stop vcs/fencing ?
    def stop_vcsfen(self, sysobj):
        rtn = {'changed' : 0, 'rc' : 0, 'msg' : ''}
        if self.had.check_proc(sysobj, 'stop'):
            sysobj.cmd('_cmd_hastop -local')
            sysobj.cmd('_cmd_hasys -wait localsys SysState EXITED -time 600')
            if self.had.check_proc(sysobj, 'stop'):
                sysobj.log.error('Stopping VCS failed.')
                rtn['rc'] = Vxfen.VXFEN_STOP_VCS_FAIL
                rtn['msg'] = Vxfen.rtnMsgCode[Vxfen.VXFEN_STOP_VCS_FAIL]
                return rtn
            else:
                rtn['changed'] = 1
        time.sleep(5)
        (rc,output) = sysobj.cmd('_cmd_lsmod | _cmd_grep vxfen')
        if output:
            #sysobj.cmd('_cmd_vxfen stop')
            self.vxfen_proc.stop_proc(sysobj)
            (rc,output) = sysobj.cmd('_cmd_lsmod | _cmd_grep vxfen')
            if output:
                sysobj.log.error('Stopping fencing failed')
                rtn['rc'] = Vxfen.VXFEN_STOP_VXFEN_FAIL
                rtn['msg'] = Vxfen.rtnMsgCode[Vxfen.VXFEN_STOP_VXFEN_FAIL]
            else:
                rtn['changed'] = 1
        return rtn

    def set_cps(self):
        # analyze cps in playbook to a dict
        cps = {}
        if not self.args.get('cps'): return cps
        for item in self.args['cps']:
            flag = 1
            item = re.split(',|\s', item)
            for system in item:
                vip, port = system.split(':')
                cps.setdefault('cpport',{})[vip] = port if port else self.args['default_https_port']
                if flag:
                    cps.setdefault('cps',[]).append(vip)
                cps.setdefault('vips',{}).setdefault(vip,[]).append(vip)
                flag = 0
        if cps: self.args['cps'] = cps
        return cps

    def config_cps_fencing(self, sysobj):
        rtn= {'rc':0, 'changed':0, 'msg':''}
        cps = self.args.get('cps')
        if self.args.get('disks'):
            rc = self.config_fence_dg(sysobj)
            if rc['rc']%2:
                return rc

        # setup https certifications
        if not cps: return rtn
        rc = self.https_config_client_fresh(sysobj, cps)
        if rc['rc']%2:
            return rc
        rtn = self.cmp_rtn(rtn, rc)

        # update cluster info on CPS
        rc = self.update_clusterinfo_on_cps(sysobj, cps)
        if rc['rc']%2:
            return rc
        rtn = self.cmp_rtn(rtn, rc)

        # if non scsi3 fencing
        # move default DiskGroup attribute Reservation in types.cf from ClusterDefault to NONE
        # for each resource of the type DiskGroup
        # --1. set the value of the MonitorReservation attribute to 0
        # --2. set the value of the Reservation attribute to NONE
        # jdh debug add node begin
        if self.args.get('non_scsi3'):
            self.non_scsi3_updateresources(sysobj)
        rc = self.stop_vcsfen(sysobj)
        if rc['rc']%2:
            return rc
        rtn = self.cmp_rtn(rtn, rc)
        # jdh debug add node end
        
        # CPS Clean Up
        # scenario 1: migrate from CPS to other CPS.
        # scenario 2: migrate from non-scsi3 to CPS.
        # clean up old info in cps database if vxfen is already running in cps mode
        oldvxfenconf = self.get_vxfen_config(sysobj)
        if oldvxfenconf.get('cps'):
            self.remove_cps(sysobj, oldvxfenconf)
        # nonscsi3 config file clean up
        #if self.args.get('non_scsi3'):
        #    self.update_vxfen_files_without_nonscsi3()

        # Apply fencing configuration
        # Generate /etc/vxfenmode file with these details in each of the CP client cluster nodes
        self.update_files(sysobj)

        # Update UseFence in main.cf
        self.usefence(sysobj)

        return rtn

    def https_config_client_fresh(self, sysobj, cps):
        rtn = {'rc':0, 'changed':0, 'msg':''}
        # sync up the time between the target node and all cp servers
        ntp_server = self.args.get('ntp_server')
        if not ntp_server:
            sysobj.log.warning('NTP server is not defined, configuring CPS based fencing may failed if the time is not synchronous on CPS and client cluster.')
        else:
            islocal = 0
            systems = []
            for server in cps['cps']:
                cpssys = SysCommon(server, islocal)
                systems.append(cpssys)
            systems.append(sysobj)
            rc, output = check_and_sysc_time(systems, ntp_server, self.args['max_async_secs'])
            if rc:
                rtn['rc'] = Vxfen.VXFEN_SYCUP_FAIL
                rtn['msg'] = Vxfen.rtnMsgCode[rtn['rc']]
                return rtn
        rtn = self.https_setup_trust(sysobj, cps)
        return rtn

    def https_setup_trust(self, sysobj, cps):
        rtn = {'rc':0, 'changed':0, 'msg':''}
        self.redirect_openssl_command(sysobj)
        conf = get_clus_uuid(sysobj)
        uuid = conf.get('uuid')
        #com = self.args['communication'] if self.args.get('communication') or 'ssh'
        # Setup HTTPS trust between server and the system
        #
        # Only the following files are needed
        #   1. /var/VRTSvxfen/security/keys/client_private.key
        #   2. /var/VRTSvxfen/security/certs/ca_<xxxxx>.crt
        #   3. /var/VRTSvxfen/security/certs/client_<xxxxx>.crt
        
        server_keys_dir = self.args['https_server_keys_dir']
        server_certs_dir = self.args['https_server_certs_dir']
        server_ca_key_file = server_keys_dir + 'ca.key'
        server_ca_crt_file = server_certs_dir + 'ca.crt'
        client_keys_dir = self.args['https_client_keys_dir'] 
        client_certs_dir = self.args['https_client_certs_dir']
        # check if in the live upgrade
        rootpath = self.args.get('rootpath') or ''
        client_private_key_file = rootpath + client_keys_dir + 'client_private.key'
        sys_private_key_file = client_keys_dir + 'client_private_' + sysobj.hostname + '_' + uuid + '.key'
        sys_csr_file = client_certs_dir + 'client_' + sysobj.hostname + '_' + uuid + '.csr'
        sys_crt_file = client_certs_dir + 'client_' + sysobj.hostname + '_' + uuid + '.crt'

        # The client_private.key should be created only one copy for different CP servers.
        # $sys->{server_private_key_ready}=1, means client_private_<uuid>.key is ready in server side
        # $sys->{client_private_key_ready}=1, means client_private.key is ready in client side
        islocal = 0
        for server in cps['cps']:
            cpssys = SysCommon(server, islocal)
            rc, output = cpssys.cmd('_cmd_ls '+sys_private_key_file+' 2> /dev/null')
            if rc:
                cpssys.cmd('_cmd_mkdir -p '+client_keys_dir)
                if os.path.exists(client_private_key_file):
                    # Copy the client private key to the server for later usage to create client certificate on the server
                    sysobj.transfer_file(cpssys, client_private_key_file, sys_private_key_file)
                    rtn['changed'] = 1
                else:
                    rc, output = cpssys.cmd('_cmd_openssl genrsa -out '+sys_private_key_file+' 2048')
                    if rc:
                        rtn['rc'] = Vxfen.VXFEN_PRIVATE_KEY_ERROR
                        rtn['msg'] = Vxfen.rtnMsgCode[rtn['rc']]
                        sysobj.log.error('Unable to create private key '+sys_private_key_file+' on CPS '+server)
                        return rtn
                    sysobj.cmd('_cmd_mkdir -p '+ rootpath + client_keys_dir)
                    cpssys.transfer_file(sysobj, sys_private_key_file, client_private_key_file)
                    rtn['changed'] = 1
            else:
                sysobj.cmd('_cmd_mkdir -p '+ rootpath + client_keys_dir)
                cpssys.transfer_file(sysobj, sys_private_key_file, client_private_key_file)
                rtn['changed'] = 1

            cpssys.cmd('_cmd_mkdir -p '+client_certs_dir)
            sysobj.cmd('_cmd_mkdir -p '+rootpath+client_certs_dir)
            rc, output = cpssys.cmd('_cmd_openssl req -new -sha256 -key '+sys_private_key_file+ \
                                    ' -subj \'/C=IN/L=Pune/OU=VCS/CN='+uuid+'\' -out '+sys_csr_file)
            if rc:
                rtn['rc'] = Vxfen.VXFEN_CRS_FILE_ERROR
                rtn['msg'] = Vxfen.rtnMsgCode[rtn['rc']]
                sysobj.log.error('Unable to create csr file '+sys_csr_file+' on CPS '+server)
                return rtn
            
            rc, output = cpssys.cmd('_cmd_openssl x509 -req -days 3650 -sha256 -in '+sys_csr_file+' -CA '+\
                                    server_ca_crt_file+' -CAkey '+server_ca_key_file+' -set_serial 01 -out '\
                                    +sys_crt_file)
            if rc:
                rtn['rc'] = Vxfen.VXFEN_CERT_FILE_ERROR
                rtn['msg'] = Vxfen.rtnMsgCode[rtn['rc']]
                sysobj.log.error('Unable to create certificate '+sys_crt_file+' on CPS '+server)
                return rtn

            cpssys.transfer_file(sysobj, server_ca_crt_file, rootpath+client_certs_dir+'/ca_'+server+'.crt')
            cpssys.transfer_file(sysobj, sys_crt_file, rootpath+client_certs_dir+'/client_'+server+'.crt')

            # Remove temp files, and chmod for /var/VRTSvxfen/security directory
            cpssys.cmd('_cmd_rmr '+sys_csr_file+';_cmd_chmod -R 700 '+self.args['https_client_security_dir'])
            sysobj.cmd('_cmd_chmod -R 700 '+rootpath+self.args['https_client_security_dir'])
            rtn['changed'] = 1

        return rtn

    #MOVE to product common
    def redirect_openssl_command(self, sysobj):
        rootpath = self.args.get('rootpath') or ''
        openssl = rootpath+'/opt/VRTSperl/non-perl-libs/bin/openssl'
        if not os.path.exists(openssl):
            sysobj.log.error(openssl+' is not installed on the system.')
            return 0
        if not os.access(openssl, os.X_OK):
            sysobj.cmd('_cmd_chmod +x '+openssl)
        sysobj.command['openssl'] = openssl

        return 1

    def update_clusterinfo_on_cps(self, sysobj, cps):
        rtn = {'rc':0, 'changed':0, 'msg':''}
        conf = get_clus_uuid(sysobj)
        clusname = conf['clusname']
        uuid = conf['uuid']
        sysname = conf['sysname']
        islocal = 0
        rc, node_id = sysobj.cmd('_cmd_lltstat -N 2>/dev/null')        
        if rc:
            with open('/etc/llthosts', 'r') as fh:
                llthosts = fh.read()
                match = re.search('(\d+)\s+'+sysname,llthosts) 
                if match: node_id = match.group(1)
        os.environ['CPS_NODEID'] = node_id

        for server in cps['cps']:
            port = cps['cpport'][server]
            cpssys = SysCommon(server, islocal)
            sysobj.log.info('Updating client cluster information on Coordination Point Server '+server)
            # Check if client cluster is already registered on the cps
            rc, output = sysobj.cmd('_cmd_cpsadm -s '+server+' -a list_nodes -p '+port+' -u '+uuid+' 2>/dev/null | _cmd_grep -w '+clusname)
            if rc or not output:
                systems = self.args.get('systems')
                if (sysobj.hostname == systems[0]):
                    sysobj.log.info('Adding the client cluster to the Coordination Point Server '+server)
                    # Add cluster
                    rc, output = cpssys.cmd('_cmd_cpsadm -s '+server+' -a add_clus -p '+port+' -c '+clusname+' -u '+uuid)
                    if not rc:
                    # Add cluster successfully
                        rtn['changed'] = 1
                    elif re.search('already exists\.', output):
                        sysobj.log.info('Cluster with UUID ('+uuid+') already exists.')
                    rc, output = cpssys.cmd('_cmd_cpsadm -s '+server+' -a list_nodes -p '+port+' -u '+uuid+' 2>/dev/null | _cmd_grep -w '+clusname)
                    if rc:
                        rtn['rc'] = Vxfen.VXFEN_ADD_CPS_CLUSTER_FAIL
                        rtn['msg'] = Vxfen.rtnMsgCode[rtn['rc']]
                        sysobj.log.error('Add cluster ('+clusname+') operation failed.')
                        return rtn
                else:
                    if (rc or not output):
                        # sleep some time to wait for add_clus command on seednode take effect.
                        time.sleep(6)
                        rc, output = sysobj.cmd('_cmd_cpsadm -s '+server+' -a list_nodes -p '+port+' -u '+uuid+' 2>/dev/null | _cmd_grep -w '+clusname)
            else:
                sysobj.log.info('Cluster with UUID ('+uuid+') already exists.')
        
            # register the client node to CPS

            rc, output = sysobj.cmd('_cmd_cpsadm -s '+server+' -a list_nodes -p '+port+' -u '+uuid+' 2>/dev/null | _cmd_grep '+clusname+'.*'+sysname)
            match = re.search(sysname+'\((\d+)', output)
            flag = 0
            if not rc and output and match.group(1) != node_id:
                # unreg and rm_node
                sysobj.cmd('_cmd_cpsadm -s '+server+' -a unreg_node -p '+port+' -u '+uuid+' -n'+match.group(1))
                sysobj.cmd('_cmd_cpsadm -s '+server+' -a rm_node -p '+port+' -c '+clusname+' -u '+uuid+' -h '+sysname+' -n'+match.group(1))
                flag = 1
            if rc or not output or flag:
                sysobj.log.info('Registering client node '+sysname+' with Coordination Point Server '+server)
                sysobj.cmd('_cmd_cpsadm -s '+server+' -a add_node -p '+port+' -c '+clusname+' -u '+uuid+' -h '+sysname+' -n'+node_id)
                rc, output = sysobj.cmd('_cmd_cpsadm -s '+server+' -a list_nodes -p '+port+' -u '+uuid+' 2>/dev/null | _cmd_grep '+clusname+'.*'+sysname)
                if rc and not output:
                    rc, output = sysobj.cmd('_cmd_cpsadm -s '+server+' -a list_nodes -p '+port+' -u '+uuid+' 2>/dev/null | _cmd_grep -w '+clusname)
                    if not rc and output and not self.args.get('nocleanup'):
                        # Clean Up: register client node failed, remove cluster
                        for item in cps['cps']:
                            tmpsys = SysCommon(item, islocal)

                            tmpsys.cmd('_cmd_cpsadm -s '+server+' -a rm_clus -p '+port+' -c '+clusname+' -u '+uuid)
                    rtn['rc'] = Vxfen.VXFEN_REGISTER_NODE_FAIL
                    rtn['msg'] = Vxfen.rtnMsgCode[rtn['rc']]
                    sysobj.log.error('Register client node failed.')
                    return rtn
                else:
                    # register node successfully
                    rtn['changed'] = 1
            else:
                sysobj.log.info('The client node '+sysname+' is already registered with Coordination Point Server '+server)
        return rtn        

    def non_scsi3_updateresources(self, sysobj):
        if self.had.check_proc(sysobj, 'start'):
            # vcs is running
            # make rw
            haconf_makerw(sysobj)
            # set default DiskGroup attribute Reservation in types.cf to NONE
            rc, output = sysobj.cmd('_cmd_haattr -default DiskGroup Reservation NONE')
            if rc:
                sysobj.log.error('Cannot modify the default Reservation attribute of DiskGroup to NONE. Manually set default Reservation attribute to NONE for DiskGroup resource.')
            rc, dg_res = sysobj.cmd('_cmd_hares -list Type=DiskGroup')
            if not rc and dg_res:
                dg_res = dg_res.split('\n')
                for dgres in dg_res:
                    dgres = re.sub('\s+.*', '', dgres)
                    if not dgres: continue

                    rc, output = sysobj.cmd('_cmd_hares -value '+dgres+' MonitorReservation')
                    if not rc and output:
                        # set the value of the MonitorReservation attribute to 0
                        rc, output = sysobj.cmd('_cmd_hares -modify '+dgres+' MonitorReservation 0')
                        if rc:
                            sysobj.log.error('Cannot modify the MonitorReservation attribute of the resource '+dgres+' to 0. Manually set MonitorReservation attribute to 0 for DiskGroup resource.')
                    rc, output = sysobj.cmd('_cmd_hares -value '+dgres+' Reservation')
                    if not rc and not re.search('NONE', output):
                        # set the value of the attribute Reservation to NONE
                        rc, output = sysobj.cmd('_cmd_hares -modify '+dgres+' Reservation NONE')
                        if rc:
                            sysobj.log.error('Cannot modify the Reservation attribute of the resource '+dgres+' to NONE. Manually set MonitorReservation attribute to NONE for DiskGroup resource.')
            # make ro
            haconf_dumpmakero(sysobj)
        else: pass
            # vcs is not running

    def get_vxfen_config(self, sysobj):
        conf = {}
        vxfenmode_file = '/etc/vxfenmode'
        vxfendg_file = '/etc/vxfendg'
        rootpath = self.args.get('rootpath') or ''
        order = 1
        # if /etc/vxfenmode not exists, return
        if not os.path.exists(vxfenmode_file):
            return conf

        match_pattern = {'vxfen_mode': '\s*vxfen_mode\s*=\s*(\w+)',
                         'scsi3_disk_policy': '\s*vxfendg\s*=\s*(\S+)',
                         'vxfendg': '\s*vxfendg\s*=\s*(\S+)',
                         'security': '\s*security\s*=\s*(\d+)',
                         'fips_mode': '\s*fips_mode\s*=\s*(\d+)',
                         'vxfen_honor_cp_order': '\s*vxfen_honor_cp_order\s*=\s*(\d+)',
                         'deprecate_raw_policy': '\s*deprecate_raw_policy\s*=\s*(\S+)'}

        rc, string = sysobj.cmd('_cmd_cat '+rootpath+vxfenmode_file+' 2> /dev/null')

        for line in string.split('\n'):
            if re.match('#', line): continue
            for key, value in match_pattern.items():
                match = re.search(value, line)
                if match: 
                    conf[key] = match.group(1)
            match = re.search('\s*cps(\d+)\s*=\s*\[.+\]', line)    
            if match:
                flag = 1

                conf.setdefault('cporder',{})[str(order)] = line
                order += 1
                for item in line.split(','):
                    vip, port = item.split(':')
                    match = re.search('\[(.+)\]', vip)
                    if not match:
                        continue
                    vip = re.sub('\s*', '', match.group(1))
                    if flag: 
                        cps_sys = vip
                        conf.setdefault('cps',[]).append(vip)
                    conf.setdefault('vips',{}).setdefault(cps_sys,[]).append(vip)
                    conf.setdefault('cpport',{})[vip] = port if port else self.args['default_https_port']
                    flag = 0

        if re.search('scsi3|sybase', conf['vxfen_mode']):
            rc, string = sysobj.cmd('_cmd_cat '+rootpath+vxfendg_file+' 2> /dev/null')
            conf['vxfendg'] = string.rstrip('\n')
        return conf

    def remove_cps(self, sysobj, conf):
        if not self.args.get('cps') or not self.args['cps']['cps']:
            return
        newcps = self.args['cps']['cps']
        oldcps = conf['cps']
        for cps in oldcps:
            # Check if the old cps will be reused
            if cps not in newcps:
                # clean up info on cps
                self.cleanup_from_cps(sysobj, cps, conf)
        
    def cleanup_from_cps(self, sysobj, cps, conf):
        rtn = {'rc':0, 'changed':0, 'msg':''}
        clus = get_clus_uuid(sysobj)
        uuid = clus['uuid']
        sysname = clus['sysname']
        clusname = clus['clusname']
        cpport = conf['cpport'][cps]
        cpsuser = 'cpsclient\@'+sysname
        rc, node_id = sysobj.cmd('_cmd_lltstat -N 2>/dev/null')        
        if rc:
            with open('/etc/llthosts', 'r') as fh:
                llthosts = fh.read()
                match = re.search('(\d+)\s+'+sysname,llthosts) 
                if match: node_id = match.group(1)
        os.environ['CPS_NODEID'] = node_id
        
        # initiate cps sys object to execute command on cps
        islocal = 0
        cpssys = SysCommon(cps, islocal)

        # Using UUID instead of cluster name for the former's uniqueness
        # 1. Unregister all the client nodes from CP Server
        with open('/etc/llthosts', 'r') as fh:
            llthosts = fh.read()
            llthosts = llthosts.split('\n')
        for host in llthosts:
            match = re.search('([0-9]+)\s+'+sysname, host)
            if match: 
                rc, output = sysobj.cmd('_cmd_cpsadm -s '+cps+' -p '+cpport+' -a unreg_node -u '+uuid+' -n'+match.group(1)+' 2>/dev/null')
                rc, output = sysobj.cmd('_cmd_cpsadm -s '+cps+' -a rm_node -p '+cpport+' -c '+clusname+' -u '+uuid+' -h '+sysname+' -n'+match.group(1))
                if not rc: rtn['changed'] = 1
        # 2. Remove the client cluster from the CP Server
        rc, output = cpssys.cmd('_cmd_cpsadm -s '+cps+' -a list_nodes -p '+cpport+' -u '+uuid+' 2>/dev/null | _cmd_grep -w '+clusname)
        if not rc and output:
            # client cluster exists, remove it
            rc, output = cpssys.cmd('_cmd_cpsadm -s '+cps+' -a rm_clus -p '+cpport+' -u '+uuid+' 2>/dev/null')
            if rc:
                sysobj.log.warning("Cannot invoke 'cpsadm' to remove the client cluster with UUID '+uuid+' from Coordination Point server '+cps+'. Cannot complete Coordination Point Server clean up. Refer to the documentation for the steps of manual clean up.") 
            else: rtn['changed'] = 1

        sysobj.log.info('Done clean up from the Coordination Point Server DB on Coordination Point server '+cps+' for the cluster UUID: '+uuid+'.')
        return rtn

class Config_cpagent:
    VXFEN_SKIP_CPAGENT = 326
    VXFEN_CONFIG_CPAGENT_FAIL = 327
    VXFEN_DUMP_FAIL = 329
    VXFEN_SKIP_DEL_CPAGENT = 332   #skip delete cpagent is right return code
    VXFEN_DEL_CPAGENT_FAIL = 333
    rtnMsgCode = {
        VXFEN_SKIP_CPAGENT : 'Veritas vxfen skip cpagent',\
        VXFEN_CONFIG_CPAGENT_FAIL : 'Veritas vxfen config cpagen fail',\
        VXFEN_DUMP_FAIL : 'Veritas vxfen dump fail',\
        VXFEN_SKIP_DEL_CPAGENT : 'Veritas vxfen skip del cpagent',\
        VXFEN_DEL_CPAGENT_FAIL : 'Veritas vxfen del cpagent fail'
    }
    def __init__(self, arguments):
        self.args = arguments
        #default value for arguments state/vxfenmode
        if 'state' not in self.args:
            self.args['state'] = 'present'
        if 'vxfenmode' not in self.args:
            self.args['vxfenmode'] = 'disabled'

    def config_cpagent_run(self, sysobj):
        rtn = {'changed' : 0, 'rc': 0, 'msg' : ''}
        add_cpagent_steps = []
        systems = self.args.get('systems')
        if not systems:
            return rtn
        seednode = self.args.get('seednode')
        if not seednode:
            seednode = systems[0]
        rc, sysname = sysobj.cmd('_cmd_cat /etc/VRTSvcs/conf/sysname')
        vxfengrp = self.args.get('vxfengrp') or 'vxfen'
        if seednode == sysname:
            self.had = instance_factory(sysobj, 'had')
            if not self.had.check_proc(sysobj, 'start'):
                sysobj.log.error('VCS is not running, exit to configure agent of coordination point')
                rtn['rc'] = Config_cpagent.VXFEN_SKIP_CPAGENT
                return rtn
            rc, cpgrp = sysobj.cmd("_cmd_hares -display -attribute Group -type CoordPoint 2>/dev/null | _cmd_grep -v '^#' | awk '{print $1}'")
            if not cpgrp:
                level2freq = self.args.get('level2freq')
                if(level2freq == None):
                    level2freq = 5
                auto_refresh_reg_support = self.args.get('auto_refresh_reg_support')
                if auto_refresh_reg_support == None:
                    auto_refresh_reg_support = 1
                if haconf_makerw(sysobj):
                    rtn['changed'] = 1
                system_index = [system+' '+str(i) for i,system in enumerate(systems)]
                cmd = '_cmd_hagrp -add ' + vxfengrp
                add_cpagent_steps.append(cmd)
                cmd = '_cmd_hagrp -modify ' + vxfengrp + ' SystemList ' + ' '.join(system_index)
                add_cpagent_steps.append(cmd)
                cmd = '_cmd_hagrp -modify ' + vxfengrp + ' AutoFailOver 0'
                add_cpagent_steps.append(cmd)
                cmd = '_cmd_hagrp -modify ' + vxfengrp + ' Parallel 1'
                add_cpagent_steps.append(cmd)
                cmd = '_cmd_hares -add coordpoint CoordPoint ' + vxfengrp
                add_cpagent_steps.append(cmd)

                # ask to configure LevelTwoMonitorFreq
                if not self.args.get('no_coord_disks') and level2freq:
                    cmd = '_cmd_hares -override coordpoint LevelTwoMonitorFreq'
                    add_cpagent_steps.append(cmd)
                    cmd = '_cmd_hares -modify coordpoint LevelTwoMonitorFreq ' + str(level2freq)
                    add_cpagent_steps.append(cmd)
                if auto_refresh_reg_support:
                    cmd = '_cmd_hares -modify coordpoint ActionOnCoordPointFault RefreshRegistrations'
                    add_cpagent_steps.append(cmd)
                cmd = '_cmd_hares -modify coordpoint Enabled 1'
                add_cpagent_steps.append(cmd)
                cmd = '_cmd_hares -add RES_phantom_' + vxfengrp + ' Phantom ' + vxfengrp
                add_cpagent_steps.append(cmd)
                cmd = '_cmd_hares -modify RES_phantom_'+ vxfengrp + ' Enabled 1'
                add_cpagent_steps.append(cmd)

                for step in add_cpagent_steps:
                    rc, output = sysobj.cmd(step)
                    if rc:
                        sysobj.log.error('Failed to configure CoordPoint Agent. Configure the Coordination Point Agent manually')
                        rtn['rc'] = Config_cpagent.VXFEN_CONFIG_CPAGENT_FAIL
                        break
                    else:
                        rtn['changed'] = rtn['changed'] or 1
                rc = haconf_dumpmakero(sysobj)
                if not rc:
                    sysobj.log.error('Cannot dump the VCS configuration into main.cf and make it read-only. Configure the Coordination Point Agent manually.')
                    rtn['rc'] = Config_cpagent.VXFEN_DUMP_FAIL
                else:
                    rtn['changed'] = 1
            else:
                sysobj.log.info('Group name '+ vxfengrp + ' for Coordination Point Agent already exists, skipping.')
        return rtn

    def remove_cpagent_run(self, sysobj):
        rtn = {'changed' : 0, 'rc': 0, 'msg' : ''}
        systems = self.args.get('systems')
        seednode = self.args.get('seednode')
        if not seednode:
            if not systems:
                return rtn
            seednode = systems[0]
        rc, sysname = sysobj.cmd('_cmd_cat /etc/VRTSvcs/conf/sysname')
        if seednode == sysname:
            self.had = instance_factory(sysobj, 'had')
            if not self.had.check_proc(sysobj, 'start'):
                sysobj.log.warning('VCS is not running, skip to remove agent of ccoordiantion point')
                rtn['rc'] = Config_cpagent.VXFEN_SKIP_DEL_CPAGENT
                return rtn
            rc, vxfengrp = sysobj.cmd("_cmd_hares -display -attribute Group -type CoordPoint 2> /dev/null | _cmd_grep -v '^#' | awk '{print $4}'")
            if rc or not vxfengrp:
                return rtn
            haconf_makerw(sysobj)
            rc,vxfenres = sysobj.cmd('_cmd_hagrp -resources '+ vxfengrp + ' -localclus 2> /dev/null')
            for res in vxfenres.split('\n'):
                sysobj.cmd('_cmd_hares -delete ' + res)
            sysobj.cmd('_cmd_hagrp -delete '+ vxfengrp)
            haconf_dumpmakero(sysobj)
            rc, vxfengrp = sysobj.cmd("_cmd_hares -display -attribute Group -type CoordPoint 2> /dev/null | _cmd_grep -v '^#' | awk '{print $4}'")
            if not vxfengrp:
                rtn['changed'] = 1
            else:
                sysobj.log.error('Failed to delete Coordination Point Agent Service Group '+ vxfengrp)
                rtn['rc'] = Config_cpagent.VXFEN_DEL_CPAGENT_FAIL

        return rtn

    def execute(self, sysobj):
        rtn  = {'changed': 0, 'rc': 0, 'msg': ''}
        state = self.args.get('state')
        if state == 'present':
            rtn = self.config_cpagent_run(sysobj)
        elif state == 'absent': 
            rtn = self.remove_cpagent_run(sysobj)

        return rtn




