class Had:
    VCS_CONF_SUCCESS = 502
    VCS_CONF_FAIL = 503
    VCS_UNCONF_SUCCESS = 504
    VCS_UNCONF_FAIL = 505
    VCS_NO_ENCRYPT = 507
    VCS_WRITE_MAINCF_FAIL = 509
    VCS_WRITE_SYSNAME_FAIL = 511
    VCS_NO_SYSNAME = 513
    VCS_NO_ENABLE_FILE = 515
    VCS_GEN_UUID_FAIL = 517
    rtnMsgCode = {VCS_CONF_SUCCESS : 'Configure vcs successfully',
                  VCS_CONF_FAIL : 'Failed to configure vcs',
                  VCS_UNCONF_SUCCESS : 'Unconfigure vcs successfully',
                  VCS_UNCONF_FAIL : 'Failed to unconfigure vcs',
                  VCS_NO_ENCRYPT : 'Cannot find vcsencrypt binary',
                  VCS_WRITE_MAINCF_FAIL : 'Failed to write the main.cf',
                  VCS_WRITE_SYSNAME_FAIL : 'Failed to write the hostname to /etc/VRTSvcs/conf/sysname',
                  VCS_NO_SYSNAME : 'Can not get the hostname of the current system',
                  VCS_NO_ENABLE_FILE : 'Can not find vcs enable file',
                  VCS_GEN_UUID_FAIL : 'Failed to generate the Cluster UUID'}

    # 45 $Macros::macros->set("VERITAS_VCS_CONF_SUCCESS", 2);
    # 46 $Macros::macros->set("VERITAS_VCS_CONF_FAIL", 3);
    # 47 $Macros::macros->set("VERITAS_VCS_UNCONF_SUCCESS", 4);
    # 48 $Macros::macros->set("VERITAS_VCS_UNCONF_FAIL", 5);
    # 49 $Macros::macros->set("VERITAS_VCS_NO_ENCRYPT", 7);
    # 50 $Macros::macros->set("VERITAS_VCS_WRITE_MAINCF_FAIL", 9);
    # 51 $Macros::macros->set("VERITAS_VCS_WRITE_SYSNAME_FAIL", 11);
    # 52 $Macros::macros->set("VERITAS_VCS_NO_SYSNAME", 13);
    # 53 $Macros::macros->set("VERITAS_VCS_NO_ENABLE_FILE", 15);
    # 54 $Macros::macros->set("VERITAS_VCS_GEN_UUID_FAIL", 17);

    def __init__(self, arguments):
        self.args = arguments

    def execute(self, sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        if not self.args['state']:
            self.args['state'] = 'present'

        sysobj.log.info('Performing HAD config')
        had_proc = instance_factory(sysobj, 'had')
        if self.args['state'] == 'present':
        #TODO:check_sys ?
            par_check_err = ""
            if not self.args.get('cluster_name'):
                par_check_err = "Attribute 'cluster_name' is not defined!"
            if not self.args.get('cluster_uuid'):
                par_check_err = "Attribute 'cluster_uuid' is not defined!"
            if not self.args.get('systems'):
                par_check_err = "Attribute 'systems' is not defined!"
            if par_check_err:
                sysobj.log.error(par_check_err)
                rtn['rc'] = Had.VCS_CONF_FAIL
                rtn['msg'] = par_check_err
                exit(str(rtn))
            systems = self.args['systems']
            syslist = []
            if type(systems) == str:
                systems = systems.split(r',')

            if sysobj.args['parameter']['module'].lower() != 'add_node':
                rc = self.update_maincf(sysobj)
                rtn['changed'] = rc['changed'] and 1 or 0
                rtn['rc'] = rc['rc'] and 1 or 0

            rc = self.cluster_uuid(sysobj, self.args['state'], self.args['cluster_uuid'])
            rtn['changed'] = rtn['changed'] or rc['changed']
            rtn['rc'] = rtn['rc'] or rc['rc']

            had_proc.enable_proc(sysobj)

            if rtn['rc']:
                sysobj.log.error('Failed to configure vcs')
                rtn.update({'rc': Had.VCS_CONF_FAIL, 'msg': Had.rtnMsgCode[Had.VCS_CONF_FAIL]})
            else:
                rtn.update({'rc': Had.VCS_CONF_SUCCESS, 'msg': Had.rtnMsgCode[Had.VCS_CONF_SUCCESS]})
        elif self.args['state'] == 'absent':
            had_proc.disable_proc(sysobj)
            rc = self.recover_maincf(sysobj)
            rtn['changed'] = rtn['changed'] or rc['changed']
            rtn['rc'] = rtn['rc'] or rc['rc']
            rc = self.cluster_uuid(sysobj, self.args['state'],'')
            rtn['changed'] = rtn['changed'] or rc['changed']
            rtn['rc'] = rtn['rc'] or rc['rc']
            if rtn['rc']:
                rtn.update({'rc': Had.VCS_UNCONF_FAIL, 'msg': Had.rtnMsgCode[Had.VCS_UNCONF_FAIL]})
                sysobj.log.error(Had.rtnMsgCode[Had.VCS_UNCONF_FAIL])
            else:
                rtn.update({'rc': Had.VCS_UNCONF_SUCCESS, 'msg': Had.rtnMsgCode[Had.VCS_UNCONF_SUCCESS]})
        return rtn

    def include_types_cf(self, sysobj):

        vcsea_types = ['/etc/VRTSagents/ha/conf/Oracle/OracleTypes.cf',\
                '/etc/VRTSagents/ha/conf/OracleASM/OracleASMTypes.cf',\
                '/etc/VRTSagents/ha/conf/Db2udb/Db2udbTypes.cf',\
                '/etc/VRTSagents/ha/conf/Sybase/SybaseTypes.cf']
        dbac_types =  ['/etc/VRTSvcs/conf/PrivNIC.cf',\
                '/etc/VRTSvcs/conf/MultiPrivNIC.cf',\
                '/etc/VRTSvcs/conf/OracleServiceTypes.cf',\
                '/etc/VRTSvcs/conf/CRSResource.cf',\
                '/etc/VRTSvcs/conf/CSSD.cf']

        config_dir = '/etc/VRTSvcs/conf/config'
        conf_dir = '/etc/VRTSvcs/conf'
        component = self.args['component']
        include_str = 'include "types.cf"\n'
        ### cp /etc/VRTSvcs/conf/types.cf to /etc/VRTSvcs/conf/config
        shutil.copy('/etc/VRTSvcs/conf/types.cf', config_dir )
        for type_file in vcsea_types:
            if not os.path.isfile(type_file):
                continue
            else:
                shutil.copy(type_file, config_dir)
                shutil.copy(type_file, conf_dir)
                tmp_arr = type_file.split(r'/')
                include_str = include_str + 'include "' + tmp_arr[-1] + '"\n'
        if re.search('sfrac',component,flags=re.IGNORECASE):
            for type_file in dbac_types:
                if not os.path.isfile(type_file):
                    continue
                else:
                    shutil.copy(type_file, config_dir)
                    tmp_arr = type_file.split(r'/')
                    include_str = include_str + 'include "' + tmp_arr[-1] + '"\n'

        return include_str

    def encrypt_passphrase(self,sysobj,encryptscript):
        passphrase = 'default' or self.args.get('passphrase')
        haconf_makerw(sysobj)
        if passphrase == 'default':
             rc, output = sysobj.cmd(encryptscript + ' -gensecinfo -autoseed 2> /dev/null')
             if rc != 0:
                 sysobj.log.error("Encryption using passphrase failed")
        haconf_dumpmakero(sysobj)

    def update_maincf(self,sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        vcs_user = []
        maincf = self.include_types_cf(sysobj)
        maincf = maincf + '\ncluster ' + self.args['cluster_name'] + ' ('
        encryscript = "/opt/VRTSvcs/bin/vcsencrypt"
        prod_vers = normalize_version(sysobj.args['parameter']['product_version'])

        if compvers(prod_vers,'7.4.2') != 1:
            sysobj.vcs_user_not_added = 1
        else:
            if not os.path.isfile(encryscript):
                sysobj.log.warning('Cannot find vcsencrypt binary - skipping add_users')
                rtn.update({'rc': Had.VCS_NO_ENCRYPT, 'msg': Had.rtnMsgCode[Had.VCS_NO_ENCRYPT]})
            else:
                userlist = self.args.get('vcs_users')
                if not userlist:
                   # users = ['admin:password:Administrators']
                    userlist = [{'user': 'admin', 'password': 'password', 'group': 'Administrators'}]
                n = 0
                for user in userlist:
                    #ur, pd, pr = user.split(r':')
                    vcs_user.append(user)
                    if not vcs_user[n].get('password'):
                        vcs_user[n]['password'] = 'password'
                    if not vcs_user[n].get('group'):
                        vcs_user[n]['group'] = 'Administrators'
                    rc, password = sysobj.cmd(encryscript + ' -vcs ' + vcs_user[n]['password'])
                    vcs_user[n]['password'] = password
                    n = n + 1
                n = 0
                users1 = ''
                admins = ''
                opers = ''
                for n in range(len(vcs_user)):
                    if users1:
                        users1 = users1 + ', '
                    users1 = users1 + '"' + vcs_user[n]['user'] + '" = ' + vcs_user[n]['password']
                    if vcs_user[n]['group'] == 'Administrators':
                        if admins:
                            admins = admins + ', '
                        admins = admins + '"' + vcs_user[n]['user'] + '"'
                    elif vcs_user[n]['group'] == 'Operators':
                        if opers:
                            opers += ', '
                        opers = opers + '"' + vcs_user[n]['user'] + '"'
    
                if users1:
                    maincf += '\n\tUserNames = { ' + users1 + ' }'
                if admins:
                    maincf += '\n\tAdministrators = { ' + admins + ' }'
                if opers:
                    maincf += '\n\tOperators = { ' + opers + ' }'
        ## GCO configuration
        if self.args.get('gco') and self.args['gco'].get('address'):
            maincf += '\n\tClusterAddress = "' + self.args['gco']['address'] + '"'
        maincf += '\n\t)\n\n'
        ## configure remotecluster & heartbeat for GCO 
        if self.args.get('gco') and self.args['gco'].get('remote_cluster'):
            for clus, vip in self.args['gco']['remote_cluster'].items():
                maincf += 'remotecluster ' + clus + ' ('
                maincf += '\n\tClusterAddress = "' + vip + '"'
                maincf += '\n\tConnectTimeout = 3000'
                maincf += '\n\tSocketTimeout = 3000'
                maincf += '\n)\n\n'
            maincf += 'heartbeat Icmp (' 
            maincf += '\n\tClusterList = {' + ','.join(self.args['gco']['remote_cluster'].keys()) + '}'
            for clus, vip in self.args['gco']['remote_cluster'].items():
                maincf += '\n\tArguments @' + clus + ' = {"' + vip + '" }'
            maincf += '\n)\n'

        n = 0
        systems = self.args['systems']
        if type(systems) == str:
            systems = systems.split(r',')
        for sysi in systems:
            maincf  = maincf +  'system ' + sysi + ' (\n\t)\n\n'
            n = n + 1
        
        ## configure VIP(group appnetworkSG on AWS) group
        if self.args.get('awsip') and not self.args.get('gco'):
            maincf += self.awsnetwork('appnetworkSG')       
        if self.args.get('gco'):
            maincf += self.config_gco('ClusterService')

        config_dir = "/etc/VRTSvcs/conf/config"
        with open(config_dir + '/main.cf', 'w') as f:
            rc = f.write(maincf)
        #####!!! change for python 2.7 as write returns None in 2.7 rather number of characters written in 3.5
        if rc:
            sysobj.log.error('1-Failed to write the main.cf')
            rtn.update({'rc': Had.VCS_WRITE_MAINCF_FAIL, 'msg': Had.rtnMsgCode[Had.VCS_WRITE_MAINCF_FAIL] })
        else:
            rtn['changed'] = 1
        hostname = sysobj.hostname

        if hostname:
            rc, output = sysobj.cmd("_cmd_echo " + hostname + " > /etc/VRTSvcs/conf/sysname")
            if rc:
                sysobj.log.error('Failed to write the hostname to /etc/VRTSvcs/conf/sysname')
                rtn.update({'rc': Had.VCS_WRITE_SYSNAME_FAIL, 'msg': Had.rtnMsgCode[Had.VCS_WRITE_SYSNAME_FAIL]})
        else:
            sysobj.log.error('Can not get the hostname of the current system')
            rtn.update({'rc': Had.VCS_NO_SYSNAME, 'msg': Had.rtnMsgCode[Had.VCS_NO_SYSNAME]})
        return rtn

    def awsnetwork(self, name):
        name = name or 'appnetworkSG'
        maincf = '\ngroup ' + name + '('
        maincf += self.service_group()
        maincf += '\n\t)'
        self.args['awsip']['name'] = 'AwsIp_Res'
        maincf += self.awsip_res(self.args['awsip'])
        self.args['awsip']['name'] = 'Ip_Res'
        maincf += self.ip_res(self.args['awsip'])
        self.args['awsip']['name'] = 'Nic_Res'
        maincf += self.nic_res(self.args['awsip'])
        maincf += '\n\nIp_Res requires AwsIp_Res'
        maincf += '\nAwsIp_Res requires Nic_Res\n\n'
    
        return maincf

    def config_gco(self, name):
        name = name or 'ClusterService'
        maincf = '\ngroup ' + name + '('
        maincf += self.service_group()
        maincf += '\n\tOnlineRetryLimit = 3'
        maincf += '\n\tOnlineRetryInterval = 120'
        maincf += '\n\t)'
        if self.args.get('awsip'):
            self.args['awsip']['name'] = 'awsip'
            maincf += self.awsip_res(self.args['awsip'])
        maincf += '\n\n\tApplication wac(\n\t\tStartProgram = "/opt/VRTSvcs/bin/wacstart"\n\t\tStopProgram = "/opt/VRTSvcs/bin/wacstop"\n\t\tMonitorProcesses = { "/opt/VRTSvcs/bin/wac" }\n\t\tRestartLimit = 3\n\t\t)\n\n'
        args = self.args.get('awsip') or self.args.get('gco')
        args['name'] = 'gcoip'
        maincf += self.ip_res(args)
        args['name'] = 'gconic'
        maincf += self.nic_res(args)
        if self.args.get('awsip'):
            maincf += '\n\ngcoip requires awsip'
            maincf += '\nawsip requires gconic'
        else:
            maincf += '\n\ngcoip requires gconic'
        maincf += '\nwac requires gcoip\n\n'
        
        return maincf
    
    def service_group(self):
        maincf = '\n\tSystemList = {'
        n = 0
        systemarr = []
        systems = self.args['systems']
        for sysi in systems:
            systemlist = sysi + '=' + str(n) 
            systemarr.append(systemlist)
            n = n + 1
        maincf += ','.join(systemarr) + '}'
        maincf += '\n\tAutoFailOver = ' + '1'
        maincf += '\n\tAutoStartList = {' + ','.join(self.args['systems']) + '}'

        return maincf

    def awsip_res(self, args):
        maincf = '\n\n\tAWSIP ' + args['name'] + '('
        if args['type'] == 'eip' and args['systems']:
            for host, ip in args['systems'].items():
                maincf += '\n\t\tPrivateIP @' + host + '="' + ip  + '"'
            maincf += '\n\t\tElasticIP = "' + args['address'] + '"' 
        elif args['type'] == 'privateip':
            maincf += '\n\t\tPrivateIP = "' + args['address'] + '"'
        elif args['type'] == 'overlayip':
            maincf += '\n\t\tOverlayIP = "' + args['address'] + '/32"'
        maincf += '\n\t\tDevice = ' + args['dev']
        maincf += '\n\t\tAWSBinDir = ' + '"/usr/local/bin"'
        maincf += '\n\t\t)'

        return maincf

    def ip_res(self, args):
        maincf = '\n\n\tIP ' + args['name'] + '('
        maincf += '\n\t\tCritical = 0'
        maincf += '\n\t\tDevice = ' + args['dev']
        if args.get('type') == 'eip' and args.get('systems'):
            for host, ip in args['systems'].items():
                maincf += '\n\t\tAddress @' + host + '="' + ip  + '"'
        else:
            maincf += '\n\t\tAddress = "' + args['address'] + '"' 
        maincf += '\n\t\tNetMask = "' + args['netmask'] + '"'
        maincf += '\n\t\t)'
    
        return maincf

    def nic_res(self, args):
        maincf = '\n\n\tNIC ' + args['name'] +'('
        maincf += '\n\t\tDevice = ' + args['dev']
        maincf += '\n\t\t)'
   
        return maincf

    def recover_maincf(self, sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        config_dir = "/etc/VRTSvcs/conf/config"
        if os.path.isfile(config_dir + '/main.cf'):
            sysobj.cmd('_cmd_rmr ' + config_dir + '/main.cf.b')
            sysobj.cmd('_cmd_cp ' + config_dir + '/main.cf ' + config_dir + '/main.cf.b')
            rc, output = sysobj.cmd('_cmd_echo include "types.cf" > ' + config_dir + '/main.cf')
            if rc:
                sysobj.log.error('2-Failed to write the main.cf')
                rtn.update({'rc': Had.VCS_WRITE_MAINCF_FAIL, 'msg': Had.rtnMsgCode[Had.VCS_WRITE_MAINCF_FAIL]})
            rc, output = sysobj.cmd('_cmd_diff '+ config_dir + '/main.cf ' + config_dir + '/main.cf.b' + ' >/dev/null')
            if rc:
                rtn['changed'] = 1
        else:
            rc, output = sysobj.cmd('_cmd_echo include "types.cf" > ' + config_dir + '/main.cf')
            if rc:
                sysobj.log.error('3-Failed to write the main.cf')
                rtn.update({'rc': Had.VCS_WRITE_MAINCF_FAIL, 'msg': Had.rtnMsgCode[Had.VCS_WRITE_MAINCF_FAIL]})
            else:
                rtn['changed'] = 1
        rc, output = sysobj.cmd("_cmd_rmr /etc/VRTSvcs/conf/sysname")
        if rc == 0:
            rtn['changed'] = 1
        return rtn

    def cluster_uuid(self,sysobj,state,uuid):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        if state == 'present':
            sysobj.cmd('_cmd_mkdirp /etc/vx/.uuids')
            rc, output = sysobj.cmd('_cmd_echo {' + uuid + '} > /etc/vx/.uuids/clusuuid')
            if rc:
                sysobj.log.error('Failed to generate the Cluster UUID')
                rtn.update({'rc': Had.VCS_GEN_UUID_FAIL, 'msg': Had.rtnMsgCode[Had.VCS_GEN_UUID_FAIL]})
            else:
                rtn['changed'] = 1
        elif state == 'absent':
            rc, output = sysobj.cmd("_cmd_rmr /etc/vx/.uuids/clusuuid")
            if rc == 0:
                rtn['changed'] = 1
        return rtn


