class Vcs_config:
    VCS_SUCCESS = 800
    VCS_FAIL = 801
    rtnMsgCode = { VCS_SUCCESS: 'VCS configuration completed successfully',
                   VCS_FAIL: 'Failed to configure VCS'}

    def __init__(self, arguments):
        self.args = arguments
        self.keys_to_compare = ["product","version","padv","component"]


    def check_args(self):
        invalid = []
        state = self.args.get('state')
        if not state:
            invalid.append('state')
        elif state == 'present':
            arg_list = ['method','vcs_clusterid','systems','component','cluster_name','cluster_uuid','seednode']
            if 'enable_lgf' in self.args.keys() and self.args['enable_lgf'] == 0:
                #arg_list = [ 'component','cluster_name','cluster_uuid' ]
                arg_list = [ 'component', 'cluster_name' ]
            else:
                self.args['enable_lgf'] = 1

            for arg in arg_list:
                if arg not in self.args.keys() or self.args.get(arg) == None:
                    invalid.append(arg)
                else:
                    continue

            method = self.args.get('method') 
            if method == 'ethernet' and self.args['enable_lgf'] != 0:
                if self.args.get('private_link'):
                    private_link = self.args['private_link'].split(r',')
                    if len(private_link) == 1 and not self.args.get('low_priority_link'):
                        invalid.append('low_priority_link')
                else:
                    invalid.append('private_link')
            elif method == 'udp' and self.args['enable_lgf'] != 0:
                 if self.args.get('udp_link'):
                     pass
                 else:
                     invalid.append('udp_link')

        edgeserver = check_edgserver_entry(self)

        if edgeserver:
            invalid.extend(edgeserver)

        return sorted(invalid)

    def execute(self,sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
    #   plat = sysobj.plat    # for UT
    #     if not plat:
    #         return rtn
        if not self.args.get('state'):
            return rtn
            # we assume all the necessary attributes are set, in future we need to come up with a function to validate for each module.


        # check if correct prod is installed and whether component can be configured
        prod = sysobj.args['parameter']['host_facters']['product']
        prod_vers = sysobj.args['parameter']['host_facters']['version']
        comp = self.args['component']
        if prod != "" and comp.lower() in get_configurable_components(prod.lower()):
            sysobj.log.info('Correct product found for configuration')
        else:
            prod_msg = ''
            if prod == "":
                prod_msg = 'no product installed.'
            else:
                prod_msg = 'product ' + prod + ' installed.'
            rtn['msg'] = comp + ' cannot be configured with ' + prod_msg
            rtn['rc'] = 1
            return rtn

        if compvers(prod_vers,normalize_version(self.args['product_version'])) != 0:
            rtn['msg'] = 'Provided product version(' + self.args['product_version'] + ') is not same as installed version(' + prod_vers + ').'
            rtn['rc'] = 1
            return rtn

        if self.args['module'].lower() == 'vcs_config':
            configured_comp = sysobj.args['parameter']['host_facters']['component']
            if configured_comp and configured_comp.lower() != 'none':
                rtn['msg'] = 'Already ' + configured_comp + ' is configured. '
                if configured_comp.lower() == sysobj.args['parameter']['component'].lower():
                    rtn['rc'] = 0
                else:
                    rtn['rc'] = 1
                    rtn['msg'] += 'Reconfiguration is not supported.'
                return rtn
            invalid_args = self.check_args()
            if invalid_args:
                rtn['msg'] = 'Invalid/Missing arguments for config:' + str(invalid_args)
                rtn['rc'] = 1
                return rtn

        if self.args['enable_lgf'] == 0 and not re.search('VCS|SFHA',self.args['component'].upper()):
            rtn['msg'] = 'Single Node cluster can be formed for VCS or SFHA component. Provided component is:' + self.args['component']
            rtn['rc'] = 1
            return rtn

        sysobj.log.info('Performing VCS config')
        self.args['padv'] = sysobj.padv
        self.args['prod'] = self.args['product']
        self.args['release'] = self.args['product_version']

        sysobj.args['parameter'].update(self.args)
        sysobj.args['parameter']['task'] = 'configure'

        if not self.args.get('systems'):
            self.args['systems'] = sysobj.args['parameter']['playbook_hosts']

        self.precheck_config(sysobj)
        if sysobj.error:
            rtn['msg'] = parse_msg(sysobj.error)
            rtn['rc'] = 1
            sysobj.log.debug(rtn['msg'])
            return rtn
        configured_comp = sysobj.args['parameter']['host_facters']['component']
        if configured_comp and configured_comp != 'None':
            rtn['msg'] = 'Already ' + configured_comp + ' is configured. '
            if configured_comp.lower() == sysobj.args['parameter']['component'].lower():
                rtn['rc'] = 0
            else:
                rtn['rc'] = 1
                rtn['msg'] += 'Reconfiguration is not supported.'
            return rtn

        llt = Llt(self.args)
        gab = Gab(self.args)
        amf = Amf(self.args)
        had = Had(self.args)
        self.args['vxfenmode'] = 'disabled'
        vxfen = Vxfen(self.args)
        gms = Gms(self.args)
        odm = Odm(self.args)

        mdrtn = []
        component_path = get_component_path()
        component_list = ['odm','gms','had','amf','vxfen','gab','llt']
        if self.confirm_onenode_cluster(sysobj) == 1:
            component_list = ['odm','gms','had','amf']
            self.set_vcs_onenode_sys(sysobj,1)
        else:
            sysobj.log.info('Not to disable LGF')
            self.set_vcs_onenode_sys(sysobj,0)
        if self.args['state'] == 'present':
            self.args['llt_hosts'] = self.args['systems']
            if self.args.get('method'):
                if 'llt' in component_list: mdrtn.append(llt.execute(sysobj))
            if 'gab' in component_list: mdrtn.append(gab.execute(sysobj))
            if 'amf' in component_list: mdrtn.append(amf.execute(sysobj))
            if 'had' in component_list: mdrtn.append(had.execute(sysobj))
            if 'vxfen' in component_list: mdrtn.append(vxfen.execute(sysobj))
            if 'access' == self.args.get('product', ''):
                sysobj.log.info("Veritas Access doesn't need to configure the components [gms , odm] right now!")
            else:
                if 'gms' in component_list: mdrtn.append(gms.execute(sysobj))
                if 'odm' in component_list: mdrtn.append(odm.execute(sysobj))
            sysobj.cmd("_cmd_echo 'configured_component = " + self.args['component'].upper() + (self.args.get('product_version')).replace('.','') + "' > " + component_path)
        elif self.args['state'] == 'absent':
            if 'access' == self.args.get('product', ''):
                component_list = ['had', 'amf', 'vxfen', 'gab', 'llt']
            for mod in component_list:   # ('gms','odm','had','amf','vxfen','gab','llt')
                mod_obj = eval(mod)
                mdrtn.append(mod_obj.execute(sysobj))     # debug
            sysobj.cmd("_cmd_rmr " + component_path)
        #print(mdrtn)
        for result in mdrtn:
            if result['changed'] == 1 and rtn['changed'] == 0:
                rtn['changed'] = 1
            if result['rc'] % 2 and rtn['rc'] == 0:
                sysobj.log.error("Failed:" + result['msg'] + ' :' + str(result['rc']))
                rtn['rc'] += result['rc']
                rtn['msg'] = result['msg'] + "\n"
        if rtn['rc']:
            sysobj.log.error("Failed to configure VCS")
        else:
            if re.search('vcs_config',self.args['module']):
                if 'license' in self.args.keys() and self.args['license'] != None:
                    # Call licensing with provided license
                    lic_obj = Licensing(self.args)
                    lic_obj.execute(sysobj)
                else:
                    # Call licensing with keyless
                    self.args['license'] = self.args['product'].lower()
                    lic_obj = Licensing(self.args)
                    lic_obj.execute(sysobj)

                # Start services
                proc_obj = Process(self.args)
                proc_rtn = proc_obj.execute(sysobj)
                if proc_rtn['rc'] != 0:
                    rtn['rc'] = Vcs_config.VCS_FAIL
                    rtn['msg'] = proc_rtn['msg'] + ' : Process could not get started.\n' + Vcs_config.rtnMsgCode[rtn['rc']]
                    sysobj.log.info(rtn['msg'])
                else:
                    rtn['rc'] = 0
                    rtn['msg'] = "VCS configuration completed successfully."
                    rtn['changed'] = 1
                    sysobj.log.info(rtn['msg'])

            else:
                rtn['rc'] = 0
                rtn['msg'] = "VCS configuration completed successfully."
                rtn['changed'] = 1
                sysobj.log.info(rtn['msg'])

        return rtn

    def confirm_onenode_cluster(self,sysobj):
        onenode = 1
        if self.args['enable_lgf'] != 0:
            onenode = 0
            sysobj.args['parameter']['enable_lgf'] = 1
        #if 'onenode_cluster' in sysobj.args['parameter']['host_facters'].keys() and sysobj.args['parameter']['host_facters']['onenode_cluster'] == 1:
        #    onenode = 0

        return onenode

    def precheck_config(self,sysobj):
        precheck_obj = Precheck(sysobj.args['parameter'])
        mrtn = precheck_obj.precheck()
        precheck_obj.precheck_compare(sysobj)
        facts = Facter(self.args)
        facts.compare_facters(sysobj,self.keys_to_compare)
        # Check license
        # Check cluster_id availabilty

    def set_vcs_onenode_sys(self,sysobj,onenode):
        sysobj.log.info('ONENODE setting is:' + str(onenode))
        vcs_obj = instance_factory(sysobj, 'vcs', None, 'prod')
        initfile = vcs_obj.initfile['vcs']
        oldonenode = ''
        if os.path.exists(initfile):
            oldonenode = sysobj.cmd('unset ONENODE; . ' + initfile + ' >/dev/null 2>/dev/null ; echo $ONENODE')

        if onenode == 1:
            for proc in ['vxfen', 'gab', 'llt']:
                proc_obj = instance_factory(sysobj, proc, None, 'proc')
                proc_obj.disable_proc(sysobj)
            # Move prev files.
            for file in ['/etc/llttab', '/etc/llthosts', '/etc/gabtab']:
                if os.path.exists(file):
                    sysobj.cmd("_cmd_mv " + file + ' ' + file + '.old')
        else:
            for proc in ['vxfen', 'gab', 'llt']:
                proc_obj = instance_factory(sysobj, proc, None, 'proc')
                proc_obj.enable_proc(sysobj)

        if (onenode == 1  and oldonenode == 'yes') or (onenode == 0 and oldonenode == 'no'):
            return 1

        rc,conf = sysobj.cmd("_cmd_cat " + initfile)
        if re.search('^\s*ONENODE=.*$',conf,re.M):
            if onenode == 1:
                sysobj.log.info('Setting ONENODE=yes')
                conf = re.sub(re.compile('^\s*ONENODE=.*', re.MULTILINE),'ONENODE=yes',conf)
            else:
                sysobj.log.info('Setting ONENODE=no')
                conf = re.sub(re.compile('^\s*ONENODE=.*', re.MULTILINE),'ONENODE=no',conf)
        else:
            if onenode == 1:
                conf = conf + "\nONENODE=yes"
            else:
                conf = conf + "\nONENODE=no"
        conf = (conf + "\n")
        writefile(initfile,conf)
        return 1;

    # Detect whether is single node cps product
    def is_single_node_cps(self,sysobj):
        
        maincf_path = '/etc/VRTSvcs/conf/config/main.cf'
        rc,content = sysobj.cmd("_cmd_cat " + maincf_path)
        if len(re.findall(r'^system\s+(\S+)\s*', content, re.M | re.I)) > 1:
            return 0
        # Is CPS
        if re.search(r'^group\sCPSSG\s',content, re.M):
            return 1

        # Is single node non-CPS
        return 0
    

