class Base_proc(object):
    def __init__(self):
        self.proc = "baseProc"
        self.start_period = 20
        self.stop_period = 20
        self.start_cmd = ""
        self.stop_cmd = ""
        ## XXX_START = 0
        self.init_conf = ''
        ## start/stop script
        self.control_file = ''

    # check whether mod is loaded
    # 1 means loaded, 0 means not loaded
    def check_proc(self, sysobj, state=None):
        module = self.get_driver(sysobj, self.proc)
        rtn = 1 if module else 0
        return rtn

    def start_proc(self, sysobj):
        sysobj.cmd(self.start_cmd)
        return 1

    def stop_proc(self, sysobj):
        sysobj.cmd(self.stop_cmd)
        if self.get_driver(sysobj, self.proc):
            sysobj.log.info('Unloading  driver')
            self.unload_driver(sysobj, self.proc)
        return 1

    # Start failed.
    # To show debug informations in start_failed
    def start_failed(self, sysobj):
        pass

    # Stop failed.
    # To show debug informations in stop_failed
    def stop_failed(self, sysobj):
        pass

    # Only for RHEL7 & SLES12
    # sometimes when CPI failed to stop llt/gab/vxfen, but the systemd status is already changed to inactive
    # in this situation, if we try to stop again, systemd would not stop llt since the service status is already inactive, but llt is still running
    # in proc_stop_sys() or proc_start_sys, check_sys() first, call stop_sys only if check_sys() is running
    # so when come to here, check_sys() is running, systemd status should be active
    # if not then we need to make systemd active first, then stop llt/gab/vxfen
    def systemctl_start_service(self, sysobj, proc):
        rc, status = sysobj.cmd('_cmd_systemctl show -p ActiveState ' + proc + ' 2>/dev/null')
        if re.search('=active', status):
            sysobj.cmd('_cmd_systemctl stop ' + proc + ' 2>/dev/null')
        sysobj.cmd('_cmd_systemctl start ' + proc + ' 2>/dev/null')
        return 1

    # Only for RHEL7 & SLES12
    def systemctl_stop_service(self, sysobj, proc):
        rc, status = sysobj.cmd('_cmd_systemctl show -p ActiveState ' + proc + ' 2>/dev/null')
        if not re.search('=active', status):
            sysobj.cmd('_cmd_systemctl start ' + proc + ' 2>/dev/null')
        sysobj.cmd('_cmd_systemctl stop ' + proc + ' 2>/dev/null')
        rc, status = sysobj.cmd('_cmd_systemctl show -p ActiveState ' + proc + ' 2>/dev/null')
        if re.search('failed', status):
            rc, path = sysobj.cmd('_cmd_systemctl show ' + proc + ' -p SourcePath')
            path = re.search('SourcePath=(.*)', path).group(1)
            sysobj.cmd(path + ' stop') if path else ''
        return 1         

    def enable_proc(self, sysobj):
        if not self.init_conf:
            return 0
        conf = sysobj.cat_file(self.init_conf)
        if not conf:
            return 0
        conf = re.sub(self.proc.upper()+'_START\s*=\s*0', self.proc.upper()+'_START=1', conf)
        conf = re.sub(self.proc.upper()+'_STOP\s*=\s*0', self.proc.upper()+'_STOP=1', conf)
        sysobj.move_file(self.init_conf, self.init_conf+'.prev')
        with open (self.init_conf, 'w') as fh:
            fh.write(conf)
        return 1

    def disable_proc(self, sysobj):
        conf = sysobj.cat_file(self.init_conf)
        if not conf:
            return 0
        conf = re.sub(self.proc.upper()+'_START\s*=\s*1', self.proc.upper()+'_START=0', conf)
        conf = re.sub(self.proc.upper()+'_STOP\s*=\s*1', self.proc.upper()+'_STOP=0', conf)
        sysobj.move_file(self.init_conf, self.init_conf+'.prev')
        with open (self.init_conf, 'w') as fh:
            fh.write(conf)
        return 1

    def get_driver(self, sysobj, driver):
        rc, output = sysobj.cmd("_cmd_modinfo | _cmd_grep '^" + driver + "'")
        return output
    
    # unload a driver on $sys
    # returns: 1 on success, 0 on failure
    def load_driver(self, sysobj, driver):
        rc, output = sysobj.cmd("_cmd_modprobe " + driver)
        if not rc: 
            return 1
        else:
            return 0

    # unload a driver on $sys
    # returns: 1 on success, 0 on failure
    def unload_driver(self, sysobj, driver):
        rc, output = sysobj.cmd('_cmd_modunload ' + driver + ' 2>&1')
        if output == '' or re.search('loaded', output):
            return 1
        else:
            return 0

    #check if one proc is running by ps
    # return : 1 running; 0: not running.
    def check_by_pid(self, sysobj, proc):
        pids = self.proc_pids(sysobj, proc)
        if pids : 
            return 1
        else: 
            return 0

    def force_stop_proc(self, sysobj):
        return 1

    def kill_proc(self, sysobj, proc):
        pids = self.proc_pids(sysobj, proc)
        for running_proc in pids:
            sysobj.cmd('_cmd_kill -9 ' + running_proc)
            time.sleep(1)

    def proc_pids(self, sysobj, proc):
        pids = []

        cmd = "_cmd_ps -ef | _cmd_grep '" + proc + "' | _cmd_grep -v grep | _cmd_grep -v defunct"
        rc, output = sysobj.cmd(cmd)
        for running_proc in re.split('\n', output):
            ma = re.search('^\s*\S+\s*(\d+)', running_proc)
            if ma:
                pids.append(ma.group(1))

        return pids
                

