class Had_common(Base_proc, object):
    def __init__(self):
        super(Had_common, self).__init__()
        self.proc = 'had'
        self.start_period = 120
        self.stop_period = 10
        self.control_file = '/etc/init.d/vcs'
        self.start_cmd = self.control_file + ' start'
        self.stop_cmd = '_cmd_hastop -local -force'
        self.init_conf = '/etc/sysconfig/vcs'

    def check_proc(self, sysobj, state):
        rc, had = sysobj.cmd("_cmd_ps -ef | _cmd_grep 'bin/had' | _cmd_grep -v grep | _cmd_grep -v defunct")
        rc, hashadow = sysobj.cmd("_cmd_ps -ef | _cmd_grep hashadow | _cmd_grep -v grep | _cmd_grep -v defunct")
        if state == 'start':
            # When in startup phase,
            # regard had not running if had not running or hashadow not running
            if not had or not hashadow:
                if not had:
                    sysobj.log.info('had is not running.')
                if not hashadow:
                    sysobj.log.info('hashadow is not running.')
                return 0
        elif state == 'stop':
            # When in stop phase,
            # regard had running if had running or hashadow running
            # regard had not running if both had and hashadow not running
            if not had and not hashadow:
                sysobj.log.info('both had and hashadow are not running.')
                return 0
            else :
                return 1

        sysobj.log.info('both had and hashadow are running, checking VCS state further')
        rc, sysname = sysobj.cmd('_cmd_cat /etc/VRTSvcs/conf/sysname')
        rc, output = sysobj.cmd('_cmd_hasys -wait ' + sysname + ' SysState RUNNING -time 60')
        if not rc:
            sysobj.log.info('VCS state is RUNNING.')
            return 1
        rc, state = sysobj.cmd('_cmd_hasys -state ' + sysname)
        sysobj.log.info('VCS is not in RUNNING state. State is ' + state)
        if re.search(r'ERROR', state):
            return 0
        if re.search(r'STALE', state):
            sysobj.log.warning('State is ' + state + ', means that had failed to start up because of main.cf error')
            self.start_period = 0
            return 0

        return 1

    def force_stop_proc(self, sysobj):
        self.kill_proc(sysobj, 'bin/had')
        self.kill_proc(sysobj, 'hashadow')

    def start_failed(self, sysobj):
        sysobj.cmd('_cmd_gabconfig -a 2>/dev/null')
        sysobj.log.info('## Check start failures in engine_A.log:')
        sysobj.cmd('_cmd_tail -10 ' + sysobj.vcs_log_path + '/log/engine_A.log 2>/dev/null')
        return 1

    def stop_failed(self, sysobj):
        sysobj.log.info('## Check stop failures in engine_A.log:')
        sysobj.cmd('_cmd_tail -10 ' + sysobj.vcs_log_path + '/log/engine_A.log 2>/dev/null')
        return 1

    def enable_proc(self, sysobj):
        conf = sysobj.cat_file(self.init_conf)
        if not conf:
            return 0
        conf = re.sub('VCS_START\s*=\s*0', 'VCS_START=1', conf)
        conf = re.sub('VCS_STOP\s*=\s*0', 'VCS_STOP=1', conf)
        sysobj.move_file(self.init_conf, self.init_conf+'.prev')
        with open (self.init_conf, 'w') as fh:
            fh.write(conf)
        return 1

    def disable_proc(self, sysobj):
        conf = sysobj.cat_file(self.init_conf)
        if not conf:
            return 0
        conf = re.sub('VCS_START\s*=\s*1', 'VCS_START=0', conf)
        conf = re.sub('VCS_STOP\s*=\s*1', 'VCS_STOP=0', conf)
        sysobj.move_file(self.init_conf, self.init_conf+'.prev')
        with open (self.init_conf, 'w') as fh:
            fh.write(conf)
        return 1

    def is_onenode_running_sys (self,sysobj):
        had_pids = self.proc_pids(sysobj,'bin/had -onenode')
        if had_pids:
            sysobj.log.info('had is running in one node mode')
            return 1
        return 0

    def post_start(self,sysobj):
        if hasattr(sysobj,'vcs_user_not_added') and sysobj.vcs_user_not_added == 1:
            vcs_user = []
            encryscript = "/opt/VRTSvcs/bin/vcsencrypt"

            if not os.path.isfile(encryscript):
                sysobj.log.warning('Cannot find vcsencrypt binary - skipping add_users')
                rtn.update({'rc': Had.VCS_NO_ENCRYPT, 'msg': Had.rtnMsgCode[Had.VCS_NO_ENCRYPT]})
            else:
                self.encrypt_passphrase(sysobj,encryscript)
                userlist = sysobj.args['parameter'].get('vcs_users')
                if not userlist:
                   # users = ['admin:password:Administrators']
                    userlist = [{'user': 'admin', 'password': 'password', 'group': 'Administrator'}]
                n = 0
                haconf_makerw(sysobj)
                for user in userlist:
                    #ur, pd, pr = user.split(r':')
                    vcs_user.append(user)
                    if not vcs_user[n].get('password'):
                        vcs_user[n]['password'] = 'password'
                    if not vcs_user[n].get('group'):
                        vcs_user[n]['group'] = 'Administrator'
                    rc,output = sysobj.cmd("_cmd_hauser -add " + vcs_user[n]['user'] + ' -password ' +  vcs_user[n]['password'] + ' -priv ' + vcs_user[n]['group']  + ' 2>/dev/null')
                    n = n + 1
                haconf_dumpmakero(sysobj)
        else:
            sysobj.log.info('Had post start has nothing to do')

    def encrypt_passphrase(self,sysobj,encryptscript):
        passphrase = 'default' or self.args.get('passphrase')
        haconf_makerw(sysobj)
        if passphrase == 'default':
             rc, output = sysobj.cmd(encryptscript + ' -gensecinfo -autoseed 2> /dev/null')
             if rc != 0:
                 sysobj.log.error("Encryption using passphrase failed")
        haconf_dumpmakero(sysobj)

