class Vxodm_linux(Vxodm_common, object):
    def __init__(self):
        super(Vxodm_linux, self).__init__()
        self.start_cmd = '/etc/init.d/vxodm start 2>/dev/null'
        self.stop_cmd = '/etc/init.d/vxodm stop 2>/dev/null'

    def enable_proc(self, sysobj):
        if os.path.exists(self.init_conf):
            conf = sysobj.cat_file(self.init_conf)
            if not re.search('ODM_START', conf):
                conf += '\nODM_START=1'
            else:
                conf = re.sub('ODM_START\s*=\s*0', 'ODM_START=1', conf)
            sysobj.move_file(self.init_conf, self.init_conf+'.prev')
            with open (self.init_conf, 'w') as fh:
                fh.write(conf)
        else:
            sysobj.cmd("_cmd_echo 'ODM_START=1' > " + self.init_conf)
        return 1

    def disable_proc(self, sysobj):
        if os.path.exists(self.init_conf):
            conf = sysobj.cat_file(self.init_conf)
            if not re.search('ODM_START', conf):
                conf += '\nODM_START=0'
            else:
                conf = re.sub('ODM_START\s*=\s*1', 'ODM_START=0', conf)
            sysobj.move_file(self.init_conf, self.init_conf+'.prev')
            with open (self.init_conf, 'w') as fh:
                fh.write(conf)
        else:
            sysobj.cmd("_cmd_echo 'ODM_START=0' > " + self.init_conf)
        return 1

    def check_proc(self, sysobj, state):
        module = self.get_driver(sysobj, self.proc)
        if not module:
            return 0
        #TODO: logic may be in 'SFRAC|SVS|SFCFS|SFCFSHA' begin
        component = sysobj.args['parameter'].get('component')
        if component and re.search('sfcfsha|sfrac', component):
            if state == 'start':
                rc, odmconf = sysobj.cmd("_cmd_gabconfig -a 2> /dev/null | _cmd_grep 'Port d'")
                if odmconf:
                    return 1
            else:
                ## module is loaded, return 1 directly
                return 1
        # For DBED, there is no port 'd' (Standalone mode).
        # Check if /dev/odm is mounted or not.
        rc, output = sysobj.cmd("/etc/init.d/vxodm status 2>/dev/null | _cmd_grep 'is stopped'")
        if not output:
            sysobj.log.info('odm started')
            return 1
        else:
            sysobj.log.info('odm loaded not configured')
            # stopping, need to unload
            return 1 if state == 'stop' else 0
        # starting, need to configure
        return 0

    def is_enabled(self, sysobj):
        if not os.path.exists(self.init_conf):
            return 0
        rc, output = sysobj.cmd('unset ODM_START 2>/dev/null >/dev/null; . ' + self.init_conf + ' 2>/dev/null >/dev/null ; echo -n $ODM_START')
        return 1 if output == 1 else 0
        
