        sysobj.cmd('_cmd_vxddladm start eventsource &>/dev/null')
        sysobj.cmd('_cmd_vxrecover -sn &>/dev/null')
        sysobj.cmd('_cmd_vxrecover -b -o iosize=64k &>/dev/null')
