class Vxnotify_common(Base_proc, object):
    def __init__(self):
        super(Vxnotify_common, self).__init__()
        self.proc = 'vxnotify'
        self.start_period = 10
        self.stop_period = 10
        self.multisystemserialstart = 1

    def start_proc(self, sysobj):
        pass

    def stop_proc(self, sysobj):
        sfmh_discovery = instance_factory(sysobj, 'sfmh_discovery')
        if sfmh_discovery.check_proc(sysobj, 'stop'):
            sfmh_discovery.stop_proc(sysobj)

        ## Access will kill the notification daemon which auto resum vxnotify
        if os.path.exists("/opt/VRTSnas/scripts/report/event_notify.sh"):
            self.kill_notification_daemon(sysobj)

        self.kill_proc(sysobj, self.proc)
        return 1
    
    def check_proc(self, sysobj, state=None):
        return self.check_by_pid(sysobj, self.proc)


    # snas: stop notification daemon
    def kill_notification_daemon(self, sysobj):
        pid_arr = []
        event_notify_procs = self.proc_pids(sysobj, 'event_notify.sh')
        for pid in event_notify_procs:
            l_cmd = "_cmd_ps -ef| _cmd_awk '{if($2 == " + pid + ") {print $3}}'"
            rc, output = sysobj.cmd(l_cmd)
            # do not kill event_notify itself
            if '1' == str(output).rstrip():
                continue
            pid_arr.extend(self.find_children_of_pid(sysobj,pid))
            pid_arr.append(pid)
        ## delete repeate elements
        pid_arr = list(set(pid_arr))
        self.kill_pids(sysobj,pid_arr)

    def kill_pids(self, sysobj, pid_arr):
        ## speed up
        killed_procs = ' '.join(pid_arr)
        sysobj.cmd('_cmd_kill -9 ' + killed_procs)
        time.sleep(1)
        ## this is slow
        # for running_proc in pid_arr:
        #     sysobj.cmd('_cmd_kill -9 ' + running_proc)
        #     time.sleep(1)

    def find_children_of_pid(self,sysobj, pid):
        pids = []
        ## over the regression
        if not pid:
            return []
        l_cmd = "_cmd_ps -ef | _cmd_awk '{if ($3 == " + pid + ") {print $2}}'"
        rc, output = sysobj.cmd(l_cmd)
        for my_pid in output.split('\n'):
            pids.extend(self.find_children_of_pid(sysobj, my_pid))
            pids.append(my_pid)
        return pids


