class Vxvm_dgvolfs:

    def __init__(self, arguments):
        self.args = arguments

    def check_args(self):
        invalid = []
        msg = ""
        arglist = [ 'dgtype', 'seednode', 'dgname', 'disks' ]
        optional_arglist = ['volinfo', 'taginfo']
        if self.args['state'] == 'present':
            n = 1
            while self.args.get('dg' + str(n)):
                temp = []
                for arg in ( arglist ):
                    if self.args['dg'+ str(n)].get(arg):
                        #print self.args['dg'+ str(n)].get(arg).__class__
                        continue
                    else:
                        temp.append(arg)
                #TODO check optional arguments also
                for optarg in optional_arglist:
                    if self.args['dg'+ str(n)].get(optarg) != None and optarg == 'volinfo':
                        #Check if volume name and size both provided
                        pass
                    if self.args['dg'+ str(n)].get(optarg) != None and optarg == 'taginfo':
                        disks = self.args['dg'+ str(n)].get('disks')
                        #Check if tagname value and disk are provided
                        for tginfo in self.args['dg'+ str(n)].get(optarg):
                            if len(tginfo) == 3:
                                nodisk = []
                                for disk in tginfo[2]:
                                    if disk not in disks:
                                        nodisk.append(disk)
                                if nodisk:
                                    msg += 'Disks provided for tagging ' + str(nodisk) + ' are not part of the disk lists ' + str(disks)+ ' provided for disk group creation.' + "\n"
                            else:
                                msg += 'Argument taginfo requires 3 values which are tagname, tagvalue, disks. Provided values:' + str(tginfo) + "\n"

                if temp != []:
                    msg = msg + 'Argument/s ' + str(temp) + ' is/are missing for module vxvm_dgvolfs under dg' +str(n) + '. Supported arguments are ' + str(arglist)
                    invalid.append(temp)
                n += 1
        if msg != "":
            rtn = {'msg': msg, 'rc': 1, 'changed': 0}
            exit(str(rtn))

    def execute(self,sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''}
        mrtn = []
        self.check_args()
        if self.args['state'] == 'present':
            n = 1
            while self.args.get('dg' + str(n)):
                dgnum = 'dg' + str(n)
                dginfo = self.args[dgnum]

                if self.check_dg_exists(sysobj,dginfo) == 1 and self.args[dgnum].get('seednode') == sysobj.hostname:
                    output = 'Disk group ' + dginfo['dgname'] + ' already exists' + "\n"
                    rtn['rc'] += 0
                    rtn['msg'] += output
                    rtn['changed'] += 0 
                    if 'taginfo' in dginfo.keys() and dginfo['taginfo'] != None :
                        for tginfo in dginfo['taginfo']:
                            mrtn.append(self.check_create_tagging(sysobj, tginfo,dginfo['dgname']))
                    if 'volinfo' in dginfo.keys():
                        mrtn.append(self.create_vol(sysobj, dginfo))
                    n += 1
                    continue
                ## disk name is the same on each EC2, but after export disks, vxvm will append suffix
                ## and we cannot get disk names before configure product, so will get all available disks on AWS
                if self.args.get('aws'):
                    sysobj.log.info('#Found aws')
                    dginfo = self.get_disk_on_aws(sysobj, dginfo)
                
                if str(self.args[dgnum].get('fss')) == '1':
                    sysobj.log.info('#Found fss')
                    # if FSS is configured(disks are not shared between nodes), need to init/export disk on its own node 
                    self.init_disk(sysobj, dginfo)
                else:
                    if self.args[dgnum].get('seednode') == sysobj.hostname:
                        # if no FSS, only do init disks on one node
                        sysobj.log.info('Call of init_disk for:' + sysobj.hostname)
                        self.init_disk(sysobj, dginfo)
                # Check if disk is initialized properly
                if self.args[dgnum].get('seednode') == sysobj.hostname:
                    if self.check_init_disk(sysobj, dginfo) != 0:
                        rtn = {'changed': 0, 'rc': 1, 'msg': 'Disk initialization failed for:' + dgnum}
                        n += 1
                        continue 

                ## create dg/vol, mkfs on one node 
                sysobj.log.info('#Out of if')
                if self.args[dgnum].get('seednode') == sysobj.hostname:
                    rc,output,changed = self.create_dg(dgnum,sysobj, dginfo)
                    if rc == 0:
                        if 'taginfo' in dginfo.keys() and dginfo['taginfo'] != None:
                            for tginfo in dginfo['taginfo']:
                                mrtn.append(self.check_create_tagging(sysobj, tginfo,dginfo['dgname']))
                        if 'volinfo' in dginfo.keys():
                            mrtn.append(self.create_vol(sysobj, dginfo))

                    mrtn.append([rc,output,changed])

                n += 1

            if mrtn:
                #print(mrtn)
                sysobj.log.info(str(mrtn))
                for result in mrtn:
                    #if result[0]:
                        rtn['rc'] += result[0]
                        rtn['msg'] += result[1] + "\n"
                        rtn['changed'] += result[2]

        elif self.args['state'] == 'absent':
            n = 1
            while self.args.get('dg' + str(n)):
                dgnum = 'dg' + str(n)
                dginfo = self.args[dgnum]
                if self.args[dgnum].get('seednode') != sysobj.hostname:
                    return {'changed': 0, 'rc': 0, 'msg': ''}
                cmd1 = 'vxdg destroy ' + dginfo['dgname']
                if self.check_dg_exists(sysobj,dginfo) == 1:
                    rc,output = sysobj.cmd(cmd1)
                    if rc == 0:
                        rtn['changed'] += 1
                        rtn['rc'] += rc
                    else:
                        rtn['changed'] += 0
                        rtn['rc'] += rc
                        rtn['msg'] += output + "\n"
                else:
                    rtn['msg'] += dginfo['dgname'] + ' does not exists' + "\n"
                    rtn['rc'] += 0
                    rtn['changed'] += 0
                n += 1
            
        return rtn

    def check_dg_exists(self,sysobj,dginfo):
        #Check if DG already exists
        cmd = 'vxprint -g ' + dginfo['dgname'] + ' -f' + " | _cmd_grep ^dg | _cmd_awk '{print $2}'"
        rc,output = sysobj.cmd(cmd)
        if output == dginfo['dgname']:
            return 1
        else:
            return 0

    def get_disk_on_aws(self, sysobj, dginfo):
        rc, disks = sysobj.cmd('vxdisk list|grep online|awk \'{print $1}\' |grep -v xvd')
        disks = disks.split('\n')
        dginfo['disks'] = disks
        
        return dginfo

    def check_init_disk (self, sysobj, dginfo):
        sysobj.log.info('#Calling check_init_disk')
        rtn = 0
        for disk in dginfo['disks']:
            sysobj.log.info('Got the disk:' + disk)
            cmd = '_cmd_vxdisk list '
            rc, output = sysobj.cmd(cmd + disk)
            if rc == 0:
                sysobj.log.error(disk + ' initialized')
            else:
                sysobj.log.error(disk + ' not initialized:' + output)
                rtn = rtn + 1

        return rtn

    def init_disk(self, sysobj, dginfo):
        sysobj.log.info('#Calling init_disk')
        rtn = 0
        for disk in dginfo['disks']:
            sysobj.log.info('Got the disk:' + disk)
            rc, output = sysobj.cmd('_cmd_vxdisk list ' + disk)
            if not rc:
                rc, output = sysobj.cmd('_cmd_vxdisk list ' + disk + ' | grep -w info | grep cdsdisk ')
                if rc:
                    rc, output = sysobj.cmd('_cmd_vxdisksetup -if ' + disk)
                    if rc and not re.search('is already being used',output):
                        rtn = 1
                if str(dginfo.get('fss')) == '1':
                    count = 0
                    while count < 5:
                        rc, output = sysobj.cmd('_cmd_vxdisk export ' + disk)
                        if rc:
                            time.sleep(5)
                            count += 1
                        else: break
            else:
                rtn = rtn+1
        return rtn

    def create_dg(self, dgnum, sysobj, dginfo):
        ## this function should be only called on one node, seednode
        ## for AWS, we will use all available disks for FSS
        sysobj.log.info('Calling create_dg')
        if self.args[dgnum].get('aws') and str(self.args[dgnum].get('fss')) == '1':
            ## wait all cluster nodes export the disks,the disk name on aws is like, 172-0-2-232_xen-vd0_1
            suffix = re.search('(\d+.*)', sysobj.hostname).group(1)
            ## get the disk number on the first local system
            rc, disknum = sysobj.cmd('_cmd_vxdisk list|grep online|awk \'{print $1}\' |grep -v xvd|grep '+suffix+'_|wc -l')
            ## all instances have the same disk number
            flag = 0
            for system in self.args['systems']:
                if system == sysobj.hostname: continue
                suffix = re.search('(\d+.*)', system).group(1)
                count = 0
                while count <60:
                    rc,num= sysobj.cmd('_cmd_vxdisk list|grep online|awk \'{print $1}\' |grep -v xvd|grep '+suffix+'_|wc -l')
                    if int(num) < int(disknum):
                        time.sleep(5)
                        count += 1
                    else: break
                if count == 60:
                    sysobj.log.error('The disks are not epxorted from '+system)
                    flag = 1
            if flag:
                return 1, 'The disks are not epxorted from all the cluster nodes.'
            dginfo = self.get_disk_on_aws(sysobj, dginfo)
        sysobj.log.info('Creating dg ' + dginfo['dgname'])
        dgtype = '-s' if dginfo['dgtype'] == 'shared' else ' '
        fss = ' -o fss' if str(dginfo.get('fss')) == '1' else ''
        time.sleep(11)
        changed = 0
        cmd = '_cmd_vxdg ' + dgtype + fss + ' init ' + dginfo['dgname'] + ' ' + ' '.join(dginfo['disks'])
        rc,output = sysobj.cmd(cmd)
        if rc == 0:
            changed = 1

        return rc, output,changed

    def check_create_tagging (self, sysobj, tginfo,dgname):
        tagname = tginfo[0]
        tagvalue = tginfo[1]
        disk_names = tginfo[2]
        rtn = {'msg':'','rc':0,'changed':0}

        for disk in disk_names:
            cmd = '_cmd_vxdisk -g ' + dgname + ' settag ' + disk + ' ' + tagname + '=' + tagvalue
            rc, output = sysobj.cmd(cmd)

            if rc == 0:
                rtn['msg'] += 'Disk tagging done for ' + dgname + ' ' + tagname + ' ' + tagvalue + "\n"
                rtn['changed'] += 1
            else:
                rtn['changed'] += 0
                if not re.search('Tagid already assigned to disk',output):
                    rtn['rc']  += 1
                    rtn['msg'] += '!! Disk tagging failed for ' + dgname + ' ' + tagname + ' ' + tagvalue + ' ' + output + "\n"
                else:
                    rtn['msg'] += 'Disk tag ' + tagname + ' already exists on disk:' + disk + "\n"

        return rtn['rc'],rtn['msg'],rtn['changed']

    def create_vol(self, sysobj, dginfo):
        mrtn = []
        cnt = 0
        changed = 0
        rc1 = 0
        msg = ''
        time.sleep(5)
        sysobj.log.info("Inside create_vol" + str(dginfo['volinfo']))
        if not dginfo['volinfo']:
            return mrtn
        volinfolen = len(dginfo['volinfo'])
        #for volname,volsize in dginfo['volinfo':
        sysobj.log.info("Inside create_vol" + str(dginfo['volinfo']))
        for i in range(volinfolen):
            volname = volsize = voldisk = None
            sysobj.log.info("Inside create_vol1" + str(dginfo['volinfo'][i]))
            if len(dginfo['volinfo'][i]) == 3:
                volname,volsize,voldisk = dginfo['volinfo'][i]
            else:
                volname,volsize = dginfo['volinfo'][i]
                voldisk = ''
            # Check if volume already exists
            cmd = 'vxprint -g ' + dginfo['dgname'] + ' -a ' + volname + " | _cmd_awk '{print $2}'"
            rc,output = sysobj.cmd(cmd)
            if output == volname:
                output = 'Volume ' + volname + ' already exists on disk group ' + dginfo['dgname']
                msg += output + "\n"
                cnt += 1
                continue
            #for volname,volsize in volinfo:
            vollayout = ' layout=' + self.args['vollayout'][cnt] if self.args.get('vollayout') and self.args['vollayout'][cnt] else ''
            sysobj.log.info('Creating and mkfs ' + volname)
            cmd = '_cmd_vxassist -g ' + dginfo['dgname'] + ' make ' + volname + ' ' + volsize + ' ' + voldisk + ' ' + vollayout
            rc,output = sysobj.cmd(cmd)
            if rc == 0:
                msg += volname + ' has been created' + "\n"
                mkfst = '_cmd_mkfs -t'
                if sysobj.plat == 'SunOS':
                    mkfst = '_cmd_mkfs -F' 
                cmd = mkfst + ' vxfs /dev/vx/rdsk/' + dginfo['dgname'] + '/' + volname
                rc, output = sysobj.cmd(cmd)
                changed += 1
                if rc != 0:
                    msg += output + "\n"
                    rc += 1
            else:
                msg += ':'.join([volname,volsize,voldisk]) + ' ->' + output + "\n"
            rc1 += rc
            cnt += 1

        return rc1,msg,changed
            

