    def create_vvr_rvggrp(self, sysobj, sgdict):
        ## create group logowner for vvr
        group = sgdict['rvg_grp']
        ip_res = sgdict['ip_res']
        nic_res = sgdict['nic_res']
        rvg_res = sgdict['rvg_res']
        awsip_res = sgdict['awsip_res'] 
        datadg_res = sgdict['datadg_res']
        iptype = self.args.get('type')
        vip = self.args['primaryvip'] if sysobj.hostname == self.args['seednode'] else self.args['secondaryvip']
        suffix = '/32' if iptype == 'overlayip' else ''
        agentinfo = self.args.get('agentinfo')
        sysobj.log.info('I am here ' + str(agentinfo))
        if sysobj.hostname == self.args['seednode']:
            agentinfo = agentinfo['primary']
        else:
            agentinfo = agentinfo['secondary']
        sysobj.log.info('I am here2 ' + str(agentinfo))
        dev = agentinfo['nic']
        netmask = agentinfo['netmask']
        dg_name = self.args['dgname']
        gateway=agentinfo['networkhosts']
        gatewaystr = ' '.join(gateway)
        rvg = self.args['rvgname']

        ## we support overlay ip and private ip on AWS
        if iptype == 'overlayip':
            iptype = 'OverlayIP'
        elif iptype == 'privateip':
            iptype = 'PrivateIP'

        rtn = add_group(sysobj, group, 'AutoFailOver', self.args['systems'])
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10129 Attempting to add existing group',rtn['msg']):
            return rtn
        ## add awsip agent if on AWS
        if self.args.get('aws'):
            rtn = add_res(sysobj, awsip_res, 'AWSIP', group)
            if rtn['rc'] and not re.search('VCS WARNING V-16-1-10238 Attempting to add existing resource',rtn['msg']):
                rtn['changed'] = 1
                return rtn
            modify_res_attr(sysobj, awsip_res, iptype, '"'+vip+suffix+'"')
            modify_res_attr(sysobj, awsip_res, 'Device', dev)
            modify_res_attr(sysobj, awsip_res, 'AWSBinDir', '"/usr/local/bin"')
            if self.args.get('route_tables'):
                string = self.args['route_tables'].replace(',', ' ')
                modify_res_attr(sysobj, awsip_res, 'RouteTableIds', string )
            modify_res_attr(sysobj, awsip_res, 'Enabled', '1')
        ## add IP agent for rlink
        rtn = add_res(sysobj, ip_res, 'IP', group)
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10238 Attempting to add existing resource',rtn['msg']):
            rtn['changed'] = 1
            return rtn
        modify_res_attr(sysobj, ip_res, 'Device', dev)
        modify_res_attr(sysobj, ip_res, 'Address', '"'+vip+'"')
        modify_res_attr(sysobj, ip_res, 'NetMask', netmask)
        modify_res_attr(sysobj, ip_res, 'Enabled', '1')
        ## add NIC agent for IP
        rtn = add_res(sysobj, nic_res, 'NIC', group)
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10238 Attempting to add existing resource',rtn['msg']):
            rtn['changed'] = 1
            return rtn
        modify_res_attr(sysobj, nic_res, 'Critical', 0)
        modify_res_attr(sysobj, nic_res, 'Device', dev)
        modify_res_attr(sysobj, nic_res, 'NetworkHosts', gatewaystr)
        modify_res_attr(sysobj, nic_res, 'Enabled', '1')
        ## add DiskGroup agent for data dg
        rtn = add_res(sysobj, datadg_res, 'DiskGroup', group)
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10238 Attempting to add existing resource',rtn['msg']):
            rtn['changed'] = 1
            return rtn
        modify_res_attr(sysobj, datadg_res, 'DiskGroup', dg_name)
        modify_res_attr(sysobj, datadg_res, 'Enabled', '1')
        ## add RVG agent
        rtn = add_res(sysobj, rvg_res, 'RVG', group)
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10238 Attempting to add existing resource',rtn['msg']):
            rtn['changed'] = 1
            return rtn
        modify_res_attr(sysobj, rvg_res, 'RVG', rvg)
        modify_res_attr(sysobj, rvg_res, 'DiskGroup', dg_name)
        modify_res_attr(sysobj, rvg_res, 'Enabled', '1')
        ## link the resources dependency
        if self.args.get('aws'):
            link_res(sysobj, ip_res, awsip_res)
            link_res(sysobj, rvg_res, awsip_res)
            link_res(sysobj, rvg_res, datadg_res)
        else:
            link_res(sysobj, rvg_res, datadg_res)
            link_res(sysobj, rvg_res, ip_res)        
        ## link the ip with nic resource
        rtn = link_res(sysobj, ip_res, nic_res)
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10905 Dependency between',rtn['msg']):
            rtn['changed'] = 1
        else:
            rtn['rc'] = 0

        return rtn

    def create_vvr_appgrp(self, sysobj, sgdict):
        ## create group application group
        group = sgdict['mnt_grp']
        mount_res = sgdict['mnt_res']
        rvg_primary_res = sgdict['rvg_primary_res']
        dg_name = self.args['dgname']
        data_vol = self.args['datavolname']
        agentinfo=self.args.get('agentinfo')
        clusterlist=[]
        mount_point=''
        sysobj.log.info('AGENTINFO1:' + str(agentinfo))
        if sysobj.hostname == self.args.get('seednode'):
            mount_point=agentinfo['primary']['mountpoint'] or '/mnt'
            clusterlist=agentinfo['primary']['clusterlist']
        else:
            mount_point=agentinfo['secondary']['mountpoint'] or '/mnt'
            clusterlist=agentinfo['secondary']['clusterlist']

        sysobj.log.info('AGENTINFO2:' + str(agentinfo))
        sysobj.log.info('MOUNTPOINT:'+ mount_point)


        #rtn = add_group(sysobj, group, 'AutoFailOver', self.args['systems'], self.args.get('clusterlist'))
        rtn = add_group(sysobj, group, 'AutoFailOver',self.args['systems'],clusterlist)
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10129 Attempting to add existing group',rtn['msg']):
            return rtn
        ## add Mount agent
        rtn = add_res(sysobj, mount_res, 'Mount', group)
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10238 Attempting to add existing resource',rtn['msg']):
            rtn['changed'] = 1
            return rtn
        modify_res_attr(sysobj, mount_res, 'MountPoint', mount_point)
        modify_res_attr(sysobj, mount_res, 'BlockDevice', '/dev/vx/dsk/'+dg_name+'/'+data_vol)
        modify_res_attr(sysobj, mount_res, 'FSType', 'vxfs')
        modify_res_attr(sysobj, mount_res, 'FsckOpt', '"%-y"')
        modify_res_attr(sysobj, mount_res, 'Enabled', '1')
        modify_res_attr(sysobj, mount_res, 'MountOpt', 'rw')
        ## add RVGPrimary agent
        rtn = add_res(sysobj, rvg_primary_res, 'RVGPrimary', group)
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10238 Attempting to add existing resource',rtn['msg']):
            rtn['changed'] = 1
            return rtn
        modify_res_attr(sysobj, rvg_primary_res, 'RvgResourceName', sgdict['rvg_res'])
        modify_res_attr(sysobj, rvg_primary_res, 'Enabled', '1')
        ## link the resources dependency
        rtn = link_res(sysobj, mount_res, rvg_primary_res)
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10905 Dependency between',rtn['msg']):
            rtn['changed'] = 1
            return rtn
        else:
            rtn['rc'] = 0
        ## link the groups dependency, requires RVGgroup
        rtn = link_grp(sysobj, group, sgdict['rvg_grp'], 'online local firm')
        if rtn['rc'] and not re.search('VCS WARNING V-16-1-10905 Dependency between',rtn['msg']):
            rtn['changed'] = 1
        else:
            rtn['rc'] = 0

        return rtn


