class Xprtl:
    cms_xprtlc = "/opt/VRTSsfmh/bin/xprtlc"
    push_file = "admin/cgi-bin/push_file.pl"
    abspath = os.path.dirname(os.path.abspath(__file__))
    remote_cmd_src = os.path.join(abspath, 'cpi_cmd.pl')
    remote_cmd_desc = "/var/opt/VRTSsfmh/cpi_cmd.pl"
    mh_var_dir = "/var/opt/VRTSsfmh/"
    whitelist_cmd = "admin/cgi-bin/whitelist.pl"
    whitename = "cpi_remote_cmd"

    def __init__(self, des_sys, user_name, passwd):
        self.des_sys = des_sys
        self.user_name = user_name
        self.passwd = passwd
        self.log = Logger.logger("Xprtl.log")
        self.log.info("init xprtl")
        self.setup_sys()

    # return 0 ok
    #       -1 fail 
    def setup_sys(self):
        cmd = Xprtl.cms_xprtlc \
              + ' -l https://' \
              + self.des_sys \
              + '//' \
              + Xprtl.push_file \
              + ' -f data=@' \
              + Xprtl.remote_cmd_src \
              + ' -d destfile=' \
              + Xprtl.remote_cmd_desc \
              + ' -d force=1 -d permission=755 -u ' \
              + self.user_name \
              + ' -p ' \
              + self.passwd \
              + ' -d whitename=' \
              + Xprtl.whitename
        rtn = os.popen(cmd).read()
        if re.search('SUCCESS', rtn):
            return 0
        else:
            self.log.error("Setup communication with " + self.des_sys + " failed, rtn: " + rtn)
            return -1

    def cmd_sys(self, cmd):
        cmd_prefix = Xprtl.cms_xprtlc \
                     + ' -l https://' \
                     + self.des_sys \
                     + '//' \
                     + Xprtl.whitelist_cmd \
                     + '/run -u ' \
                     + self.user_name \
                     + ' -p ' \
                     + self.passwd \
                     + ' -d argv=[\\"' \
                     + Xprtl.whitename \
                     + '\\",\\"'
        cmd_suffix = '\\"]'
        cmd = re.sub('2\s*>\s*/dev/null', "", cmd)

        def ord_repl(matchobj):
            return "%%%02X" % ord(matchobj.group(0))

        cmd = re.sub('([^A-Za-z0-9])', ord_repl, cmd)
        cmd = cmd_prefix + cmd + cmd_suffix
        self.log.info("xprtl cmd: " + cmd)
        rtn_out = os.popen(cmd).read()
        rtn_out = re.sub('\n', "", rtn_out)
        rtn_json = json.loads(rtn_out)
        stdout = rtn_json['RESULT']['RESULT']['out']
        stderr = rtn_json['RESULT']['RESULT']['err']
        exitcode = rtn_json['RESULT']['RETURNCODE']
        stdout = stdout.rstrip('\n')
        stderr = stderr.rstrip('\n')
        if stderr:
            stdout = stderr

        return exitcode, stdout

