    #       -1 fail 
    def unsetup_sys(self):
        (exitcode, stdout) = self.cmd_sys('rm -rf ' + Xprtl.remote_cmd_desc)
        if exitcode:
            self.log.error("Rm file failed, exitcode: " + exitcode)
            return -1
        return 0


