class SysCommon:
    def __init__(self, hostname, islocal, communication = {'type':'ssh'}, args = {}):
        # init logger first, record log right now
        if args and args['parameter'].get('product_version'):
            self.version = args['parameter']['product_version']
        else:
            self.version = VERSION
        self.hostname = hostname
        self.args = args
        self.module = self.args.get('module')
        self.communication = communication
        # NumberOfPasswordPrompts is used for no prompt if need password
        # StrictHostKeyChecking is used for not checking consistence of public key
        # Connecttimeout is used to define the timeout for building connection
        self.log = Logger.logger(self.hostname + '.log')
        self.tmpdir = Logger.logdir

        # Below will have push_error implementation of cpi.
        self.error = []
        self.message = []
        self.warning = []

        #System has its own member like communication, if need add it
        self.islocal = islocal
        self.vcs_log_path = '/var/VRTSvcs/'
        b_connect = False
        if islocal:
            b_connect = True
        else:
            b_connect = self.check_communication()
        self.b_connect = b_connect
        self.log.info("self.hostname connect status: " + str(b_connect))
        if self.b_connect:
            init_cmds = {}
            init_cmds['uname'] = '/bin/uname'
            init_cmds['cut'] = '/bin/cut'
            self.command = init_cmds
            self.plat = self.get_plat()
            self.padv = self.get_padv()
            self.padv_obj = instance_factory(self, 'padv', version = "", class_type = 'padv')
            self.command = self.padv_obj.command
            self.hostname = nofqdn(self)
        if args and 'facters' in args['parameter'].keys():
            self.set_host_facters()
        else:
            self.log.info('No facters at all' + str(self.args['parameter']))

    def get_plat(self):
        expect_plat = [ 'Linux', 'AIX', 'SunOS']
        exitcode, output = self.cmd('LC_ALL=C LANG=C ' + '_cmd_uname')
        if (exitcode):
            self.log.error("get plat failed")
            exit(-1)
        if output not in expect_plat:
            self.log.error("not expect plat:" + output)
        return output

    def get_padv(self):
        padv = '' 
        if os.path.exists('/etc/oracle-release'):
            rc,output = self.cmd("_cmd_uname -r | _cmd_cut -d 'l' -f2 | _cmd_cut -d '.' -f1")
            output = output.rstrip();
            if 'uek' in output:
                self.log.error("OL uek kernel is not support")
                exit(1)
            elif output == '6':
                padv = 'rhel6_x86_64'
            elif output == '7': 
                padv = 'rhel7_x86_64'
        elif os.path.exists('/etc/SuSE-release'):
            padv = 'SLES'
        elif os.path.exists('/etc/redhat-release'):
            rc,output = self.cmd("_cmd_uname -r | _cmd_cut -d 'l' -f2 | _cmd_cut -d '.' -f1")
            output = output.rstrip();
            if output == '6': 
                padv = 'rhel6_x86_64'
            elif output == '7': 
                padv = 'rhel7_x86_64'
            elif output == '8':
                padv = 'rhel8_x86_64'
            else:
                self.log.error('Unknown padv' + output +'Iamhere')
        return padv

    def check_communication(self):
        b_connect = False
        if 'ssh' == self.communication['type']:
            if not self.islocal:
                try_ssh_cmd = 'ssh -o NumberOfPasswordPrompts=0 -o StrictHostKeyChecking=no -o Connecttimeout=30 root@'+ self.hostname + ' date'
                exitcode = subprocess.check_call(try_ssh_cmd)
                ####! chage for py2.7
                if not exitcode:
                    b_connect = True
            else:
                b_connect = True
        return b_connect


    def cmd(self, cmd, mode='sync', msg={}, connect_timeout=60):
        exitcode = 0
        output = ''
        cmd = re.sub(r'\b_cmd_(\w+)\b', lambda x: self.command.get(x.group(1)) or x.group(), cmd)
        undef = re.findall(r'\b_cmd_\w+\b', cmd, re.M)
        if undef:
            self.log.critical('undefined cmd substitution: ' + str(undef))
            exit(1)
        cmd = re.sub(r'\b_cmd_', '', cmd)
        cmd = 'LC_ALL=C LANG=C ' + cmd
        if not self.islocal:
            if self.communication['type'] == 'ssh' or self.communication['type'] == 'rsh':
                cmd = re.sub('\"(.*?)\"', lambda o: '"\\"' + o.group(1) + '\\""', cmd)
            cmd = self.remote_cmd(cmd)
        # set timer
        starttime = datetime.datetime.now()
        # when server is down as some reason, no data would received, then the client will send a
        # heartbeat to the server every ServerAliveInterval sec and if the number the times of no response exceed ServerAliveCountMax
        # ssh client will stop connection and return.
        if 'sync' != mode and self.communication['type'] == 'ssh' or self.islocal:
            ###fix issue: if the cmd have some sub-string with "ssh" , this replace will lead some unexpect behaviour
            ### only replace the cmd with prefix ssh staring
            if re.match(r'ssh',cmd):
                cmd = cmd.replace('ssh','ssh -o ServerAliveInterval=10 -o ServerAliveCountMax=12 -o ConnectTimeout=' + str(connect_timeout),1)
        
        if 'sync' != mode:
            if self.communication['type'] == 'ssh' or self.communication['type'] == 'rsh' or self.islocal:
                cmd = cmd + ' >/dev/null 2>&1' + ' &'
                ### Log should track the full cmd
                self.log.info("cmd " + cmd)
                exitcode = os.system(cmd)
                output =str(msg)
#           elif self.communication['type'] == 'xprtl':
#               #TODO
#               pass
        else:
            ### Log should track the full cmd
            self.log.info("cmd " + cmd)
            if self.communication['type'] == 'ssh' or self.communication['type'] == 'rsh' or self.islocal:
                try:
                    subproc = subprocess.Popen(cmd, stderr=subprocess.PIPE, stdout=subprocess.PIPE, shell=True)
                    (output,err) = subproc.communicate()
                    exitcode = subproc.wait()
                except subprocess.CalledProcessError as CPE:
                    output = CPE.output;
                    self.log.error(output)
                    output = 'Exception occurred while running command:"' + cmd + '". Please check the log for more info'
                    exitcode = CPE.returncode;
#           elif self.communication['type'] == 'xprtl':
#               xprtl = Xprtl(self.hostname, self.communication['user'], self.communication['password'])
#               exitcode, output = xprtl.cmd_sys(cmd)
        endtime = datetime.datetime.now()
        elapsed = (endtime - starttime).seconds
        output = output.rstrip('\n')
        log_string = 'cmd exit=' + str(exitcode) + ' ' + output + ' (duration: ' + str(elapsed) + ' second)'
        if err:
            log_string = 'cmd exit=' + str(exitcode) + ' ' + output + ' (duration: ' + str(elapsed) + ' second)' + '\nError:' + err
            err = err.rstrip('\n')
            output = output + ' ERR:' + err

        
        # in multi-thread mode, the whole system object (including class members) needs to be serialized
        # logger from logging module cannot be serialized by pickle
        # hence we define a temporary variable here
        self.log.info(log_string)
        return exitcode, output

    def remote_cmd(self, cmd):
        if self.communication['type'] == 'ssh':
            cmd = self.communication['type'] + ' root@' + self.hostname + ' ' + cmd
        elif self.communication['type'] == 'rsh':
            cmd = self.communication['type'] + ' root@' + self.hostname + ' ' + cmd
        return cmd

    def transfer_file(self, dest_sys, src_file_path, des_file_path):
        
        rtn = {'hostname': self.hostname}
        exitcode = 0
        output = ''

        (des_file_dir, des_file) = split(des_file_path)
        if dest_sys.islocal and self.islocal:
            cmd = ' mkdir -p ' + des_file_dir + \
                  '; cp ' + src_file_path + ' ' + des_file_path
            self.log.info(cmd)
            exitcode = subprocess.check_call(cmd)
        elif self.islocal and not dest_sys.islocal:
            if 'xprtl' == self.communication['type']:
                xprtl = Xprtl(dest_sys.hostname, dest_sys.communication['user'], dest_sys.communication['password'])
                (exitcode, output) = xprtl.cmd_sys("mkdir -p " + des_file_dir)
                if 0 != exitcode:
                    rtn['rc'] = exitcode
                    rtn['msg'] = output
                    return exitcode, str(rtn)
                (exitcode, output) = xprtl.copy_to_sys(src_file_path, des_file_path)
            elif 'ssh' == self.communication['type']:
                cmd = 'ssh ' + 'root@' + dest_sys.hostname + ' mkdir -p ' + des_file_dir
                self.log.info(cmd)
                try:
                    output = subprocess.check_output(cmd, shell=True)
                    exitcode = 0
                except CalledProcessError as CPE:
                    output = CPE.output
                    exitcode = CPE.returncode
                if 0 != exitcode:
                    rtn['rc'] = exitcode
                    rtn['msg'] = output
                    return exitcode, str(rtn)

                import platform
                if platform.system() == 'Windows':
                    # Workaround for Windows cygwin, the drive name such as D: will be recognized as hostname
                    drive, path = os.path.splitdrive(src_file_path)
                    cmd = drive + " & cd / & " + 'scp ' + path + ' root@' + dest_sys.hostname + ':' + des_file_path
                else:
                    cmd = 'scp ' + src_file_path + ' root@' + dest_sys.hostname + ':' + des_file_path
                self.log.info(cmd)
                output = subprocess.check_output(cmd, shell=True)
        elif not self.islocal and dest_sys.islocal:
            if 'xprtl' == self.communication['type']:
                #TODO
                pass
            elif 'ssh' == self.communication['type']:
                cmd = 'mkdir -p ' + des_file_dir
                try:
                    output = subprocess.check_output(cmd, shell=True)
                    exitcode = 0
                except CalledProcessError as CPE:
                    output = CPE.output
                    exitcode = CPE.returncode
                if 0 != exitcode:
                    rtn['rc'] = exitcode
                    rtn['msg'] = output
                    return exitcode, str(rtn)
                cmd = 'scp ' + 'root@' + self.hostname + ':' + src_file_path + ' ' + des_file_path
                self.log.info(cmd)
                try:
                    output = subprocess.check_output(cmd, shell=True)
                    exitcode = 0
                except CalledProcessError as CPE:
                    output = CPE.output
                    exitcode = CPE.returncode

        rtn['rc'] = exitcode
        rtn['msg'] = output
        return exitcode, str(rtn)

    def cat_file(self, file_name):
        rc, output = self.cmd('_cmd_cat ' + file_name + ' 2>/dev/null')
        if rc != 0:
            return ''
        return output

    def move_file(self, source_file, target_file):
        if not source_file or not target_file:
            return 0
        self.cmd('_cmd_rmr ' + target_file)
        rc, output = self.cmd('_cmd_mv ' + source_file + ' ' + target_file)
        rc = 0 if rc else 1
        return rc

    def pkgvers(self,pkg):
        ## read facter josn, for upgrade, return the old pkg version
        if pkg in self.pkgvers:
            return self.pkgvers[pkg]
        filename = self.nfs_dir+ '/' + self.hostname + '.json'
        facter_json = readfile(filename,'json')
        installed_pkgs = facter_json.get('software')
        vrts_pkgs = installed_pkgs.get("vrts_packages") if installed_pkgs else ''
        if vrts_pkgs:
            pkg_list = vrts_pkgs.split(' ')
            for pkgi in pkg_list:
                if pkgi.split('-')[0] == pkg:
                    self.pkgvers[pkg] = pkgi.split('-')[1]
                    return self.pkgvers[pkg]

    def copyfile(self,src_file,target_file):
        rc,output = self.cmd("_cmd_cp -rp " + src_file + ' ' + target_file + ' 2>/dev/null')
        return rc

    def set_host_facters(self):
        self.args['parameter']['host_facters'] = {}
        self.args['parameter']['playbook_hosts'] = []
        dict_facters = {}
        for item in self.args['parameter']['facters']:
            for sys,values in item.items():
                self.log.debug('Host_facters:sys:' + sys + ' hostname:' +self.hostname)
                if sys == self.hostname:
                    self.args['parameter']['host_facters'] = eval(values)
                    self.log.debug('Host_facters:' + str(self.args['parameter']['host_facters']))
                self.args['parameter']['playbook_hosts'].append(sys)
                dict_facters.update({sys:eval(values)})
        self.args['parameter']['facters'] = dict_facters

def create_install_scripts(sysobj,version):
    scriptsfilename = 'add_install_scripts'
    scriptsdir = '/opt/VRTS/install/bin/UXRT'
    scriptsprefix = version.replace('.','')
    script = scriptsdir + scriptsprefix + '/' + scriptsfilename
    rc = 0
    if os.path.exists(script):
        rc,output = sysobj.cmd(script)
        if rc != 0 :
            sysobj.error.append({'add_installscript': 'error occurred:' + output})
        else:
            sysobj.message.append({'add_installscript': 'Successful'})
    else:
        sysobj.error.append({'add_installscript':script + ' not found'})
        rc = 1
    return rc

