    #
    # Function: mounted_vxfs_sys()
    # Determines if any VxFS File System is currently mounted on a host.
    # Return a reference to an array to contain vxfs mount points

    def mounted_vxfs_sys(self,sysobj):
        mps = []
        ret = ''
        rc , ret = sysobj.cmd("_cmd_mount -v | _cmd_grep 'type vxfs' | _cmd_awk '{print $3}' ")
        if ret:
            mps = re.split("\s+",ret)

        return mps

    # For rolling upgrade, check mounted file system Not under VCS control
    # Before rolling upgrade phase-1, the file system must be unmounted manually
    def mounted_vxfs_notvcs_sys(self,sysobj):
        mnts = []
        mps = self.mounted_vxfs_sys(sysobj)
        rc,maincf = sysobj.cmd('_cmd_cat /etc/VRTSvcs/conf/config/main.cf')
        for mnt in mps:
            mnt = mnt.strip()
            if not re.search('"' + mnt+'/"' , maincf):
                mnts.append(mnt)

        return mnts

