    #
    # Function: upgrade_precheck_sys()
    # Display preinstallation messages and confirmations for upgrade
    # before installation. Also take care of -precheck option.
    # It takes care of mounted filesystems and EOLed disk layouts.
    #
    def upgrade_precheck(self,sysobj):
        params = sysobj.args['parameter']
        #not putting main pkg installed and its version check here
        if sysobj.plat == 'SunOS':
            self.kernel_parameter_sys()
        if 'upgrade_phase' in params.keys() and params['upgrade_phase'] != 'upgrade_nonkernelpkgs':
            self.check_vxfs_sys(sysobj,'upgrade')

        self.eol_disk_layout(sysobj)

    # For patchupgrade_precheck,upgrade_precheck and uninstall_precheck need to check mounted vxfs filesystem
    def check_vxfs_sys(self,sysobj,action):
        params = sysobj.args['parameter']
        notvcs_vxfs = []
        vcs_vxfs = []
        mps_ref = self.mounted_vxfs_sys(sysobj)
        if mps_ref:
            notvcs_vxfs = self.mounted_vxfs_notvcs_sys(sysobj)
            vcs_vxfs = [item for item in mps_ref if item not in notvcs_vxfs]
            if sysobj.plat == 'SunOS':
                pass
            else:
                # If all VxFS are under VCS control, rolling upgrade can proceed
                if 'upgrade_phase' in params.keys() and params['upgrade_phase'] == 'upgrade_kernelpkgs' and len(notvcs_vxfs) == 0:
                    mps_ref = []

        if mps_ref:
            if 'upgrade_phase' in params.keys() and params['upgrade_phase'] == 'upgrade_kernelpkgs':
                mps = ','.join(notvcs_vxfs)
            else:
                mps = ','.join(mps_ref)

            sysobj.error.append({'upgrade_precheck vxfs file system': 'Some VxFS file systems are mounted on mount points ' + mps + ' on node ' + sysobj.hostname + ' and need to be unmounted before upgrade.'})

