    def stop_precheck(self,sysobj):
        self.check_cvmstatus_sys(sysobj);
        return self.perform_task_sys(sysobj, 'stop_precheck')

    def check_cvmstatus_sys(self,sysobj):

        # Check if cvm is frozen and online.
        # If so, quit upgrading and ask user to unfreeze cvm before upgrading.
        cvm_frozen = 0
        cvm_online = 0 
        rc, rtn = sysobj.cmd("_cmd_hagrp -list Frozen=1 2>/dev/null")
        for line in re.split("\s+",rtn):
            grp = re.split("\s+",line)[0]
            if grp and grp == 'cvm':
                cvm_frozen = 1
                break

        if cvm_frozen:
            rc, rtn = sysobj.cmd("_cmd_hagrp -display cvm -attribute State")
            for line in re.split("\n",rtn):
                temp = re.split("\s+",line)
                if temp[0] == '#Group':
                    continue

                sysi = temp[2]
                #for obj_sysi in syslist:
                rc , sysname = sysobj.cmd("_cmd_cat /etc/VRTSvcs/conf/sysname")
                if sysi == sysname and (temp[3] == '|ONLINE|' or temp[3] == '|PARTIAL|'):    
                    cvm_online = 1
                    break
            
            if cvm_online:
                if sysobj.args['parameter']['task'].lower() == 'upgrade':
                    sysobj.error.append({'cvm_status check':'Cannot upgrade if cvm service group is frozen and online. Unfreeze cvm service group before upgrading.'})
                elif sysobj.args['parameter']['task'].lower() == 'stop':
                    sysobj.error.append({'cvm_status check':'Cannot stop if cvm service group is frozen and online. Unfreeze cvm service group before stop.'})
            else:
                sysobj.log.debug("cvm_status check passed")


    def configure(self, sysobj, component):
        super(Sfcfsha_common,self).configure(sysobj, component)
        pass

    def start_cvm_cfs_agents(self, sysobj):
        rc = 1
        for count in range(4):
            rc, cvmstate = sysobj.cmd("_cmd_hagrp -display cvm -attribute State")
            if rc == 0:
                break
            else:
                time.sleep(5)
        if rc != 0:
            sysobj.error.append({'start_cvm_cfs_agents':'Command failed to make cvm group online'})
            return

        for line in cvmstate.split('\n'):
            if re.search('^#Group', line):  
                continue
            fields = line.split()
            grp = fields[0]
            sysi = fields[2]
            state = fields[3]
            if re.search('OFFLINE', state):
                sysobj.log.debug("Online cvm Group on " + sysi)
                rc, output = sysobj.cmd("_cmd_hagrp -online " + grp + " -sys " + sysi)
                if rc == 0:
                    sysobj.cmd('_cmd_hagrp -wait ' + grp + ' State ONLINE -sys '+ sysi + " -time 300")
                else:
                    sysobj.log.debug("Unable to online cvm service group on " + sysi)

    def upgrade_post_start(self, sysobj, seednode=None):
        #sysobj.log.debug("self.hostname is " + sysobj.hostname + " seednode is " + seednode)
        if sysobj.hostname == seednode:
            self.start_cvm_cfs_agents(sysobj)
            #create_flag(sysobj, 'start_cvm_cfs_agents_done')
        else:
            count = 30
            while (count != 0):
                rc,cvm_running = sysobj.cmd("/opt/VRTS/bin/hagrp -state cvm|grep '|ONLINE|'|grep -v 'OFFLINE'")
                #rc = wait_for_flag(sysobj, 'start_cvm_cfs_agents_done')
                if rc != 0:
                    sysobj.log.debug("Waiting for CVM to come online...")
                else:
                    break
                count -= 1
                time.sleep(10)
            if count == 0:
                sysobj.log.error("Failed to execute upgrade_post_start function due to CVM not online")

        super(Sfcfsha_common,self).upgrade_post_start(sysobj, seednode)

