class Sfha_common(Base_prod, object):
    def __init__(self):
        super(Sfha_common,self).__init__()
        self.prod_pkgs = []
        self.component_list = ['vm','fs','sf','vcs','sfha']
        self.perform_component_list = ['sf','vcs']
        #self.perform_component_list = []
        self.max_component = 'sfha'

    def configure(self, sysobj, component):
        super(Sfha_common,self).configure(sysobj, component)
        pass

    def stop_precheck_sys(self,sysobj):
        pass

    def upgrade_precheck(self,sysobj):
        self.perform_task(sysobj,'upgrade_precheck')
        return 

    def upgrade_post_start(self,sysobj,seednode=None):
        sysobj.log.info("Calling preform_task of SFHA:")
        self.perform_task(sysobj,'upgrade_post_start')
        params = sysobj.args['parameter']
        if not hasattr(sysobj,'upgrade_phase'):
            self.upgrade_protocol_version(sysobj)
        return 
 
    def upgrade_pre_stop(self,sysobj):
        sysobj.log.info("Start sfsha upgrade_pre_stop")
        params = sysobj.args['parameter']
        if sysobj.hostname == params['seednode'] and params.get('upgrade_phase') and params['upgrade_phase'] == 'upgrade_nonkernelpkgs':
            #### wait for CVM online
            self.upgrade_rolling_post(sysobj)
        if params.get('upgrade_phase') and params['upgrade_phase'] == 'upgrade_kernelpkgs':
            self.ru_prestop(sysobj)

        sysobj.log.info("Calling preform_task of SFHA:")
        self.perform_task(sysobj,'upgrade_pre_stop')

    ## Only execute when system1
    def upgrade_rolling_post(self,sysobj):
        #if os.path.exists(sysobj.nfs_dir+ '/' + "upgrade_rolling_post"):
        component_obj = instance_factory(sysobj, 'vcs', None, 'prod')
        component_obj.upgrade_rolling_post(sysobj)
        self.upgrade_protocol_version(sysobj)

    def ru_prestop(self,sysobj):
        params = sysobj.args['parameter']
        if params['upgrade_phase'] == 'upgrade_kernelpkgs':
            ver1 = ver2 = ver3 = 0
            rc,output = sysobj.cmd("_cmd_vxdctl protocolversion 2>&1")
            ma = re.search("Cluster\s+running\s+at\s+protocol\s+(\d+)",output,re.M)
            if ma:
                ver1 = ma.group(1)
            rc,ver2 = sysobj.cmd("_cmd_fsclustadm protoversion 2>&1 | _cmd_awk '/local/ {print $3}'")
            rc,ver3 = sysobj.cmd("_cmd_odmclustadm protoversion 2>&1 | _cmd_awk '/local/ {print $3}'")

        vcs_obj = instance_factory(sysobj, 'vcs', None, 'prod')
        vcs_obj.ru_prestop(sysobj)
 

        # set fs/vm/odm version
        if ver1:
            sysobj.cmd("_cmd_vxdctl setversion " + ver1 + " 2>&1")
        if ver2:
            sysobj.cmd("_cmd_fsclustadm protoset " + ver2 + " 2>&1")
        if ver3:
            sysobj.cmd("_cmd_odmclustadm protoset " + ver3 + " 2>&1")

    def upgrade_protocol_version(self,sysobj):
        ## The product should include CVM
        done_upgrade = 0
        count = 300
        if sysobj.args['parameter']['host_facters']['component'].lower() != 'sfcfsha':
            return 1
        sfcfsha_obj = instance_factory(sysobj, 'sfcfsha', None, 'prod')
        while done_upgrade == 0:
            if count == 0:
                sysobj.log.debug('Could not upgrade the protocol version')
                break
            rc,output = sysobj.cmd("_cmd_vxdctl -c mode 2>/dev/null")
            if re.search('cluster active - MASTER',output,re.M):
                sysobj.cmd("_cmd_vxdctl upgrade")
                sysobj.cmd("_cmd_fsclustadm protoclear")
                sysobj.cmd("_cmd_fsclustadm protoupgrade")
                sysobj.cmd("_cmd_odmclustadm protoclear")
                sysobj.cmd("_cmd_odmclustadm protoupgrade")
                done_upgrade = 1
                #create_flag(sysobj, 'protocol_upgrade_done')
            elif(re.search('cluster active - SLAVE',output,re.M)):
                #wait_for_flag(sysobj, 'protocol_upgrade_done')
                # Check if protocol upgrade is done on MASTER or wait
                sysobj.cmd("_cmd_fsclustadm protoclear")
                sysobj.cmd("_cmd_fsclustadm protoupgrade")
                sysobj.cmd("_cmd_odmclustadm protoclear")
                sysobj.cmd("_cmd_odmclustadm protoupgrade")
            else:
                pass
            default_protocol_version = sfcfsha_obj.get_release_protocol_version(sysobj.args['parameter'])
            sysobj.log.info('Default protocol version is:' + str(default_protocol_version))
            rc,output = sysobj.cmd("_cmd_vxdctl protocolversion 2>/dev/null")
            sysobj.log.debug(output)
            found = re.search('Cluster\s+running\s+at\s+protocol\s+([0-9]+)', output, re.M)
            if found:
                new_proto = int(found.group(1))
                if new_proto == default_protocol_version or new_proto > default_protocol_version:
                    done_upgrade = 1
            else:
                time.sleep(5)
                count -= 1

        if not done_upgrade:
            sysobj.log.error("Unable to upgrade CVM protocol version. Please make sure CVM is online on all the nodes in the cluster and run 'vxdctl upgrade' to complete the upgrade.")

    def perform_task(self, sysobj, task):
        
        #if not (re.search('upgrade',task) and sysobj.noconfig_sf):
            sf_obj = instance_factory(sysobj, 'sf', None, 'prod')
            sysobj.log.debug('Performing  sf ' + task + ':')
            sf_rtn = getattr(sf_obj, task)(sysobj)
        #if not (re.search('upgrade',task) and sysobj.noconfig_vcs):
            vcs_obj = instance_factory(sysobj, 'vcs', None, 'prod')
            sysobj.log.debug('Performing vcs ' + task + ':')
            vcs_rtn = getattr(vcs_obj, task)(sysobj)

            return (sf_rtn and vcs_rtn)

    def check_config(self,sysobj):
        task = 'check_config'
        sysobj.log.debug('SFHA check_config called')
        return self.perform_task(sysobj,task)

    def addnode_post_start(self,sysobj):
        
        if sysobj.hostname in sysobj.args['parameter']['add_del_nodes']:
            delete_procs = []
            vxfenmode = sysobj.args['parameter']['facters'][sysobj.cluster_node]['vxfenmode']
            if vxfenmode not in ['disabled', 'N/A']:
                delete_procs.append('vxfen')
                vxfen_proc = instance_factory(sysobj, 'vxfen', None, 'proc')
                vxfen_proc.disable_proc(sysobj)

            component = sysobj.args['parameter']['facters'][sysobj.cluster_node]['component']
            #start all SF procs
            procs = get_procs(sysobj, 'infoscale' , component, 'start')
            procs = [ item for item in procs if item not in delete_procs]

            obj = Process({'procs':procs, 'state':'present', 'module':'addnode'})
            proc_rtn = obj.execute(sysobj)
            if proc_rtn['rc'] != 0:
                sysobj.error.append({'addnode_post_start': 'Failed to start SFHA processes'})
                sysobj.log.info('addnode_post_start:' + str(proc_rtn['msg']))
                return 1
        else:
            return 0

        return 0

