    def upgrade_pre_stop(self, sysobj):
        sysobj.log.debug('upgrade_pre_stop of vcs called:')
        params = sysobj.args['parameter']
        sys0 = params['seednode']
        
        if params.get('upgrade_phase') and params['upgrade_phase'] == 'upgrade_kernelpkgs':
            self.ru_prestop(sysobj)
            #return 1
        elif params.get('upgrade_phase') and params['upgrade_phase'] == 'upgrade_nonkernelpkgs':
            if sysobj.hostname == sys0:
                self.upgrade_rolling_post(sysobj)

        # set secure cluster attribute to each system
        rc,maincf = sysobj.cmd('_cmd_cat ' + self.conf + '/config/main.cf')
        if re.search ( 'SecureClus\s*=\s*1',maincf,re.M):
            sysobj.args['parameter']['host_facters']['secureclus'] = 1

        # following tasks only performed on first node
        if sysobj.hostname == sys0:
            # freeze service group
            proc = instance_factory(sysobj, 'had')
            if proc.check_proc(sysobj, 'start'):
                haconf_makerw(sysobj)
                freeze_groups(sysobj)
                haconf_dumpmakero(sysobj)
            #create_flag(sysobj, 'maincf_pre_upgrade_done')
        else:
            time.sleep(15)
            #wait_for_flag(sysobj, 'maincf_pre_upgrade_done')


        #backup config/conf files
        upgradepath = sysobj.tmpdir + 'upgrade'
        rtn = self.backup_cf_files_sys(sysobj,self.config, upgradepath + '/old_config')
        rtn = self.backup_cf_files_sys(sysobj,self.conf, upgradepath + '/old_conf')
        return 0

    def ru_prestop(self, sysobj):
        params = sysobj.args['parameter']
        if params.get('upgrade_phase') and params['upgrade_phase'] == 'upgrade_kernelpkgs':
            procs = ['gab', 'vxfen']
            for proc in procs:
                proc_obj = instance_factory(sysobj, proc)
                proc_obj.pre_stop(sysobj)
            had_obj = instance_factory(sysobj, 'had')
            if had_obj.check_proc(sysobj, 'stop'):
                # Still running, execute hastop command
                rc, output = sysobj.cmd('_cmd_hastop -local -evacuate 2>/dev/null')
                if rc:
                    sysobj.log.error('Failed to stop VCS. Contact our technical support or refer to the Veritas InfoScale installation guide for further troubleshooting.')
                    return 1
                time.sleep(2)
        return 0

    def upgrade_rolling_post(self, sysobj):
        rc, output = sysobj.cmd('_cmd_cat /etc/gabtab')
        if not re.search('V', output):
            return
        if os.path.exists('/etc/gabtab.rubak'):
            sysobj.cmd('_cmd_rmr /etc/gabtab')
            sysobj.cmd('_cmd_cp -rfp /etc/gabtab.rubak /etc/gabtab 2>/dev/null')
        if os.path.exists('/etc/vxfenmode.rubak'):
            sysobj.cmd('_cmd_rmr /etc/vxfenmode')
            sysobj.cmd('_cmd_cp -rfp /etc/vxfenmode.rubak /etc/vxfenmode 2>/dev/null')
        if sysobj.hostname == sysobj.args['parameter']['seednode']:
            sysobj.cmd('_cmd_gabconfig -R')
            sysobj.cmd('_cmd_vxfenconfig -R')
            #create_flag(sysobj, 'upgrade_rolling_post')
        else:
            #wait_for_flag(sysobj, 'upgrade_rolling_post')
            pass

        return

