    def restore_vcs_lock_files(self,sysobj):
        lockfilepath = sysobj.tmpdir + '/VRTSvcs_lock'
        if not os.path.isdir(lockfilepath):
            return 1
        newfolder = sysobj.vcs_log_path + '/lock/volatile'
        sysobj.cmd("_cmd_mkdir -p " + newfolder)

        rc, files = sysobj.cmd("cd " + lockfilepath + "; _cmd_ls -a | _cmd_grep online_lock 2>/dev/null")
        file_list = files.split('\n')
        for oldlockfile in file_list:
            newlockfile = oldlockfile
            if not re.search(r'._online_lock',oldlockfile):
                newlockfile = re.sub('_online_lock','._online_lock',newlockfile)
                if not os.path.exists(newfolder + '/' + newlockfile):
                    sysobj.cmd("_cmd_cp -p " + lockfilepath+ '/' + oldlockfile + ' ' + newfolder + '/' + newlockfile)

        rc, files = sysobj.cmd("cd " + lockfilepath + "; _cmd_ls -a | _cmd_grep nfsrestart 2>/dev/null")
        file_list = files.split('\n')
        for oldlockfile in file_list:
            if not os.path.exists(newlockfile + '/' + oldlockfile):
                sysobj.cmd("_cmd_cp -p "+ lockfilepath+ '/' + oldlockfile + ' ' + newfolder + '/' + oldlockfile)

        if os.path.exists(lockfilepath + '/' + '.nfs4_status_file'):
            sysobj.cmd("_cmd_cp -p " + lockfilepath + '/.nfs4_status_file ' + newfolder + '/')

        sysobj.cmd("_cmd_rm -f " + sysobj.vcs_log_path + '/lock/.habootfile 2>/dev/null')
        sysobj.cmd("_cmd_touch " + sysobj.vcs_log_path + '/lock/.habootfile 2>/dev/null')

