def add_group(sysobj, group, sg_type, syslist, cluslist=None):
    rtn = {'changed': 1, 'rc': 0, 'msg': ''}
    ## get SystemList, AutoStartList
    sysindex = ''
    clusindex = ''
    sys_string = ' '.join(syslist)
    for sys in syslist:
        sysindex += sys + ' ' + str(syslist.index(sys)) + ' '
    ## add head of groups, the SystemList, Parallel or FailOver, AutoStartList
    rc, output = sysobj.cmd('_cmd_hagrp -add ' + group) 
    if rc:
        rtn['rc'] = rc
        rtn['msg'] = output
        rtn['changed'] = 0
        return rtn

    cmd = '_cmd_hagrp -modify ' + group + ' SystemList ' + sysindex + ';'
    cmd += '_cmd_hagrp -modify ' + group + ' AutoStartList ' + sys_string + ';'
    cmd += '_cmd_hagrp -modify ' + group + ' ' + sg_type + ' 1' 
    sysobj.cmd(cmd) 
    ## get ClusterList for GCO/VVR
    sysobj.log.info('CLUSLIST1:' + str(cluslist) + ':' + str(type(cluslist)))
    if cluslist:
        for cluster in cluslist:
            sysobj.log.info('CLUSLIST2:' + str(cluster) + ':' + str(type(cluster)))
            #clusindex += cluster + ' ' + cluslist.index(cluster) + ' '
            clusindex += cluster + ' ' + str(cluslist.index(cluster)) + ' '
        sysobj.cmd('_cmd_hagrp -modify ' + group + ' ClusterList ' + clusindex)
        sysobj.cmd('_cmd_hagrp -modify ' + group + ' Authority ' + '1')

    return rtn

