def online_group(sgname,sysobj,systems=None):

    cmd = command = ''
    parallel = type_group(sgname,sysobj)
    if parallel == 0: 
        # FailOver service group
        cmd = '_cmd_hagrp -online ' + sgname + ' -any'
    else:
        # Parallel servce group
        cmd = '_cmd_hagrp -online ' + sgname
        for system in systems:
            command += cmd + ' -sys '+system + ';'
        cmd = command

    rc, output = sysobj.cmd(cmd)
    if rc:
        return 1

    return 0

